							mbtn_view_child_OnMenuItem(strName, nItemID)
{
	if(!app.service.can_update(true))
		return false;
	var iProperties =1;	
	var iRelationship =2;
	
	var funcUpdateSave = function()
	{
		_swdoc.update_costs();
		_swdoc.Save(0);
	}	

	if(nItemID == iProperties)
	{
		if(app.g.sl_getcolvalueraw(sl_children, -1, 'fk_child_type')=='ME->Service')
		{
			app.g.sl_openformforedit(sl_children,'fk_child_id','portfolio',"",true, function()
			{					
				funcUpdateSave();
			});
		}
		else
		{
			var inCIkey = sl_children.GetItemTextRaw(sl_children.curSel, 'fk_child_id');
			app.cmdb.popup_configitem(inCIkey, true, "", true, function()
			{
				funcUpdateSave();
			});
		}		
	}
	else if(nItemID == iRelationship)
	{
		//-- edit relationship	
		var intRelKey = sl_children.GetItemTextRaw(sl_children.curSel, 3);
		app.OpenFormForEdit("service_editrelationship", intRelKey, "", true, function()
		{
			sl_children.Refresh();
			funcUpdateSave();
		});
	}
	else
	{
		funcUpdateSave();
	}	
}

							mbtn_view_parent_OnMenuItem(strName, nItemID)
{
	if(!app.service.can_update(true))
		return false;
	var iProperties =1;	
	var iRelationship =2;		

	if(nItemID == iProperties)
	{
		app.g.sl_openformforedit(sl_parents,'fk_parent_id','portfolio',"",true, function(){
			_swdoc.update_costs();
		});
	}
	else if(nItemID == iRelationship)
	{
		//-- edit relationship	
		var intRelKey = sl_parents.GetItemTextRaw(sl_parents.curSel, 3);
		app.OpenFormForEdit("service_editrelationship", intRelKey, "", true, function(){
			sl_parents.Refresh();
			_swdoc.update_costs();
		});
	}
	
	
	_swdoc.update_costs();

}

							TabFinance_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here

	if(nTab==3)
	{
		app._eim(img_ci,sc_folio.vsb_image);
	}
	else if(nTab==2)
	{
		app._ete(btn_dataform_onsub,((_swdoc.oService().fk_df_onsub<1)?"Create Subscription Form":"Edit Subscription Form"));
		app._een(fld_allow_subs,_swdoc.oService().fk_df_onsub>0);
		_swdoc.MF().sl_visibility.Refresh();
		
		//-- Disable remove button if nothing selected.
		var selCount = app.g.sl_getselected_count(_swdoc.MF().sl_visibility);
		if (selCount==0) app._een(_swdoc.MF().btn_del_subscription1,false);
	}
	else if(nTab==1)
	{
		//TK REquired app.service level info
		app._slf(_swdoc.MF().sl_service_sla,"swc=fk_service&kvs="+config_itemi.pk_auto_id);
		app.g.apply_appcode_sla_filter(_swdoc.MF().sl_service_sla,'SLA-SERVICE');
		app._slf(_swdoc.MF().fld_fk_default_sla,"swc=fk_service&kvs="+config_itemi.pk_auto_id);
		app.g.apply_appcode_sla_filter(_swdoc.MF().fld_fk_default_sla,'SLA-SERVICE');
		_swdoc.MF().sl_service_sla.Refresh();
		//--
		
		app._ete(btn_dataform_request,((_swdoc.oService().fk_df_onreq<1)?"Create Request Form":"Edit Request Form"));
		app._ete(btn_dataform_support,((_swdoc.oService().fk_df_support<1)?"Create Support Form":"Edit Support Form"));
		app._een(fld_allow_request,_swdoc.oService().fk_df_onreq>0);
		app._een(fld_allow_support,_swdoc.oService().fk_df_support>0);
		_swdoc.MF().sl_revenue.Refresh();
		
		//-- Disable remove button if nothing selected.
		var selCount = app.g.sl_getselected_count(_swdoc.MF().sl_revenue);
		if (selCount==0) app._een(_swdoc.MF().btn_del_subscription,false);
	
	}else if(nTab==0)
	{
		_swdoc.MF().sl_costs1.Refresh();
		
		//-- Disable remove button if nothing selected.
		var selCount = app.g.sl_getselected_count(_swdoc.MF().sl_costs1);
		if (selCount==0) app._een(_swdoc.MF().btn_del_additional1,false);
		
		
	}else if(nTab==4)
	{
		_swdoc.MF().sl_target.Refresh();
		
		//-- Disable remove button if nothing selected.
		var selCount = app.g.sl_getselected_count(_swdoc.MF().sl_target);
		if (selCount==0) app._een(_swdoc.MF().btn_del_subscription2,false);
	
	}
	
}

							btn_dataform_onsub_OnPressed()
{
	if(btn_dataform_onsub.text=="Create Subscription Form")
	{
		//-- create form
		
		var funcFinalProcessing = function()
		{
			if(_swdoc.oService().fk_df_onsub>0)
			{
				app._ete(btn_dataform_onsub,"Edit Subscription Form");
				app._een(fld_allow_subs,true);
			}
		}
		
		if(_swdoc.oService().fk_df_onsub<1)
		{
			app.service.create_dataform(_swdoc.oService().ck_config_type,"Service Request",function(inID)
			{
				_swdoc.oService().fk_df_onsub = inID;
				_swdoc.Save(0);
				funcFinalProcessing();
			});
			
		}
		else
		{
			funcFinalProcessing();
		}
		
	}
	else
	{
		//-- edit form record
		app.OpenFormForEdit("service_dataform", _swdoc.oService().fk_df_onsub, "", true);
	}	
}

							btn_dataform_support_OnPressed()
{
	if(btn_dataform_support.text=="Create Support Form")
