							Button1_OnPressed()
{
	// TODO: Add your event handler code here
	var p = {};
	p.sid=_swdoc.oCI().pk_auto_id;
	var intCount = app.g.staticfilter_rowcount("sc_details","catalog_details",p);
	if(intCount>0)
	{
		var oRS = app.g.get_sqrecordset("service/get_service_details","sid="+_swdoc.oCI().pk_auto_id);
		if(oRS.Fetch())
		{
			var intValue = app.g.get_field(oRS, "pk_auto_id");
			app.OpenFormForEdit("sc_details", intValue, "", true, function(){});
		}
		else
			MessageBox('Failed to load service details.');
	}
	else
		app.OpenForm("sc_details", "area=details&fk_service="+_swdoc.oCI().pk_auto_id, true, function(){});

}

