							mbtn_add_comp1_OnMenuItem(strName, nItemID)
{
	if(!app.service.can_update(true))
		return false;
	var iService =1;	
	var iEntity =2;
	
	
	var funcUpdateAndSave = function()
	{
		_swdoc.update_costs();
		_swdoc.Save(0);
	}	

	if(nItemID == iService)
	{
		//-- get intCI
  		var intCI = _swdoc.oCI().pk_auto_id;
  		if(intCI>0)
  		{
 			//-- create component relationship
			app.service.generate_componentrelation(intCI,1,function(boolResp)
			{
				if(boolResp)
				{
					sl_component.Refresh();
					sl_component.SetRowSelected(0);
					funcUpdateAndSave();
				}
				else
				{
					return true;
				}
			});
		}
		else
		{
			funcUpdateAndSave();
		}
	}
	else if(nItemID == iEntity)
	{
		//-- get intCI
  		var intCI = _swdoc.oCI().pk_auto_id;
  		if(intCI>0)
  		{
 			//-- create component relationship
			app.service.generate_cmdb_componentrelation(intCI,1, function(boolResp)
			{
				if (boolResp)
				{
					sl_component.Refresh();
					sl_component.SetRowSelected(0);
					funcUpdateAndSave();
				}
				else
				{
					return true;
				}	
			});
		}
		else
		{
			funcUpdateAndSave();
		}
	}
	else
	{
		funcUpdateAndSave();
	}
}

