							mbtn_ola_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	


	if(nItemID == iAssignCI)
	{
		if(!app.oSettings.get_setting("boolFilterAppcodeSLALoaded"))
			app.g.load_appcode_sla();
		var strFilter = "";
		var strAppcodes = app.oSettings.get_setting("strFilterAppcodeOLA");
		if(strAppcodes!="")
		{
			var arrAppcodes = strAppcodes.split(",");
			strAppcodes = "";
			for (var x=0; x < arrAppcodes.length; x++)
			{		
				if (strAppcodes != "")
				{
					strAppcodes += ",";
				}
				strAppcodes += "'" + arrAppcodes[x] + "'";
			}
			strFilter = "appcode IN (" + strAppcodes + ")";
		}
		var strURL = "mode=select&filter="+app.pfu(strFilter);
		app.OpenFormForAdd("browse_olas", "", strURL, true, function(x){
			var strKeys = x.document.strSelectedKey;
			if(strKeys=="")return;
			var arrKeys = strKeys.split(",");
			var strCMDBIDs = x.document.strSelectedCMDBIDs;
			var arrCMDBIDs = strCMDBIDs.split(",");
			for (var x=0; x< arrKeys.length;x++){
				var fk_key = arrKeys[x];
				if(app.g.sl_findrow_byvalue(sl_olas1,fk_key,"pk_ola_id")==-1){
	        		var strRel = "Uses";
					var boolSuccess = app.cmdb.generate_meconfigrelation(arrCMDBIDs[x],_swdoc.oCI().pk_auto_id,strRel); 
					if( boolSuccess){
						//-- relationship created, so append to list
						app.g.sqllist_appendfilter(sl_olas1, fk_key, "pk_ola_id");		
					}
					else{
						MessageBox("Failed to create relationship between Service record and OLA record. \nPlease contact your Supportworks Administrator.");
					}
				}
						
			}
		
			sl_olas1.Refresh();
			sl_olas1.SetRowSelected(0);
			sl_olas1_OnItemSelected(0);
			return;    
		});
	}
	else if(nItemID == iRemoveCI)
	{
		if(sl_olas1.curSel == -1)
		{
			return false;
		}
		
		var strConfirm = "Are you sure you want to remove the selected operational service level definition record from the Service?";
		if(confirm(strConfirm))
		{
			var fk_cmdb_id = app.g.sl_getcolvalue(sl_olas1, sl_olas1.curSel, "fk_cmdb_id");//app.g.sl_getprikeyvalue(sl_olas1, sl_olas1.curSel);
			app.cmdb.delete_meconfigrelation(fk_cmdb_id,_swdoc.oCI().pk_auto_id);
			
			var strOLAs = app.g.get_ci_olas(_swdoc.oCI().pk_auto_id);
			_swdoc.MF().sl_olas1.storedQuery = "common.load_by_colname";
			strFilter = "swc=pk_ola_id&kvs="+strOLAs;
			var strAppcodes = app.oSettings.get_setting("strFilterAppcodeOLA");
			if(strAppcodes!="")
				strFilter += "&_acin="+strAppcodes;
			app._slf(_swdoc.MF().sl_olas1,strFilter);
			sl_olas1.Refresh();
		}
	}
	

	var iAssignCI = 1;
	var iRemoveCI = 2;	


}

