							sl_contracts1_OnItemDoubleClicked(nRow, nCol)
{
	// TODO: Add your event handler code here
	var strKeyValue = app.g.sl_getprikeyvalue(sl_contracts1, nRow);
	var strType = app.g.sl_getcolvalue(sl_contracts1, nRow, "type");
	if(strType=="supplier")
	{
		app.OpenFormForEdit("Tf3PartyContract", strKeyValue, "", true, function(){
			return false;	
		});	
	}
	else{
		app.OpenFormForEdit("TfContract", strKeyValue, "", true, function(){
			return false;
		});
	}
}

							mbtn_ci_contract_view1_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;	
	var iOrg = 2;	
	
 	if(nItemID == iProperties)
	{
 		var strKeyValue = app.g.sl_getprikeyvalue(sl_contracts1, sl_contracts1.curSel);
		if (strKeyValue=="")return;
		var strType = app.g.sl_getcolvalue(sl_contracts1, sl_contracts1.curSel, "type");
		if(strType=="supplier"){
			app.OpenFormForEdit("Tf3PartyContract", strKeyValue, "", true, function(){
				sl_contracts1.SetRowSelected(sl_contracts1.curSel,true);
				
				//-- refresh in case removed ci from contract while viewing properties
				var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
				var strFilter = "swc=pk_contract_id&kvs=0";
				if(strContracts!=0){
					strFilter = "swc=pk_contract_id&kvs="+ strContracts;
				}
				app._slf(sl_contracts1,strFilter);
				
				sl_contracts1.SetRowSelected(0);	
			});	
		}
		else{
			app.OpenFormForEdit("TfContract", strKeyValue, "", true, function(){
				sl_contracts1.SetRowSelected(sl_contracts1.curSel,true);
				//-- refresh in case removed ci from contract while viewing properties
				var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
				var strFilter = "swc=pk_contract_id&kvs=0";
				if(strContracts!=0){
					strFilter = "swc=pk_contract_id&kvs="+ strContracts;
				}
				app._slf(sl_contracts1,strFilter);
				
				sl_contracts1.SetRowSelected(0);
			});
		}
   }
  	else if(nItemID == iOrg)
	{
		var strOrg = sl_contracts1.GetItemTextRaw(sl_contracts1.curSel, "fk_company_id");
		if (strOrg=="")return;
		var strType = app.g.sl_getcolvalue(sl_contracts1, sl_contracts1.curSel, "type");
		if(strType=="supplier"){
			app.OpenFormForEdit("EfSupplier", strOrg, "",true, function(){
				//-- refresh in case removed ci from contract while viewing properties
				var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
				var strFilter = "swc=pk_contract_id&kvs=0";
				if(strContracts!=0){
					strFilter = "swc=pk_contract_id&kvs="+ strContracts;
				}
				app._slf(sl_contracts1,strFilter);
				
				sl_contracts1.SetRowSelected(0);
			})	;
		}
		else{
			app.OpenFormForEdit("TfCompany", strOrg, "",true, function(){
				//-- refresh in case removed ci from contract while viewing properties
				var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
				var strFilter = "swc=pk_contract_id&kvs=0";
				if(strContracts!=0){
					strFilter = "swc=pk_contract_id&kvs="+ strContracts;
				}
				app._slf(sl_contracts1,strFilter);
				
				sl_contracts1.SetRowSelected(0);
			})	;
		}
    }

	//-- refresh in case removed ci from contract while viewing properties
	var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
	var strFilter = "swc=pk_contract_id&kvs=0";
	if(strContracts!=0){
		strFilter = "swc=pk_contract_id&kvs="+ strContracts;
	}
	app._slf(sl_contracts1,strFilter);
	
	sl_contracts1.SetRowSelected(0);
	
}

							mbtn_ci_contract_actions1_OnMenuItem(strName, nItemID)
{
    
	var iRemoveContract = 2;
	var iSearchContract= 3;
	
	if(nItemID == iRemoveContract)
	{
		var strContract = sl_contracts1.GetItemTextRaw(sl_contracts1.curSel, "pk_contract_id");
		var strContractCMDBID = sl_contracts1.GetItemTextRaw(sl_contracts1.curSel, "fk_cmdb_id");		
		if ((strContract=="")||(strContractCMDBID==""))return;
	
		if (!confirm("Are you sure you want to remove the selected contract from this ci ?")) return;
		{
    		//--
			//-- delete ci relationship (always assume that ME is parent)
			app.cmdb.delete_meconfigrelation(strContractCMDBID, _swdoc.oCI().pk_auto_id);
    	} 		
	}
	else if(nItemID == iSearchContract)
	{
		var strURL = "canreset=0&fk_company_id=" + app.pfu(_swdoc.oCI().fk_supplier);
		var oRes = app.g.search_for("contract",true,strURL);
		if(oRes.selectedkeys=="")return;
		
		var oOrgRec =null;
		var arrKeys = oRes.selectedcmdbids.split(",");
		var arrCompanyKeys = oRes.selectedcompanyids.split(",");	
		var strCICompanyKeys = app.cmdb.get_ci_company_ids(_swdoc.oCI().pk_auto_id,false);
		//-- associate contracts
		var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultContractCIRelationship");   		
		var strCompanyRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgCIRelationship");		
		for(var x=0; x< arrKeys.length;x++)
		{
			app.cmdb.generate_meconfigrelation(arrKeys[x], _swdoc.oCI().pk_auto_id, strRel);		
			
			//-- if contract belongs to a org that does not use this CI then create ci - org relationship			
			if(strCICompanyKeys.indexOf(arrCompanyKeys[x])==-1)
			{
				//-- make company and ci relationship
				//-- get company fk_cmdb_id
				oOrgRec = app.g.get_record("company", arrCompanyKeys[x]);
				if(oOrgRec)
				{
					app.cmdb.generate_meconfigrelation(oOrgRec.fk_cmdb_id, _swdoc.oCI().pk_auto_id, strCompanyRel);
				}
			}
		}
		
	}    

	//-- refresh list
	var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
	var strFilter = "swc=pk_contract_id&kvs=0";
	if(strContracts!=0)
	{
		strFilter = "swc=pk_contract_id&kvs="+ strContracts;
	}
	app._slf(sl_contracts1,strFilter);
	_swdoc.MF().sl_contracts1.Refresh();

}

							TabRel_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	var tContract=5;
	
	if(nTab==tContract)
	{
		app._een(mbtn_ci_contract_actions1,(_swdoc.oCI().pk_auto_id>0));
		var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
		app._slf(sl_contracts1,"swc=pk_contract_id&kvs=0");
		if(strContracts!=0)
		{
			app._slf(sl_contracts1,"swc=pk_contract_id&kvs="+ strContracts);
		}
		_swdoc.MF().sl_contracts1.Refresh();
	}
	else if(nTab==2)
	{
		app._slf(_swdoc.MF().sl_service_sla,"swc=fk_service&kvs="+config_itemi.pk_auto_id);
		app.g.apply_appcode_sla_filter(_swdoc.MF().sl_service_sla,'SLA-SERVICE');
		app._slf(_swdoc.MF().fld_fk_default_sla,"swc=fk_service&kvs="+config_itemi.pk_auto_id);
		app.g.apply_appcode_sla_filter(_swdoc.MF().fld_fk_default_sla,'SLA-SERVICE');
		_swdoc.MF().sl_service_sla.Refresh();
		
		//-- Disable remove button if nothing selected.
		var selCount = app.g.sl_getselected_count(_swdoc.MF().sl_service_sla);
		if (selCount==0) app._een(_swdoc.MF().btn_service_sla,false);
		
		
	}
	else if(nTab==3)
	{
		var strOLAs = app.g.get_ci_olas(_swdoc.oCI().pk_auto_id);
		app._slf(_swdoc.MF().sl_olas1,"swc=pk_ola_id&kvs="+strOLAs);

		// RJC F0104320
		app.g.apply_appcode_sla_filter(_swdoc.MF().sl_olas1,'OLA');
		_swdoc.MF().sl_olas1.Refresh();
	}else if(nTab==0)
	{
		_swdoc.MF().TabDep_OnTabSelected(0);

	}else if(nTab==1)
	{
		_swdoc.MF().sl_component.Refresh();
		_swdoc.MF().sl_optional_component.Refresh();
	}

}

							fld_fk_supplier1_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	if(_swdoc.oCI().pk_auto_id>0)
	{
		if(_swdoc.strCurrentSupplier!="")
		{
			var strContracts = app.cfg.get_ci_contractkeys(_swdoc.oCI().pk_auto_id,true);
			if(strContracts!=false)
			{
				if(confirm("Altering the supplier will remove the associated contracts. Do you want to continue?"))
				{
					_swdoc.Save(0);
					app._slf(sl_contracts1,"swc=pk_contract_id&kvs="+ strContracts);
					var strContractCMDBID = app.g.sqllist_get_valuesdel(mainform.sl_contracts1, "fk_cmdb_id", ",", "");
					if (strContractCMDBID!="")
					{
						var arrCMDBIDs = strContractCMDBID.split(",");
						for(var i=0; i<arrCMDBIDs.length;i++)
						{
							app.cmdb.delete_meconfigrelation(arrCMDBIDs[i], _swdoc.oCI().pk_auto_id);
						}
					}
					app._slf(sl_contracts1,"swc=pk_contract_id&kvs=0"); 
				}else
				{
					_swdoc.oCI().fk_supplier = _swdoc.strCurrentSupplier;
					_swdoc.UpdateFormFromData();
				}
			}
		}

		var tHolder = mainform.TabRel.tab;
		mainform.TabRel.ShowTabItem(5,_swdoc.oCI().fk_supplier!="");
		app._etab(mainform.TabRel,tHolder);
	}
	
	_swdoc.strCurrentSupplier = strValue;
	
}

