							mbtn_ola_actions2_OnMenuItem(strName, nItemID)
{
	var iAssignKB = 1;
	var iRemoveKB = 2;	

	
	if(nItemID == iAssignKB)
	{
        app.g.search_for("swkb", true,"",function(oRes)
        {
        	var strKeys = oRes.selectedkeys;
			if(strKeys=="")return;
			var arrKeys = strKeys.split(",");
			for (var x=0; x< arrKeys.length;x++)
			{
				var fk_key = arrKeys[x];
				if(app.g.sl_findrow_byvalue(sl_kb1,fk_key,"docref")==-1)
				{
					app.oKB.relate_ci_to_kb(_swdoc.oCI().pk_auto_id,fk_key);
					app.g.sqllist_appendfilter(sl_kb1, fk_key, "docref");		
				}
			}
			sl_kb1.Refresh();
			sl_kb1.SetRowSelected(0);
			sl_kb1_OnItemSelected(0);
			return;
        });
    }
	else if(nItemID == iRemoveKB)
	{
		var fk_kb_id = app.g.sl_getprikeyvalue(sl_kb1, sl_kb1.curSel);
		if(fk_kb_id!="")
		{
			var strConfirm = "Are you sure you want to remove the selected Knowledgebase article from the Configuration Item?";
			if(confirm(strConfirm))
			{
				app.oKB.unrelate_ci_from_kb(fk_kb_id,_swdoc.oCI().pk_auto_id);	
			}
			var strDocs = app.oKB.get_cis_kbs(_swdoc.oCI().pk_auto_id);
			if(strDocs=="")
				strDocs="0";
			sl_kb1.storedQuery = "common.load_by_colname";
			app._slf(sl_kb1, "swc=docref&kvs="+strDocs);
		}
		_swdoc.MF().sl_kb1.Refresh();
	}
	




}

							btn_del_subscription2_OnPressed()
{
	if(!app.service.can_update(true))
		return false;
	
	var strConfirm = "Are you sure you want to remove the selected usage plan?";
	if (confirm(strConfirm))
	{
		var strPriKey = app.g.sl_getprikeyvalue(sl_target, sl_target.curSel);
		var strParams = "key="+app.pfu(strPriKey);
		app.g.submitsqs("service/delete_usage_plan", strParams);	
		sl_target.RemoveRow(sl_target.curSel);
		sl_target.SetRowSelected(0);
	}	
}

							btn_add_target_OnPressed()
{
	if(!app.service.can_update(true))
		return false;

	//-- get intCI
  	var intService = _swdoc.oService().fk_cmdb_id;
  	if(intService>0)
  	{
		app.OpenFormForAdd("sc_target", "", "fk_auto_id="+_swdoc.oService().fk_cmdb_id, true, function(x){
			sl_target.Refresh();
		});
	}

}

