							mbtn_view_targets_OnMenuItem(strName, nItemID)
{
	var iActualvsForecast = 1;
	var iActualvsForecastSubs = 2;
	var iCost = 3;

	if(IsObjectDefined("_bHtmlWebClient"))
    {
    	var getColourScheme = 4; //silver default
    }else
    {
    	var getColourScheme = app.g.get_CurrentSkinColorSchema();
    }

	//-- get intCI
  	var intService = _swdoc.oService().fk_cmdb_id;
  	if(intService>0)
  	{
		//-- pass in text identfier as well
  		if(nItemID == iActualvsForecast)
		{
			var intTarget = app.g.sl_getprikeyvalue(sl_target, sl_target.curSel);
			var result = app.OpenHtmlWindow("consumption", "frame", "&[app.webroot]/clisupp/details/&[app.currentdd]/service_actual_vs_forecast_consump.php?target="+intTarget+"&sessid="+app.session.sessionId+"&ColourScheme="+getColourScheme, 
                                  "Actual vs Forecasted Consumption", true,600,450);
 		}
 		else if(nItemID == iActualvsForecastSubs)
		{
			var intTarget = app.g.sl_getprikeyvalue(sl_target, sl_target.curSel);
			var result = app.OpenHtmlWindow("subscription_request", "frame", "&[app.webroot]/clisupp/details/&[app.currentdd]/service_actual_vs_forecast_subsc.php?target="+intTarget+"&sessid="+app.session.sessionId+"&ColourScheme="+getColourScheme, 
                                  "Actual vs Forecasted Subscriptions", true,600,450);
  		}
		else if(nItemID == iCost)
		{
			var intTarget = app.g.sl_getprikeyvalue(sl_target, sl_target.curSel);
			var result = app.OpenHtmlWindow("subscription_cost", "frame", "&[app.webroot]/clisupp/details/&[app.currentdd]/service_actual_vs_forecast_cost.php?target="+intTarget+"&sessid="+app.session.sessionId+"&ColourScheme="+getColourScheme, 
                                  "Actual vs Forecasted Costs", true,600,450);
  		}
	}
}

