							btn_calc1_OnPressed()
{
	app._een(btn_rec_ag1,true);
		
	//-- get data based on dates
	var intOperationalDownMinutes = 0;
	var intTotalDownMinutes = 0;	
	var rowcount= 0;
	var intLastEndDatex = 0;
	var intSecondsBetweenFailures = 0;
	var intTotalMinutesToResolve = 0
	
	var oRS = app.cmdb.get_availrecs_started_between(_swdoc.oCI().pk_auto_id, _swdoc.MF().tb_aggstart1.value, _swdoc.MF().tb_aggend1.value)
	while(oRS.Fetch())
	{
		if(rowcount>0)
		{
			intSecondsBetweenFailures = intSecondsBetweenFailures + (app.g.get_field(oRS,"startedonx",0) - intLastEndDatex);
		}
		rowcount++;
		intTotalMinutesToResolve = intTotalMinutesToResolve + new Number(app.g.get_field(oRS,"minutesdown",0));
		intOperationalDownMinutes = intOperationalDownMinutes + new Number(app.g.get_field(oRS,"opminutesdown",0));
		intTotalDownMinutes = intTotalDownMinutes + new Number(app.g.get_field(oRS,"minutesdown",0));			
		intLastEndDatex = new Number(app.g.get_field(oRS,"endedonx",0))
	}
	
	//-- reset to 0.00	
	app._ete(lbl_recordcount1,("Number of records : " + rowcount));
	app._ete(tb_totaldowntime1,"0.00");
	app._ete(tb_totalopdowntime1,"0.00");
	app._ete(tb_avgopdowntime1,"0.00");
		
	app._ete(tb_totaluptime1,"0.00");
	app._ete(tb_totalopuptime1,"0.00");
	app._ete(tb_avgopuptime1,"0.00");
			
	app._ete(tb_avgissuetime1,"0.00");
	app._ete(tb_avgresolutiontime1,"0.00");

	//--- sote recount in doc var so can use when saving
	_swdoc.intAvailIssueCount = rowcount;
	if(rowcount==0) return;
	
	//-- if last end date is greate than range then adjsut range.
	var intUseLastDatex = (intLastEndDatex>_swdoc.MF().tb_aggend1.value)?intLastEndDatex:_swdoc.MF().tb_aggend1.value;
	var fmtintUseLastDatex = new Number(intUseLastDatex) +"";
	app._eva(tb_aggend1,fmtintUseLastDatex);
	app._ete(tb_aggend1,fmtintUseLastDatex);
		
	//-- then get total op down times and total down times using sqllist functions
	var avgOpDownTime =  Math.round(intOperationalDownMinutes / rowcount);	
	
	//-- now work out the working time between the two dates using (uses current operational hours to work out)
	//-- calc to based on end date of last record
	var intOperationalMinutes = app.cmdb.get_unavailable_workingtime(_swdoc.oCI().pk_auto_id, _swdoc.MF().tb_aggstart1.value, intUseLastDatex);
	var intActualOperationlMinutes = (intOperationalMinutes > intOperationalDownMinutes)?intOperationalMinutes - intOperationalDownMinutes: intOperationalDownMinutes - intOperationalMinutes;
	
	//-- work out total up time in hours and mins
	var intTotalSeconds = intUseLastDatex - _swdoc.MF().tb_aggstart1.value;
	var intTotalUpMinutes = new String(Math.round(intTotalSeconds / 60)).split(".")[0];
	intTotalUpMinutes = intTotalUpMinutes - intTotalDownMinutes;
	var avgOpUpTime =  Math.round(intActualOperationlMinutes / rowcount);
	
	//--
	//-- now do averages
	var intMinutesBetweenFailures = new String(Math.round(intSecondsBetweenFailures / 60)).split(".")[0];
	var avgTimeBetweenFailures =  Math.round(intMinutesBetweenFailures / rowcount);	
		
	//-- resolve time
	var avgTimeToResolve =  Math.round(intTotalMinutesToResolve / rowcount);	
	
	//-- show in text boxes
	app._ete(tb_totaldowntime1,app.g.hoursminutes_fromminutes(intTotalDownMinutes));
	app._ete(tb_totalopdowntime1,app.g.hoursminutes_fromminutes(intOperationalDownMinutes));
	app._ete(tb_avgopdowntime1,app.g.hoursminutes_fromminutes(avgOpDownTime));
	
	app._ete(tb_totaluptime1,app.g.hoursminutes_fromminutes(intTotalUpMinutes));	
	app._ete(tb_totalopuptime1,app.g.hoursminutes_fromminutes(intActualOperationlMinutes));
	app._ete(tb_avgopuptime1,app.g.hoursminutes_fromminutes(avgOpUpTime));
		
	//-- avg tbs
	app._ete(tb_avgissuetime1,app.g.hoursminutes_fromminutes(avgTimeBetweenFailures));
	app._ete(tb_avgresolutiontime1,app.g.hoursminutes_fromminutes(avgTimeToResolve));
	
}

