							mbtn_aba_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignBusArea = 1;
	var iRemoveBusArea = 2;	

	
	if(nItemID == iAssignBusArea)
	{
        app.g.search_for("bus_area", true,"",function(oRes)
        {
        	var strKeys = oRes.selectedkeys;

			if(strKeys=="")return;
			var arrKeys = strKeys.split(",");
			for (var x=0; x< arrKeys.length;x++)
			{
				var fk_key = arrKeys[x];
				if(app.g.sl_findrow_byvalue(sl_bus_area1,fk_key,"fk_bus_area_id")==-1)
				{			
					app.cmdb.insert_affected_bus_area(_swdoc.oCI().ck_config_item, fk_key);
				}
			}
		
			sl_bus_area1.Refresh();
			sl_bus_area1.SetRowSelected(0);
			sl_bus_area1_OnItemSelected(0);
			return;
        });
    }
	else if(nItemID == iRemoveBusArea)
	{
		var strID = sl_bus_area1.GetItemTextRaw(sl_bus_area1.curSel, 'fk_bus_area_id');	
		if(strID!="")
		{
			var strConfirm = "Are you sure you want to remove the selected Business Area from the Service?";
			if(confirm(strConfirm))
			{
				app.cmdb.delete_affected_bus_area(_swdoc.oCI().ck_config_item, strID, "");
				sl_bus_area1.Refresh();
			}
		}
	}
	




}

