							btn_call_next_OnPressed()
{
	_swdoc.intPageNo++;
	
	//-- Add Paging
	var strFilter = "&strFilter="+_swdoc.strFilter;
	strFilter += "&paged=true&start="+_swdoc.intPageNo+"&end=&ci_id="+_swdoc.oCI().pk_auto_id+"&webclient="+_swdoc.webclient+"";
	
	app.g.sl_filter(sl_incs,strFilter,null,0)
	_swdoc.MF().sl_incs.Refresh();
	if(_swdoc.MF().sl_incs.rowCount() < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_call_next,false);	
	}
	app._een(_swdoc.MF().btn_call_prev,true);
		
}

							btn_call_prev_OnPressed()
{
	_swdoc.intPageNo--;
	
	//-- Add Paging
	var strFilter = "&strFilter="+_swdoc.strFilter;
	
	strFilter += "&paged=true&start="+_swdoc.intPageNo+"&end=&ci_id="+_swdoc.oCI().pk_auto_id+"&webclient="+_swdoc.webclient+"";
	app.g.sl_filter(sl_incs,strFilter,null,0)
	_swdoc.MF().sl_incs.Refresh();
	if (_swdoc.intPageNo==1) 
	{
		app._een(_swdoc.MF().btn_call_prev,false);
	}
	app._een(_swdoc.MF().btn_call_next,true);
}

