							btn_search_OnPressed()
{
	_swdoc.strFilter = ""; 
	var intPageNo = 1;
	_swdoc.intPageNo = 1;
	var strFilter = ""

	if(fd_class.value != ""){
		_swdoc.strFilter += " AND CALLCLASS = '" + fd_class.value + "'";
	}
	if(fd_id.value != ""){
		var strCallRef = app.global.GetCallRefValue(_swdoc.MF().fd_id.text);	
		_swdoc.strFilter += " AND callref LIKE '% " + strCallRef + " %'";
	}
	
	//--Update Document so Filter is Global
	strFilter = "&strFilter="+_swdoc.strFilter;

	//-- Add Paging
	strFilter += "&paged=true&start="+intPageNo+"&end=&ci_id="+_swdoc.oCI().pk_auto_id+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_incs,strFilter,null,0)
	_swdoc.MF().sl_incs.Refresh();
	
	if(_swdoc.MF().sl_incs.rowCount()  < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_call_next,false);	
	}else
	{
		app._een(_swdoc.MF().btn_call_next,true);	
	}
	app._een(_swdoc.MF().btn_call_prev,false);
}

