							btn_service_slr_OnPressed()
{
	var strID = app.g.sl_getselected_valuesdel(sl_service_slr,"pk_slad_id",",");
	if(strID!= "")
	{
		if(!app.service.can_update(true))
			return false;
		var strConfirm = "Are you sure you want to dis-associate the selected SLR??";
		if(confirm(strConfirm))
		{
			var service_name = app.g.sl_getcolvalue(sl_service_slr, -1, "description");
			var pk_auto_id = app.g.sl_getcolvalue(sl_service_slr, -1, "fk_cmdb_id");
			var pk_id = app.g.sl_getcolvalue(sl_service_slr, -1, "pk_slr_id");
			app.cmdb.delete_meconfigrelation(config_itemi.pk_auto_id,pk_auto_id);
			
			var strSLAs = app.g.get_srs_slrs(config_itemi.pk_auto_id);
			var strFilter = "swc=fk_cmdb_id&kvs="+strSLAs;
			// RJC F0104320
			app._slf(_swdoc.MF().sl_service_slr , strFilter);
			_swdoc.MF().sl_service_slr.Refresh();
			
			//--SLR Diary
			strText = "Service " + "(" + sc_folio.vsb_title + ") Dis-associated.";
			var p = {};
			p.fsi = pk_id;
			p.ut = strText;
			p.uc = "Service Association";
			p.aid = app.session.analystId;							
			p.ux = 	app.g.todaysdate_epoch();										
 			app.g.submitsimplesql("itsmsp_slrd_diary.insert", p);
		}
		
	}
		
}

							btn_new_service_slr_OnPressed()
{
	if(!app.service.can_update(true))
		return false;

	if(!app.oSettings.get_setting("boolFilterAppcodeSLALoaded"))
			app.g.load_appcode_sla();
		var strFilter = "";
		var strAppcodes = app.oSettings.get_setting("strFilterAppcodeSLR");
		if(strAppcodes!="")
			strFilter = "appcode in ('"+strAppcodes+"')";
		var strURL = "mode=select&filter="+app.pfu(strFilter);
		app.OpenForm("browse_slrs", strURL, true, function(x){
			var intSlrId = x.document.strSelectedKey;
			if(intSlrId=="")return;
			
			//-- check that sla is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(_swdoc.MF().sl_service_slr,intSlrId,"fk_cmdb_id");
			if (rowPos == -1){ 	
				var strRel = "Uses";
				app.cmdb.generate_meconfigrelation(config_itemi.pk_auto_id,intSlrId,strRel);  
				
				var strSLAs = app.g.get_srs_slrs(config_itemi.pk_auto_id);
				var strFilter = "swc=fk_cmdb_id&kvs="+strSLAs;
				// RJC F0104320
				app._slf(_swdoc.MF().sl_service_slr , strFilter);
				_swdoc.MF().sl_service_slr.Refresh();
			}
			//--SLR Diary
			strText = "Service " + "(" + sc_folio.vsb_title + ") associated.";
			var p = {};
			rowPos = app.g.sqllist_findrow_byvalue(_swdoc.MF().sl_service_slr,intSlrId,"fk_cmdb_id");
			p.fsi = app.g.sl_getcolvalue(_swdoc.MF().sl_service_slr, rowPos, "pk_slr_id");
			p.ut = strText;
			p.uc = "Service Association";
			p.aid = app.session.analystId;							
			p.ux = 	app.g.todaysdate_epoch();										
 			app.g.submitsimplesql("itsmsp_slrd_diary.insert", p);

		});
}

