function reset_form_fields()
{
	//-- clear search fields
	app.g.clear_form_fields("config_itemi",mainform,"",false);

	app._ete(_swdoc.MF().lb_abln, "Yes");
	app._ete(_swdoc.MF().lb_abln1, "Yes");
	app._ete(_swdoc.MF().lb_authorised,"Yes");

	_swdoc.setup_fixed_criteria();

	//-- reset date values of ci other and ext fields
	_swdoc.ext_field1_fromdate = 0;_swdoc.ext_field1_todate = 0;
	_swdoc.ext_field2_fromdate = 0;_swdoc.ext_field2_todate = 0;
	_swdoc.ext_field3_fromdate = 0;_swdoc.ext_field3_todate = 0;
	_swdoc.ext_field4_fromdate = 0;_swdoc.ext_field4_todate = 0;

	//-- used to store date values if other ci fields are being used for dates
	_swdoc.ci_field1_fromdate = 0;_swdoc.ci_field1_todate = 0;
	_swdoc.ci_field2_fromdate = 0;_swdoc.ci_field2_todate = 0;
	_swdoc.ci_field3_fromdate = 0;_swdoc.ci_field3_todate = 0;
	_swdoc.ci_field4_fromdate = 0;_swdoc.ci_field4_todate = 0;	

	//-- setup other ci fields
	var arrIncludeOnlyFields = new Array();
	var oRS = app.g.get_sqrecordset("select.table","table=cmdb_srch_fields");	
	while(oRS.Fetch())
	{
		var strField = app.g.get_field(oRS,"pk_fieldname");
		arrIncludeOnlyFields[strField] = true;
	}
	
	var strList = app.g.dd_getcolumnlist("config_itemi",true, arrIncludeOnlyFields);
	strList = "Select Field...^0|" + strList;
	_swdoc.MF().ci_field1.pickList = strList;
	_swdoc.MF().ci_field2.pickList = strList;
	_swdoc.MF().ci_field3.pickList = strList;
	_swdoc.MF().ci_field4.pickList = strList;
	app._eva(_swdoc.MF().ci_field1_value,"");
	app._eva(_swdoc.MF().ci_field2_value,"");
	app._eva(_swdoc.MF().ci_field3_value,"");
	app._eva(_swdoc.MF().ci_field4_value,"");
	
	
	//-- disable ext search fields
	_swdoc.enable_ext_fields(false);
}

function enable_ext_fields(boolEnable, strType)
{
	
	if(boolEnable)
	{
		boolEnable = false;
	
		//-- load type table name if it has one and get column names from dd object
		var p={};p.pk_config_type = strType
		var oRS = app.g.get_tablerecordset_bycol("config_typei",p,false,"extended_table");		
		if(oRS.Fetch())
		{
			var strExtTable = app.g.get_field(oRS,"extended_table");
			if((strExtTable!="") && (dd.tables[strExtTable]))
			{
				//-- get column list
				var strList = app.g.dd_getcolumnlist(strExtTable,true);
				strList = "Select Field...^0|" + strList;
				_swdoc.MF().ext_field1.pickList = strList;
				_swdoc.MF().ext_field2.pickList = strList;
				_swdoc.MF().ext_field3.pickList = strList;
				_swdoc.MF().ext_field4.pickList = strList;
				boolEnable = true;
				
				_swdoc.strTypeTable = strExtTable;
			}
		}
	}
	
	//-- col fields
	app._een(_swdoc.MF().ext_field1,boolEnable);
	app._een(_swdoc.MF().ext_field2,boolEnable);	
	app._een(_swdoc.MF().ext_field3,boolEnable);	
	app._een(_swdoc.MF().ext_field4,boolEnable);
	app._eva(_swdoc.MF().ext_field1,"0");
	app._eva(_swdoc.MF().ext_field2,"0");
	app._eva(_swdoc.MF().ext_field3,"0");
	app._eva(_swdoc.MF().ext_field4,"0");


	//-- value fields
	app._een(_swdoc.MF().ext_field1_value,boolEnable);
	app._een(_swdoc.MF().ext_field2_value,boolEnable);	
	app._een(_swdoc.MF().ext_field3_value,boolEnable);	
	app._een(_swdoc.MF().ext_field4_value,boolEnable);	
	app._eva(_swdoc.MF().ext_field1_value,"");
	app._eva(_swdoc.MF().ext_field2_value,"");
	app._eva(_swdoc.MF().ext_field3_value,"");
	app._eva(_swdoc.MF().ext_field4_value,"");
}

//-- NWJ - 11.08.2008 - read in url and find any matching bindings - if found set field value and disable
//-- as means we have passed in fixed search criteria - also returns fixed filter
function setup_fixed_criteria()
{
	var boolCanChange = (_swdoc.GetArg('canreset')=="1");
	var strFilter = "";
	for(var x = 0; x < _swdoc.MF().elements.length;x++)
	{
		var element = _swdoc.MF().elements[x];
		if((element.dataRef!="") && ((element.type == FE_FIELD)||(element.type == app.FE_SEARCHFIELD)))
		{
			var arrInfo = element.dataRef.split(".");
			var strFixedValue = unescape(_swdoc.GetArg(arrInfo[1]));
			if(strFixedValue!="")
			{
				app._ete(element, strFixedValue);
				app._een(element,boolCanChange);
								
				if(strFilter!="")strFilter += "&";
				strFilter += "_swc_" + element.dataRef + "=" + strFixedValue;
				
			}
			
			//-- F0098488
			//-- If appcode filter is set then apply it to the organisation 
			if((arrInfo[1]=="fk_company_id") && (dd.GetGlobalParamAsNumber("Application Settings/UseAppCodeFilter")==1))
				app._slf(element , "_swc_appcode IN("+app.itsm.get_session_param("datasetFilterList")+")");	
		}
		
	}
	
	return strFilter;
}

function switch_view(intAdvanced)
{

	if(intAdvanced)
	{
		app._eva(_swdoc.MF().fld_item_basic, _swdoc.MF().fld_item_adv.value);
		app._eva(_swdoc.MF().fld_comp_basic, _swdoc.MF().fld_comp_adv.value);
		app._eva(_swdoc.MF().fld_owner_basic, _swdoc.MF().fld_owner_adv.value);
		app._ete(_swdoc.MF().lb_abln1, _swdoc.MF().lb_abln.text);
		_swdoc.MF().TabSearch.ShowTabItem(1,true);        	
		_swdoc.MF().TabSearch.ShowTabItem(0,false);    
		_swdoc.MF().TabRes1_OnTabSelected(_swdoc.MF().TabRes1.tab);    
	}
	else
	{
		app._eva(_swdoc.MF().fld_item_adv,_swdoc.MF().fld_item_basic.value);
		app._eva(_swdoc.MF().fld_comp_adv, _swdoc.MF().fld_comp_basic.value);
		app._eva(_swdoc.MF().fld_owner_adv, _swdoc.MF().fld_owner_basic.value);
		app._ete(_swdoc.MF().lb_abln,_swdoc.MF().lb_abln1.text);
		_swdoc.MF().TabSearch.ShowTabItem(0,true);        	
		_swdoc.MF().TabSearch.ShowTabItem(1,false);        	
		_swdoc.MF().TabRes_OnTabSelected(_swdoc.MF().TabRes.tab);    	
	}

}

