 	var intControlType = app.g.dd_fieldtype('config_itemi',ci_field2.value-1);
 	if(intControlType==5)
 	{
 		//-- popup date selector and put value into 
 		
 		var strFieldLabel = app.g.dd_fieldlabel('config_itemi',ci_field2.value-1);
 		app.g.popup("popup_date_selector","label_name=" + strFieldLabel, function(aForm)
 		{
 			if (aForm)
 			{
 				if(aForm.document.fromDate!="")
 				{
 					var arrFrom = aForm.document.fromDate.split("|");
					var arrTo = aForm.document.toDate.split("|"); 			
					app._eva(ci_field2_value, "Between " + arrFrom[1].substring(0,10) + " and " + arrTo[1].substring(0,10));
 					
 					_swdoc.ci_field2_fromdate = arrFrom[0];
 					_swdoc.ci_field2_todate = arrTo[0];
 				}
 				return false;
 			}
 		});
	}

	_swdoc.ci_field2_fromdate = 0;
	_swdoc.ci_field2_todate = 0;
	return true;
}

