							btn_subset1_OnPressed()
{
	//-- add selected items to the subset list
	var strSelKeys = app.g.sl_getselected_valuesdel(sl_results1	,'pk_auto_id',",","");
	var strSubKeys = app.g.sl_get_valuesdel(sl_sub1,'pk_auto_id',",","");
	
	//-- if we already have sub calls
	if(strSubKeys!="")
	{
		strSelKeys += "," + strSubKeys;
	}
	
	var strFilter = "_kv=" + strSelKeys;
	app.g.sl_filter(sl_sub1,strFilter,null,0);
	_swdoc.MF().sl_sub1.Refresh();
}

