							btn_search1_OnPressed()
{
	_swdoc.strFilter = ""; 
	var intPageNo = 1;
	_swdoc.intPageNo = 1;
	
	//-- 19.01.2006 - NWJ
	//-- create search criteria
	app._etab(TabRes,0);
	app._een(mbtn_view_ci,false);
	app.g.sqllist_clear(sl_results);
	
	var boolAND = (lb_type1.value==1);
	var strFilter = app.g.create_search_params(mainform,"config_itemi","#fffffe",boolAND,false);
	var strOrAnd = (boolAND)?" AND ":" OR ";
	
	//-- nwj 15.07.2008 - show ME items?
	if(dd.GetGlobalParamAsString("Application Settings/CMDB/ShowManagedItemsWhenAssociatingToRequests")!=1)
	{
		if(strFilter!="")strFilter+="&"
		strFilter+="sf=cilist_exludemes"; //- -add static filter to exclude mes
	}	
	
	//--Update Document so Filter is Global
	_swdoc.strFilter = strFilter;
	
	//-- Add Paging
	strFilter += "&paged=true&start="+intPageNo+"&end=";
	
	//-- apply filter
	app.g.sl_filter(sl_results1,strFilter,null,0)
	_swdoc.MF().sl_results1.Refresh();
	
	//Show Next Button
	if(sl_results1.rowCount() < _swdoc.intCIsPerPage)
	{
		app._een(_swdoc.MF().btn_next1,false);	
	}else
	{
		app._een(_swdoc.MF().btn_next1,true);	
	}
	app._een(_swdoc.MF().btn_prev1,false);
}

