							btn_prev1_OnPressed()
{
	_swdoc.intPageNo--;
	
	//-- Add Paging
	var strFilter = _swdoc.strFilter;
	strFilter += "&paged=true&start="+_swdoc.intPageNo+"&end=";
	
	//-- apply filter
	app.g.sl_filter(sl_results1,strFilter,null,0)
	_swdoc.MF().sl_results1.Refresh();
	
	if (_swdoc.intPageNo == 1) 
	{
		app._een(_swdoc.MF().btn_prev1,false);
	}
	app._een(_swdoc.MF().btn_next1,true);
	
}

