fld_organiser_OnLeftClick()
{
	app.g.pick_analyst("Select Analyst", function(oAnalyst)
	{
		if (!oAnalyst)
			return false;
		app._ete(_swdoc.MF().fld_organiser , oAnalyst.name);
		_swdoc.oReview().organiser = oAnalyst.id;
		_swdoc.UpdateFormFromData();		
	});
	return false;
}

fld_organiser_OnRightClick()
{
	app._ete(fld_organiser, "");
	app._eva(fld_organiser, "");
	slad_review_details.organiser = "";
	_swdoc.UpdateFormFromData();
	return true;
}
