//###20120801
function multi_c_set_company(companyId){

	var c = app.g.get_record("company", companyId);
	
	
	if (c){
		_swdoc.OC().fk_company_id = c.pk_company_id;	
		_swdoc.OC().companyname = c.companyname;
		
		app._eva(_swdoc.MF().pl_oc_org , c.pk_company_id);
		app._ete(_swdoc.MF().pl_oc_org , c.pk_company_id);
		_swdoc.MF().pl_oc_org.display = c.companyname;
		
    	_swdoc.set_org_filter();
	}
}

function set_org_filter(){
 	if (""!=_swdoc.OC().cust_id){
		var strKeyValues = "pk_company_id="+app.pfu(_swdoc.OC().fk_company_id) + "&fk_user_id="+app.pfu(_swdoc.OC().cust_id);
		app._slf(_swdoc.MF().pl_oc_org , strKeyValues);
	} else {
		app._slf(_swdoc.MF().pl_oc_org , "pk_company_id=");
	}
	var strCompFilter = "_swc_fk_company_id=" + _swdoc.MF().pl_oc_org.value;
	app._slf(_swdoc.MF().fld_oc_dept , strCompFilter);
	app._slf(_swdoc.MF().lb_oc_site1 , strCompFilter);
	app._slf(_swdoc.MF().lb_oc_cc1 , strCompFilter);

	_swdoc.OC().site = (_swdoc.OC().fk_company_id.toLowerCase() != _swdoc.oCust().fk_company_id.toLowerCase())?"":_swdoc.oCust().site;
	_swdoc.OC().fk_dept_code = (_swdoc.OC().fk_company_id.toLowerCase() != _swdoc.oCust().fk_company_id.toLowerCase())?"":_swdoc.oCust().department;
	_swdoc.OC().costcenter = (_swdoc.OC().fk_company_id.toLowerCase() != _swdoc.oCust().fk_company_id.toLowerCase())?"":_swdoc.oCust().costcenter;
	_swdoc.OC().fk_contract_id = "";
	_swdoc.handle_set_contract("");	
	_swdoc.UpdateFormFromData();
}



//-- given an analyst object (from app.global.js) load values for either monitored / raised by analyst
//-- for the rfc
function set_rfc_analyst(oAnalyst,boolMonitoredBy)
{
	if(!oAnalyst) 
	{
		//-- invalid so clear fields
		oAnalyst = app.g.get_blank_analyst();
	}
	
	if(boolMonitoredBy)
	{
		_swdoc.RFC_EXT().monitored_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().monitored_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().monitored_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().monitored_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().monitored_by_email = oAnalyst.email;	
	}
	else
	{
		_swdoc.RFC_EXT().raised_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().raised_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().raised_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().raised_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().raised_by_email = oAnalyst.email;
	}
	_swdoc.UpdateFormFromData();	
}


//-- given a ci or list of cis link it to problem cause list
function link_cause_cis_to_change(strLinkCIs)
{
	app.g.sl_kv_appendfilter(_swdoc.EF().sl_configitems1, strLinkCIs,0);
	
	var arrCIs = strLinkCIs.split(",");
	
	//Bespoke Terms variables
	var itemType = "Configuration Item";	
	//--End of Bespoke Terms variables
	
	for(var x = 0; x<arrCIs.length; x++)
	{
		var oCI = app.g.get_record('config_itemi', arrCIs[x]);
		var strParams = "ci_item="+oCI.ck_config_item;
   		var oRS = app.g.get_sqrecordset("call/common/get_ci_abas",strParams);
		while(oRS.Fetch())
		{
			strABA = app.g.get_field(oRS,"FK_BUS_AREA_ID");
			_swdoc.arrABAAssocTypes[strABA] = "CI";
		//	app.g.sl_kv_appendfilter(_swdoc.MF().sl_ABAs1, strABA,0);
			var strKeys = app.g.sqllist_get_valuesdel(_swdoc.MF().sl_ABAs1, "pk_area_id", ",", "");	
			if(strKeys!="")strKeys+=",";
			var strParams = "aid=1&_kv="+strKeys+strABA;
			app._slf(_swdoc.MF().sl_ABAs1,strParams);
			_swdoc.MF().sl_ABAs1.Refresh();
		}
	}
	app.bespokeTermsProcessing(strLinkCIs, itemType);
	//END OF BESPOKE TERMS PROCESSING
	
	//-- if only 1 CI use it default profile unless blank
	if(_swdoc.EF().sl_configitems1.rowCount()==1)
	{
		var strCode = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems1,0,"fk_profilecode");
		if(strCode!="")
		{
			_swdoc.OC().probcode = strCode;
			_swdoc.UpdateFormFromData();		
		}
	}
	_swdoc.EF().sl_configitems1.Refresh();
}

//-- given a ci or list of cis link it to problem cause list
function link_cause_services_to_change(strLinkCIs)
{
	//-- F0101301
	var arrCIs = strLinkCIs.split(",");
	
	//Bespoke Terms variables
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();
	var itemType = "Service";	
	//--End of Bespoke Terms variables
	
	for(var x = 0; x<arrCIs.length; x++)
	{
		var oCI = app.g.get_record('config_itemi', arrCIs[x]);
		var strParams = "ci_item="+oCI.ck_config_item;
   		var oRS = app.g.get_sqrecordset("call/common/get_ci_abas",strParams);
		while(oRS.Fetch())
		{
			strABA = app.g.get_field(oRS,"FK_BUS_AREA_ID");
			_swdoc.arrABAAssocTypes[strABA] = "CI";
		//	app.g.sl_kv_appendfilter(_swdoc.MF().sl_ABAs1, strABA,0);
			var strKeys = app.g.sqllist_get_valuesdel(_swdoc.MF().sl_ABAs1, "pk_area_id", ",", "");	
			if(strKeys!="")strKeys+=",";
			var strParams = "aid=1&_kv="+strKeys+strABA;
			app._slf(_swdoc.MF().sl_ABAs1,strParams);
			_swdoc.MF().sl_ABAs1.Refresh();
		}
		
		// -- SG, 21/08/2012, F0103324
		// Process Bespoke Terms
		if(oCI && oCI.recordresolvedmsg != '')
		{
    		bespokeTermsArray[countBespokeTerms] = new Array(2);
    		bespokeTermsArray[countBespokeTerms][0] = oCI.ck_config_item;
    		bespokeTermsArray[countBespokeTerms][1] = oCI.recordresolvedmsg;
    		countBespokeTerms++;
		}
	}
	app.bespokeTermsOutput(bespokeTermsArray, arrCIs.length, itemType);
	//END OF BESPOKE TERMS PROCESSING

	app.g.sl_kv_appendfilter(_swdoc.EF().sl_services, strLinkCIs,0);

	//-- if only 1 CI use it default profile unless blank
	if(_swdoc.EF().sl_services.rowCount()==1)
	{
		var strCode = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"fk_profilecode");
		if(strCode!="")
		{
			_swdoc.OC().probcode = strCode;
			_swdoc.UpdateFormFromData();		
		}
	}
	_swdoc.EF().sl_services.Refresh();

}

//-- given a ci or list of cis link it to problem affect list
function link_affect_cis_to_change(strLinkCIs)
{
	
	//Bespoke Terms variables
	var arrCIs = strLinkCIs.split(",");
	var countBespokeTerms = 0;
	bespokeTermsArray = new Array();
	var itemType = "Configuration Item";	
	//--End of Bespoke Terms variables
	
	for(var x = 0; x<arrCIs.length; x++)
	{
		// -- SG, 21/08/2012, F0103324
		// Process Bespoke Terms
		var pk_ci_id = arrCIs[x];
		var rec = app.g.get_record("config_itemi", pk_ci_id);
		if(rec && rec.recordresolvedmsg != '')
		{
    		bespokeTermsArray[countBespokeTerms] = new Array(2);
    		bespokeTermsArray[countBespokeTerms][0] = rec.ck_config_item;
    		bespokeTermsArray[countBespokeTerms][1] = rec.recordresolvedmsg;
    		countBespokeTerms++;
    		MessageBox(countBespokeTerms);
		}
	}
	app.bespokeTermsOutput(bespokeTermsArray, arrCIs.length, itemType);
	//END OF BESPOKE TERMS PROCESSING	
	
	app.g.sl_kv_appendfilter(_swdoc.EF().sl_impactcis1, strLinkCIs,"pk_auto_id");
	_swdoc.EF().sl_impactcis1.Refresh();	
}

//-- for removed incidents prompt user to remove attached CIs linked to those incidents
function remove_selected_incident_cis(strSelectedIncs)
{
	var array_RemoveImpactCIS = new Array();
	
	if(_swdoc.EF().sl_impactcis1.rowCount()<1) return;
	
	//--
	//-- get linked inc cis and see if they are linked to problem
	var strLinkedCIs = app.cmdb.get_call_cis(strSelectedIncs , "INCIDENT", "");
	if(strLinkedCIs=="0")return;

	var arrayCIS = strLinkedCIs.split(",");
	for(var z=0;z<arrayCIS.length;z++)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_impactcis1,arrayCIS[z],"pk_auto_id")>-1)	
		{
			array_RemoveImpactCIS[array_RemoveImpactCIS.length++] = arrayCIS[z];			
		}
	}
		
	//-- if we have linked impact inc cis then ask if want to remove them
	if(array_RemoveImpactCIS.length>0)
	{
		if(confirm("There are associated impact configuration items, that are linked to the incidents you are removing. Do you want to remove these items as well?"))
		{
			for(var y=0;y<array_RemoveImpactCIS.length;y++)
			{
				app.g.sl_delrow_byvalue(_swdoc.EF().sl_impactcis1,array_RemoveImpactCIS[y],"pk_auto_id", null,0);
			}
		}
	}
	_swdoc.EF().sl_impactcis1.Refresh();
}

function remove_selected_ke_incs(strSelectedKes)
{
	var array_RemoveKEincs = new Array();
	
	if(_swdoc.EF().sl_incs1.rowCount()<1) return;
	
	//--
	//-- get linked inc cis and see if they are linked to problem
	var strLinkedINCs = app.g.get_slave_calls(strSelectedKes , "PROBLEM-INCIDENT", "");
	if(strLinkedINCs=="0")return;

	var arrayINCS = strLinkedINCs.split(",");
	for(var z=0;z<arrayINCS.length;z++)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_incs1,arrayINCS[z],"callref")!=-1)	
		{
			array_RemoveKEincs[array_RemoveKEincs.length++] = arrayINCS[z];			
		}
	}
		
	//-- if we have linked impact inc cis then ask if want to remove them
	if(array_RemoveKEincs.length>0)
	{
		if(confirm("There are associated incident records, that are linked to the known error you are removing. Do you want to remove these records as well?"))
		{
			for(var y=0;y<array_RemoveKEincs.length;y++)
			{
				app.g.sl_delrow_byvalue(_swdoc.EF().sl_incs1,array_RemoveKEincs[y],"callref", null,0);
			}
			//--
			//-- remove linked incident CIs
			_swdoc.remove_selected_incident_cis(strLinkedINCs);
		}
	}
	_swdoc.EF().sl_incs1.Refresh();
}

//-- for removed incidents prompt user to remove attached CIs linked to those incidents
function remove_selected_ke_cis(strSelectedKes)
{
	if((_swdoc.EF().sl_impactcis1.rowCount()<1)&&(_swdoc.EF().sl_configitems1.rowCount()<1)) return;

	_swdoc.remove_selected_ke_causeci(strSelectedKes);
	_swdoc.remove_selected_ke_impactci(strSelectedKes);	
}

function remove_selected_ke_causeci(strSelectedKes)
{	//--
	//-- get linked ke cis and see if they are linked to problem
	var array_RemoveCIS = new Array();
	var strLinkedCauseCIs = app.cmdb.get_call_cis(strSelectedKes , "PROBLEM-CAUSE", "");
	if(strLinkedCauseCIs=="0")return;

	
	var arrayCIS = strLinkedCauseCIs.split(",");
	for(var z=0;z<arrayCIS.length;z++)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,arrayCIS[z],"pk_auto_id")>-1)
		{
			array_RemoveCIS[array_RemoveCIS.length++] = arrayCIS[z];			
		}
	}
		
	//-- if we have linked impact inc cis then ask if want to remove them
	if(array_RemoveCIS.length>0)
	{
		if(confirm("There are associated change configuration items, that are linked to the known errors you are removing. Do you want to remove these items as well?"))
		{
			for(var y=0;y<array_RemoveCIS.length;y++)
			{
				app.g.sl_delrow_byvalue(_swdoc.EF().sl_configitems1,array_RemoveCIS[y],"pk_auto_id", null,0);
			}
		}
	}
	_swdoc.EF().sl_configitems1.Refresh();
}

