function filter_ke_impact(strKErefs)
{
	app.g.sl_kv_appendfilter(_swdoc.EF().sl_kes1, strKErefs,0);
	_swdoc.EF().sl_kes1.Refresh();
	//-- check if we should add ke incidents
	if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorIncidents")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkIncidents = app.g.get_slave_calls(strKErefs,"PROBLEM-INCIDENT");
		if(strLinkIncidents!="0")
		{
 			_swdoc.filter_inc_impact(strLinkIncidents);
		}
	}

	//-- Now check if we should link new ke ci items as being affect or cause (or both)
	if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorCauseCIs")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var oCis = app.cmdb.get_call_ci_info(strKErefs , "PROBLEM-CAUSE", "cis_excludeservice");
		if(oCis.keys!="")
			_swdoc.link_cause_cis_to_change(oCis.keys);
		
		//-- if dont have info get it otherwise use info we go above
		var oServices = app.cmdb.get_call_ci_info(strKErefs , "PROBLEM-CAUSE", "cis_servicesonly");
		if(oServices.keys!="")
			_swdoc.link_cause_services_to_change(oServices.keys);
	}
	
	if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorImpactCIs")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkedCIs = app.cmdb.get_call_cis(strKErefs , "PROBLEM-AFFECT", "");
		_swdoc.link_affect_cis_to_change(strLinkedCIs);
	}
}

//-- link incidents
function filter_inc_impact(strIncidentRefs)
{
	app.g.sl_kv_appendfilter(_swdoc.EF().sl_incs1,strIncidentRefs ,0);
	_swdoc.EF().sl_incs1.Refresh();

	//-- Now check if we should link new ci items as being affect
	if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyIncidentCIs")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkedCIs = app.cmdb.get_call_cis(strIncidentRefs , "INCIDENT", "");
		_swdoc.link_affect_cis_to_change(strLinkedCIs);
	}

}

function filter_impact_customers(oForm)
{
	//-- get list of customers impacted by these incidents and refresh cust impact list
	var strCustFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'cust_id',",","",true);
	oForm.sl_incs1.Refresh();
	//-- get customers that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_impactcis1,'pk_auto_id',",");	
	var strCustIidsFromImpactCIs = app.cmdb.get_ci_customer_ids(strImpactedCIs,true);
	if(strCustIidsFromImpactCIs!="")
	{
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=strCustIidsFromImpactCIs;	
	}

	//-- get customers affected by the associated kes
	var strKeCallrefs = app.g.sl_get_valuesdel(oForm.sl_kes1,'callref',",");
	if(strKeCallrefs=="")strKeCallrefs="0";
	var strCustIidsFromImpactKes = app.itsm.get_problem_impact_customerids(strKeCallrefs,true);
	if(strCustFilter!="")strCustFilter+=",";
	strCustFilter+=strCustIidsFromImpactKes;	

	if(_swdoc.oCust().keysearch!="")
		strCustFilter+=","+_swdoc.oCust().keysearch;	
	
	app._slf(oForm.sl_customerimpact, "_kv=" + strCustFilter);
	oForm.sl_customerimpact.Refresh();			
}

function filter_impact_orgs(oForm)
{
	//-- get list of orgs impacted by impacted customer
	var strOrgFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'fk_company_id',",","",true);
	
	//-- get orgs that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_impactcis1,'pk_auto_id',",");	
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strImpactedCIs,true);
	if(strOrgIDSFromCIs!="")
	{
		if(strOrgFilter!="")
			strOrgFilter+=",";
		strOrgFilter+=strOrgIDSFromCIs;
	}

	//-- 2.4 include orgs that use change ci
	//-- get orgs that use change cis
	var strChangeCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,'pk_auto_id',",");	
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strChangeCIs,true);
	if(strOrgIDSFromCIs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIDSFromCIs;
	}

	//-- get orgs affected by the associated kes
	var strKeCallrefs = app.g.sl_get_valuesdel(oForm.sl_kes1,'callref',",");
	if(strKeCallrefs=="")strKeCallrefs="0";
	var strOrgIdsFromImpactKes = app.itsm.get_problem_impact_orgids(strKeCallrefs,true);
	if(strOrgIdsFromImpactKes!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIdsFromImpactKes;
	}

	if(_swdoc.oCompany().pk_company_id!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=_swdoc.oCompany().pk_company_id;
	}
	app._slf(oForm.sl_orgimpact, "_kv="+strOrgFilter);
	oForm.sl_orgimpact.Refresh();	
}

function get_company_cis(strCompanyID)
{
	//###20120801 - rewrite to accommodate multiple organisations
	var strKeyValues = app.cfg.get_company_cikeys(strCompanyID,true,true);	

	var strParams = "fk_company_id="+app.pfu(strCompanyID);
    var oRS = app.g.get_sqrecordset("general/get_company_sites",strParams);
	while(oRS.Fetch())
	{
		if(strKeyValues!="")strKeyValues+=",";	
		var strSiteName = app.g.get_field(oRS,"site_name");
		strKeyValues+= app.cmdb.get_site_cis(strSiteName);
	}	
	
	if(strKeyValues=="")
		strKeyValues = "-1";
	_swdoc.multi_companyCIIDS[strCompanyID] = strKeyValues;

}

function check_cis()
{
	var oList =_swdoc.EF().sl_configitems1;
	if(oList.rowCount() =="")
		return;
	var boolCIRemoved = false;
	//###20120801
	var strCompanyID = _swdoc.OC().fk_company_id;
	if(typeof _swdoc.multi_companyCIIDS[strCompanyID] === "undefined")
		_swdoc.get_company_cis(strCompanyID);
		
	var arrCIS = _swdoc.multi_companyCIIDS[strCompanyID].split(",");
	for (var x=oList.rowCount();x > -1;x--)
	{
		var strValue = oList.GetItemText(x,0);
		if(strValue !=""){
			if(app.in_array(arrCIS,strValue))
			{
				oList.SetRowSelected(x,false,true);
			}
			else
			{
				oList.SetRowSelected(x,true,true);
				boolCIRemoved = true;
			}
		}
	}
	_swdoc.EF().mbtn_cci_actions1_OnMenuItem('test',1);
	if(boolCIRemoved)
		MessageBox("CIs were removed from this call, as they were not associated with the currently selected Organisation");
}

function search_for_cis(funcCallback)
{
   	var strURL = "canreset=1&fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id);
	app.cmdb.search_ci(true,strURL,function(sResults)
	{
		if (sResults.selectedkeys=="")
		{
			if ((funcCallback != undefined) && (funcCallback != null))
			{
				funcCallback();
			}
		}
		else
		{
   			var strType = sResults.selectedtypes;
  			if(strType.indexOf("Service")>-1)
  			{
  				var strServiceKeys = "";
  				var strKeys = "";
  				var arrTypes = strType.split(",");
  				var arrKeys = sResults.selectedkeys.split(",");
  				for(var x in arrKeys)
  				{
  					var strThisType = arrTypes[x];
  					//MessageBox(strThisType.indexOf("Service"));
  					if(strThisType.indexOf("Service")>-1)
  					{
  						if(strServiceKeys!="")
  							strServiceKeys+=",";
  						strServiceKeys += arrKeys[x];
  					}else
  					{
  						if(strKeys!="")
  							strKeys+=",";
  						strKeys += arrKeys[x];
  					}
  				}
  				if(strKeys!="")
					_swdoc.link_cause_cis_to_change(strKeys);
  				if(strServiceKeys!="")
					_swdoc.link_cause_services_to_change(strServiceKeys);
  			}
  			else
				_swdoc.link_cause_cis_to_change(sResults.selectedkeys);
		
			//-- nwj - 15.07.2008 - check ci contract
			app.cfg.process_call_contract_check(sResults.selectedkeys, true,_swdoc);
			app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
			if ((funcCallback != undefined) && (funcCallback != null))
			{
				funcCallback();
			}
			
		}
	});	
}

function search_for_services()
{
	//-- JPG - 22.01.2015
	//Definition mode for the new window
	var strURL = "searchmode=1&multiselect=1&canreset=1&fk_company_id=" + app.pfu(_swdoc.oCust().fk_company_id);
	//Path of the new window
	app.g.popup("manage_portfolio", strURL, function(sResults)
	{
		if(!sResults) return;
		if (sResults.document.strSelectedKeys=="") return;
		
		//-- nwj - 15.07.2008 - check ci contract
		_swdoc.link_cause_services_to_change(sResults.document.strSelectedKeys);
		app.cfg.process_call_contract_check(sResults.document.strSelectedKeys, true,_swdoc);
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	});
}



//ITSM MEROPE

//-- check if there is alternative slad settings based on the customers department
function check_customer_department_slad(strSLADID, strDepartment)
{
	if(strDepartment=="")
		return;
	var strParams = "fk_slad_id=" +app.pfu(strSLADID)+ "&fk_dept=" +app.pfu(strDepartment);
 	var oRecSLD = app.g.get_sqrecordset("call/incident/get_dept_sla",strParams);
	if(oRecSLD.Fetch())
	{
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD,"chargeto_cc");
		switch(strNewCC)
		{
			case "[Use Department Charge Centre]":
				strNewCC = _swdoc.oCust().department;
				break;
			case "[Use Customer Charge Centre]":
				strNewCC = _swdoc.oCust().costcenter;
				break;		
			default:
				break;
		}
		//-- eof cc option
			
		//-- set oc values
		if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
		_swdoc.OC().priority = app.g.get_field(oRecSLD,"fk_sla");
	}
}

//-- load sld info and settings
function handle_set_sladef(strNewSLD, boolValidate, boolOnResolve)
{
	if(_swdoc.boolSLAEnforced)
	{
		_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
		_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
		_swdoc.UpdateFormFromData();
		MessageBox("SLA is being enforced from the process.");
		return;
	}

	if(boolValidate==undefined)
		boolValidate==false;
	var strDisplaySLD = strNewSLD;

	var strUseSLD = strNewSLD;
	var boolRelative = false;
	var strRelative = "";

	//-- get actual SLD ID if getting from related record
	switch(strNewSLD)
	{
		case "[Use Profile SLA]":
			if(_swdoc.OC().probcode!="")
			{
				var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
				var oRecSLD = app.g.get_record("itsmsp_slad", oRec.itsm_sla);
				if(oRecSLD)
				{
					strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
					boolRelative = true;
					strRelative = "[Use Profile Priority]";
					//return false;
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Profile SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No profile has been set for this request. The Profile SLA cannot be used.",16);
				return false;
			}
			break;
		case "[Use Charge Centre SLA]":
			if(_swdoc.OC().costcenter!="")
			{
				var strCCUse = _swdoc.OC().costcenter;
				if(_swdoc.OC().costcenter=="[Use Customer Charge Centre]")
				{
					strCCUse = _swdoc.oCust().costcenter;
				}
				if(strCCUse=="")
				{
					_swdoc.OC().itsm_sladef = 0;
					_swdoc.OC().itsm_slaname = "[Use Charge Centre SLA]";
					_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
					_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
					_swdoc.UpdateFormFromData();
					if(boolValidate)
						MessageBox("No charge centre has been set for this request. The charge centre SLA cannot be used.");
					return false;

				}
				else
				{
                    //-- 992268 Add the below line
					_swdoc.OC().costcenter = strCCUse;
					var oRec = app.g.get_record("costcent", strCCUse);
					var oRecSLD = app.g.get_record("itsmsp_slad", oRec.itsm_sla);
					if(oRecSLD)
					{
						strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
						boolRelative = true;
						strRelative = "[Use Charge Centre Priority]";
						//return false;
					}
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Charge Centre SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No charge centre has been set for this request. The charge centre SLA cannot be used.");
				return false;
			}
			break;
		case "[Use Customer SLA]":
			if(_swdoc.oCust().keysearch=="")
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Customer SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No customer selected, cannot resolve customer SLA. Please select a valid SLA.",16);
				return false;
			}
			if(_swdoc.oCust().sld=="")
			{
				_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
				_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No Default SLA is associated to this Customer. Please select a valid SLA.",16);
				else
					MessageBox("Customer does not have a default SLA.");
				return false;
			}
			var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.oCust().sld);
			if(oRecSLD)
			{
				strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
				boolRelative = true;
				strRelative = "[Use Customer Priority]";
				//return false;
			}
		break;		
		case "[Use Process SLA]":
			if(bpm_workflow.pk_workflow_id!="")
			{
				if(bpm_workflow.fk_default_sla_new=="")
				{
					_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
					_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
					_swdoc.UpdateFormFromData();
					MessageBox("No SLA has been set for the requests Business Process. The Process SLA cannot be used.");
					return false;
				}
				else
				{
					var strSLA = bpm_workflow.fk_default_sla_name;
					if(strSLA.indexOf("[Use")>-1 && strSLA.indexOf("SLA]")>-1)
					{
						_swdoc.handle_set_sladef(bpm_workflow.fk_default_sla_name);
						return true;
					}
					else
					{
						//var oRec = app.g.get_record("costcent", strCCUse);
						var oRecSLD = app.g.get_record("itsmsp_slad", bpm_workflow.fk_default_sla_new);
						if(oRecSLD)
						{
							strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
							boolRelative = true;
							strRelative = "[Use Process Priority]";
							//return false;
						}
					}
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Process SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No process has been set for this request. The Process SLA cannot be used.");
				return false;
			}
			break;	}

  	var oRecSLD = app.g.get_sqrecordset("general/get_itsm_sla","kv="+strUseSLD);
	if(oRecSLD.Fetch())
	{	
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD, "chargeto_cc");
		switch(strNewCC)
		{
			case "[Use Department Charge Centre]":
				strNewCC = _swdoc.oCust().department;
				break;
			case "[Use Customer Charge Centre]":
				strNewCC = _swdoc.oCust().costcenter;
				break;		
			default:
				break;
		}
		//-- eof cc option
			
		//-- set oc values
		if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
		_swdoc.OC().itsm_sladef = 	app.g.get_field(oRecSLD, "pk_slad_id");
		_swdoc.OC().itsm_slaname = app.g.get_field(oRecSLD, "slad_id");
		_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
		_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
		
		var strFKSsla = app.g.get_field(oRecSLD, "fk_ssla");
		if(boolRelative)
			_swdoc.handle_set_priority(strRelative);
		else
			_swdoc.handle_set_priority(strFKSsla);
		
		//-- check if customer dept has nay alternative settings
		_swdoc.check_customer_department_slad(strNewSLD,_swdoc.oCust().department);
		
		app._een(_swdoc.MF().lbl_oc_priority, true);
		app._een(_swdoc.MF().lb_oc_sla, true);
		_swdoc.UpdateFormFromData();
	}
	else
	{
		MessageBox("Failed to retrieve service level definition record for '" + strDisplaySLD + "'. Please contact your Supportworks Administrator");
		return false;
	}
	return true;
}

function handle_set_priority(strValue)
{
	if(strValue=="[Use SLA Default Priority]")
	{
		var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.OC().itsm_sladef);
		if(oRecSLD)
		{	
			_swdoc.OC().priority = oRecSLD.fk_ssla;
			_swdoc.strCurrentPriority = _swdoc.OC().priority;
			_swdoc.UpdateFormFromData();
		}
		return false;
	}	
	else if(strValue=="[Use Customer Priority]")
	{
		if(_swdoc.oCust().keysearch!="")
		{
			if(_swdoc.oCust().sld==_swdoc.OC().itsm_sladef)
			{
				if(_swdoc.oCust().priority=="[Use SLA Default Priority]")
					_swdoc.handle_set_priority("[Use SLA Default Priority]");
				else
				{
					_swdoc.OC().priority = _swdoc.oCust().priority;
					_swdoc.UpdateFormFromData();
				}
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The customer SLA is not the same as the currently selected SLA. The Customer priority cannot be used.");
			}
		}
		else
		{
				if(_swdoc.OC().itsm_slaname=="[Use Customer SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("No customer selected. The Customer priority cannot be used.");
				}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Profile Priority]")
	{
		if(_swdoc.OC().probcode!="")
		{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Profile SLA is not the same as the currently selected SLA. The Profile priority cannot be used.");
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Profile SLA]")
			{
				_swdoc.OC().priority="[Use Profile Priority]";
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No profile has been set for this request. The Profile priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Charge Centre Priority]")
	{
		if(_swdoc.OC().costcenter!="")
		{
			var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.priority);
			}
			else
			{
				if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("The cost center SLA is not the same as the currently selected SLA. The cost center priority cannot be used.");
				}
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
			{
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
				_swdoc.OC().priority = strValue;
				_swdoc.strCurrentPriority=_swdoc.OC().priority;
				_swdoc.UpdateFormFromData();
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No cost center has been set for this request. The cost center priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Process Priority]")
	{
		if(bpm_workflow.pk_workflow_id!="")
		{
			//var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(bpm_workflow.fk_default_sla_new==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(bpm_workflow.fk_default_sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Process SLA is not the same as the currently selected SLA. The Process priority cannot be used.");
			}
		}
		else
		{
			_swdoc.OC().priority = _swdoc.strCurrentPriority;
			_swdoc.UpdateFormFromData();
			MessageBox("No process has been set for this request. The process priority cannot be used.");
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else
	{
		if (_swdoc.boolChangeSLASetManually)
		{
			_swdoc.OC().priority = strValue;
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		_swdoc.UpdateFormFromData();
		return false;
	}
}
function handle_set_contract(strNewSLD)
{
	var strDefaultContract = app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract, strNewSLD);
	if(strDefaultContract!="")
	{
		_swdoc.OC().fk_contract_id = strDefaultContract;
		app.g.strLogAndResolveContract = strDefaultContract;
	}
	_swdoc.UpdateFormFromData();
		
}

function process_impact_urgency_change()
{
    if(_swdoc.OC().itsm_sladef!="")
    {
    	//-- if use incident assessment over ci
    	//if(dd.GetGlobalParamAsNumber("Application Settings/Incident/UseCIAssessmentLevels")!=1)
    	//{
    		var strPriority = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
    		if(strPriority!="")
				_swdoc.handle_set_priority(strPriority);
    		_swdoc.UpdateFormFromData();
    	//}
    }
}

						OnFormLoading(strFormName, strCallclass)
{
	//-- can they raise a record of this class

	if(app.itsm.can_create(strCallclass,true)==false) return false;

	return true;
}

						OnRecordValueChanged(strTable, strColumn, strValue, strOldValue)
{
	//-- NWJ - check to see if user has changed the itsm_opencall_rfc table field
	//-- to search for raised by and monitored by analyst - so it works like resolving a customer.
	if(strTable=="itsm_opencall_rfc")
	{
		var oAnalyst = false;
		if((strColumn.indexOf("raised_") == 0)||(strColumn.indexOf("monitored_") == 0))
		{
			//-- get db col name based on passed in col name
			var strDBCol="";
			//MessageBox(strColumn.split("_")[2])
			switch(strColumn.split("_")[2])
			{
				case "analystid":
					strDBCol="analystid";
					break;
				case "name":
					strDBCol="name";
					break;
				case "group":					
					strDBCol="supportgroup";
					break;
				case "telephone":
					strDBCol="contactb";
					break;
				case "email":
					strDBCol="contacte";
					break;
				default:
					MessageBox("The extended rfc column (" + strColumn + ") cannot be matched to swanalysts. please contact your Supportworks Administrator.");
					_swdoc.set_rfc_analyst(false,true);
					return;
					break;								
			}
		}
		if(strColumn.indexOf("raised_") == 0)
		{
			//-- auto search raised by
			app.g.resolve_analyst(strDBCol,strValue,function(oAnalyst)
			{
				if(oAnalyst) _swdoc.set_rfc_analyst(oAnalyst,false);
			});	
		}
		else if (strColumn.indexOf("monitored_") == 0)
		{
			//-- auto search monitored by
			app.g.resolve_analyst(strDBCol,strValue,function(oAnalyst)
			{
				if(oAnalyst) _swdoc.set_rfc_analyst(oAnalyst,true);
			});			
		}
	}
	else if(strColumn=="priority" && strTable == "opencall")
    {
    	if(_swdoc.boolChangeProfile)
    	{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			var oSLA = app.g.get_record("itsmsp_slad", oRec.itsm_sla);

			_swdoc.handle_set_sladef(oSLA.slad_id);
			_swdoc.boolChangeSLASetManually = true;
			_swdoc.handle_set_priority(oRec.sla);
 			_swdoc.UpdateFormFromData();
   		}
    }	
    else if(strColumn=="probcode" && strTable == "opencall")
    {
    	_swdoc.boolChangeProfile = true;
    	app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
  		mainform.SetTimer(4, 100);
  	}
  	else if( (strColumn=="itsm_impact_level")||(strColumn=="itsm_urgency_level") )
    {
    	_swdoc.boolChangeSLASetManually = true; //--107865
    	_swdoc.process_impact_urgency_change();
    }
    else if (strColumn=="fk_company_id"||strColumn=="site"||strColumn=="fk_dept_code"||strColumn=="costcenter"||strColumn=="pk_workflow_id")
	{
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}

    


    
	


}

						OnCallLoggedOK(nCallRef, dateRespondBy, dateFixBy, strCustName, strSla)
{
	//-- NWJ - 12-02.2007 - after logging problem link its CIs and Incidents
    //-- nwj 
    //-- 15.03.2004
    var intCallRef = app.g.callref_unpad(nCallRef);

    //-- loop through causing CI and assign them to this call
	var strDiaryText = "The following Configuration Items are to be changed.\n";
	var boolCIs = false;
    var olist = _swdoc.EF().sl_configitems1;
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolCIs =true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'CPR-CAUSE'))
		{
			// DTH: F0097653 Adding Affected Business Areas from CI to the Call
			//app.g.add_affected_bus_area_to_call(intCallRef, "CI", ci_item_key, "", false);
			strDiaryText    += "Configuration Item (" + ci_item_key + "). \n";
		}
    }

	var strServiceDiaryText = "The following Services are to be changed.\n";
	var boolServices = false;
    var olist = _swdoc.EF().sl_services;
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolServices =true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'CPR-CAUSE'))
		{
			strServiceDiaryText    += "Service (" + ci_item_key + "). \n";
		}
    }

	//-- affected CIs
    var olist = _swdoc.EF().sl_impactcis1;
    var boolAffCIs = false;
	var strCauseDiaryText = "\nThe following Configuration Items will be impacted by change.\n";    
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolAffCIs = true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'CPR-AFFECT'))
		{
			strCauseDiaryText    += "Configuration Item (" + ci_item_key + "). \n"
		}
    }    
    
    //-- if we have CI to add add it
    if (boolCIs)
    {
		var strActionSource = "Change Management";
		var strCode			= "Linking Configuration Items";
		if(boolAffCIs) strDiaryText += strCauseDiaryText;
		app.g.new_dairyevent(intCallRef,strDiaryText,strActionSource,strCode);    
    }
    
       //-- if we have CI to add add it
    if (boolServices)
    {
		var strActionSource = "Change Management";
		var strCode			= "Linking Services";
		app.g.new_dairyevent(intCallRef,strServiceDiaryText,strActionSource,strCode);    
    }

    // -- If we have a CI or Service, then check whether the first Stage requires Authorisations for Service Owner, Technical Owner and CI Owner
    if(boolCIs || boolServices)
    {
		var xmlmc = new XmlMethodCall();
		xmlmc.SetParam("in_callref", intCallRef);
		xmlmc.Invoke("vpme", "checkStageAuth");
	}
	

    //-- insert a relationship between change proposal and rfcs
    var strIncidents = app.g.sl_get_valuesdel(_swdoc.EF().sl_rfcs,0,",");
    if (strIncidents !="")
    {
    	var strText = "This change proposal has been associated with the following change requests\n\n";
    	var arrIncidents = strIncidents.split(",");
    	for(var x=0; x < arrIncidents.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(arrIncidents[x],intCallRef,'RFC-CPR'))
			{
				strText += "Change Request (" + app.g.pad_callref(arrIncidents[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strIncText = "The change proposal (" + nCallRef + ") has been associated to this change request";
				app.g.new_diaryevent(arrIncidents[x],strIncText,"Change Management","Change Proposal Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Change Management","Change Request Association");
    }
   
   //-- insert a relationship between rfc and problem / ke
    var strProblems = app.g.sl_get_valuesdel(_swdoc.EF().sl_kes1,0,",");
    if (strProblems !="")
    {
    	var strText = "This change request has been associated with the following Known Errors\n\n";
    	var arrProblems = strProblems.split(",");
    	for(var x=0; x < arrProblems.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrProblems[x],'RFC-ERROR'))
			{
				strText += "Known Error (" + app.g.pad_callref(arrProblems[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strPrbText = "The change request (" + nCallRef + ") has been associated to this known error.";
				app.g.new_diaryevent(arrProblems[x],strPrbText,"Change Management","Change Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Change Management","Known Error Association");
    }


      //-- insert a relationship between rfc and sr
    var strSRs = app.g.sl_get_valuesdel(_swdoc.EF().sl_srs,0,",");
    if (strSRs !="")
    {
    	var strText = "This change request has been associated with the following Service Requests\n\n";
    	var arrSRs = strSRs.split(",");
    	for(var x=0; x < arrSRs.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrSRs[x],'REQUEST-CHANGE'))
			{
				strText += "Service Request (" + app.g.pad_callref(arrSRs[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strRelText = "The change request (" + nCallRef + ") has been associated to this Service Request.";
				app.g.new_diaryevent(arrSRs[x],strRelText,"Change Management","Change Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Change Management","Service Request Association");
    }

    //-- insert a relationship between incs and doc
    if(!app.g.use_sw_knowledgebase())
	{
		if(app.strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			var strDocs = app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,",");
    		if (strDocs !="")
    		{
    			var strText = "This change request has been associated with the following Knowledgebase Documents\n\n";
    			var arrDocs = strDocs.split(",");
    			for(var x=0; x < arrDocs.length; x++)
    			{
					//-- call app.g method to link problem to incident
					app.g.relate_call_to_rakb(arrDocs[x],intCallRef,'Used','Change',false);
					strText += "Knowledgebase Document (" + arrDocs[x] + ") associated\n";
				}			
				app.g.new_diaryevent(intCallRef ,strText,"Change Management","Knowledgebase Document Association");
    		}
		}
	}
	else
	{
    	var strDocs = app.g.sl_get_valuesdel(_swdoc.EF().sl_kbs1,0,",");
    	if (strDocs !="")
    	{
    		var strText = "This change request has been associated with the following Knowledgebase Document\n\n";
    		var arrDocs = strDocs.split(",");
    		for(var x=0; x < arrDocs.length; x++)
    		{
				//-- call app.g method to link problem to incident
				app.oKB.relate_call_to_kb(arrDocs[x],intCallRef,'CPR-KB');
				strText += "Knowledgebase Document (" + arrDocs[x] + ") associated\n";
			}			
			app.g.new_diaryevent(intCallRef ,strText,"Change Management","Knowledgebase Document Association");
    	}
   }
    
    // DTH: F0097668 20120202
    // If Impact Level Assessment was enabled and completed, load the selected options into cmn_rel_opencall_ila
	if (_swdoc.boolILAEnabled && _swdoc.OC().flg_ila_complete)
	{
		app.g.relate_ila_options_to_call(intCallRef, "Change Log Call Form", _swdoc.strILAOptions);
	}
	
	//-- RJC - F0099436 ABAs as authorisers in first stage
    var strABAs = app.g.sl_get_valuesdel(_swdoc.MF().sl_ABAs1,0,",");
    if (strABAs !="")
    {
    	var arrABAs = strABAs.split(",");
    	for(var x=0; x < arrABAs.length; x++)
    	{
    		var strRelType = _swdoc.arrABAAssocTypes[arrABAs[x]];
			if(strRelType=="CI")
			{
				// link the ABA to the call
				var xmlmc = new XmlMethodCall();
				xmlmc.SetParam("table", "cmn_rel_opencall_aba");
				xmlmc.SetData("fk_callref", intCallRef);
				xmlmc.SetData("fk_bus_area_id", arrABAs[x]);
				xmlmc.SetData("status", 'Active');
				xmlmc.SetData("source", "CI");
				xmlmc.Invoke("data", "addRecord");
			}
			else
			{
				//-- call app.g method to link problem to incident
				app.g.add_affected_bus_area_to_call(intCallRef, strRelType, "", arrABAs[x], false);
			}
		}
		//-- has ABAs, so check if 1st stage requires Auth
		var xmlmc = new XmlMethodCall();
		xmlmc.SetParam("in_callref", intCallRef);
		xmlmc.Invoke("vpme", "checkABAAuth");
    }

    // DTH: Create a history record for the selected Impact Level Assessment Options
	if (_swdoc.strILAOptions != "")
	{	
		var xmlmc = new XmlMethodCall;

		// Set up our input parameters
		xmlmc.SetValue("analystid", app.session.analystId);
		xmlmc.SetValue("callref", intCallRef);
		xmlmc.SetValue("weighting", _swdoc.OC().ila_weighting);
		xmlmc.SetValue("impact_level", _swdoc.OC().itsm_impact_level);
		xmlmc.SetValue("usage_area", "Change Log Call Form");
		xmlmc.SetValue("ila_options", _swdoc.strILAOptions);
		xmlmc.SetValue("create_diary", 0); // Not creating a Diary entry when logging a call
		xmlmc.SetValue("prev_impact_level", "");
		xmlmc.SetValue("prev_weighting", 0);
		
		// Invoke the method
		if(!xmlmc.Invoke("VPME", "ilaCreateCallHist"))
		{
			MessageBox(xmlmc.GetLastError());
		}
	}
	
	//-- F0105032 - audit trail if incoming call	
	if(_swdoc._form.PreloadType==2)
	{
		if(_swdoc.OC().bpm_workflow_id!="")
		{
			var intEpochTime = app.global.GetCurrentEpocTime();
			var oAnalyst = app.g.get_sys_record("swanalysts",app.session.analystId);
			var oGroup = app.g.get_sys_record("swgroups",app.session.groupId);
			var strParams = "fk_workflow_id="+app.pfu(_swdoc.OC().bpm_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				var strTable = arrBinding[0];
				var strColName = arrBinding[1];
				
				var oldValue = "";
				if(strTable=="opencall")
					var oldValue = _swdoc.oOpencall[strColName];
				else
					var oldValue = _swdoc.oExtended[strColName];
				
				var newValue = _swdoc[strTable][strColName];
				if(app.g.dd_isnumeric(strTable, strColName))
				{
					if(newValue<0)
						newValue = app.g.fix_epoch(newValue);
					if(oldValue<0)
						oldValue = app.g.fix_epoch(oldValue);
				}
				if(oldValue!=newValue)
				{
					var strFieldDisplay = dd.tables[app.LC(strTable)].columns[strColName].DisplayName;
					var strFmtOldValue = app.g.dd_format(strTable, strColName, oldValue);
					var strFmtNewValue = app.g.dd_format(strTable, strColName, newValue);
					var oInsertTmp = new app.oXmlmcInsert('oc_audit');
					oInsertTmp.setfield('fk_callref',_swdoc.OC().callref);
					oInsertTmp.setfield('updatetimex',intEpochTime);
					oInsertTmp.setfield('updatedby',app.session.analystId);
					oInsertTmp.setfield('updatedbyname',oAnalyst.name);
					oInsertTmp.setfield('updatedbygroup',app.session.groupId);
					oInsertTmp.setfield('updatedbygroupname',oGroup.name);
					oInsertTmp.setfield('databinding',strBinding);
					oInsertTmp.setfield('bindingfield',arrBinding[1]);
					oInsertTmp.setfield('bindingtable',arrBinding[0]);
					oInsertTmp.setfield('fielddisplay',strFieldDisplay);
					oInsertTmp.setfield('oldvalue',oldValue);
					oInsertTmp.setfield('newvalue',newValue);
					oInsertTmp.setfield('fmtoldvalue',strFmtOldValue);
					oInsertTmp.setfield('fmtnewvalue',strFmtNewValue);
					oInsertTmp.execute();
				}
			}
		}
	}	//-- EOF F0105032 
	
}

						OnValidate()
{

		//-- nwj - hide confirmation if we have no customer
	if(userdb.keysearch=="")
	{
		app.g.bPromptToOpenCallDetails = true;
		app.g.bDoNotShowLogCallConfirmation = true;
	}
	else
	{
		app.g.bDoNotShowLogCallConfirmation = false;
	}	

	
	if(bpm_workflow.flg_requireci==1)
	{
		if(_swdoc.EF().sl_configitems1.rowCount() < 1)
		{
			if(_swdoc.EF().sl_services.rowCount() < 1)
			{
				if(_swdoc.EF().sl_kbs1.rowCount() < 1)
				{
					MessageBox("Before logging this Change Proposal you have to associate at least one change Configuration Item, Service or Knowledgebase Document.");
					return VALIDATE_FAIL;
				}
			}
		}
	}

	//-- 108840 : Check if we have required number of CIs 
	var strCallType = "Change";
	var intCurCINo = _swdoc.EF().sl_configitems1.rowCount() + _swdoc.EF().sl_services.rowCount() + _swdoc.EF().sl_kbs1.rowCount();
	if(!app.itsm.mandatory_ci_check(strCallType, intCurCINo))
	{
		var intReqCINo = app.oSettings.get_setting("int" + strCallType + "OnLoggingRequiredCINo");
		var strPlural = "";
		if (intReqCINo > 1)
		{
			strPlural= "s";
		}
		MessageBox("Before logging this " + strCallType + " Proposal you have to associate at least " + intReqCINo + " change configuration item" + strPlural + ", service" + strPlural + " or Knowkedgebase Document" + strPlural + ".");
		return VALIDATE_FAIL;
	}

	//-- check that planned dates dont conflict / prompt user to confirm
	var strMessage = "";
	var strCIids = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,"pk_auto_id",",","");
	_swdoc.RFC_EXT().dow_note = updatedb.updatetxt;
	

	//-- get schedule for ci or cis
/*	if(_swdoc.MF().tb_schedstart.value>_swdoc.MF().tb_schedend.value)
	{
		MessageBox("The schedule from date is greater than the scheduled to date. Please revise.");
		return VALIDATE_FAIL;
	}
	
	var array_schedule = app.cmdb.check_change_schedule(strCIids, _swdoc.MF().tb_schedstart.value, _swdoc.MF().tb_schedend.value);
	for(intCI in array_schedule)
	{
		//-- for each ci in that has a conflicting schedule get callref (note ci may have more than one conflicting schedule)
		for(intCallref in array_schedule[intCI])
		{
			var arrayInfo 	 = array_schedule[intCI][intCallref].split(",");
			var intRow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,intCI,"pk_auto_id");
			var conflictItem = app.g.sl_field(_swdoc.EF().sl_configitems1,intRow,"ck_config_item");
			
			var startDate = app.g.convert_epochdate(arrayInfo[0]);
			var endDate = app.g.convert_epochdate(arrayInfo[1]);			
			
			strMessage += "Change Item (" + conflictItem + ") \n";
			 //(" + g.pad_callref(intCallref) + ") between the dates of " + startDate + " and " + endDate + "\n";
		}
	}
	if(strMessage!="")
	{
		strMessage = "The following items are already scheduled for change during the schedule dates your provided. Do you still wish to submit this request? \n \n" + strMessage;
		if(confirm(strMessage))
		{
		
		}
		else
		{
			return VALIDATE_FAIL;
		}
	}
	*/
	return VALIDATE_DEFAULT;
}

						OnRecordResolved(strTable)
{
//###MessageBox("Resolved : " + strTable) ;
	if(IsObjectDefined("mainform") && (strTable=="bpm_workflow"))
	{
		var boolSet = true;
		var boolEnable = true;

		if(bpm_workflow.fk_default_sla_new>0)
		{
			var boolSLA = bpm_workflow.flg_sla_apply>0;
			if(boolSLA)
			{
				var boolSet = true;
				if(_swdoc.boolChangeSLASetManually)
				{
					var boolOverride = confirm("It is recommended to use SLA set against the process.\n\n\Would you like to set the SLA to the process value?");
					if(!boolOverride)
						boolSet = false;
				}
				
				if(boolSet)
				{
					if(bpm_workflow.flg_sla_enforce==1)
						boolEnable = false;
				}
			}
			else
			{
				boolSet = false;
			}
		}
		else
		{
			boolSet = false;
		}
		
		if(boolSet)
		{
			//-- F0107934
			_swdoc.boolSLAEnforced = false;
			_swdoc.handle_set_sladef(bpm_workflow.fk_default_sla_name,false,true);
			var boolTempSLASetManually = _swdoc.boolChangeSLASetManually;
			_swdoc.boolChangeSLASetManually = true;
			_swdoc.handle_set_priority(bpm_workflow.fk_default_sla);
			_swdoc.boolChangeSLASetManually = boolTempSLASetManually;
			_swdoc.UpdateFormFromData();
			//END
			if(!boolEnable)
			{
				app._een(mainform.lb_oc_slad1,false);
				app._een(mainform.label_itsm_sladef,false);
				_swdoc.boolSLAEnforced = true;
			}
		}
		
		if(boolEnable)
		{
			app._een(mainform.lb_oc_slad1,true);
			app._een(mainform.label_itsm_sladef,true);
			_swdoc.boolSLAEnforced = false;
		}

		app.g.reset_bpm_form_attribute_check(_swdoc);
		app.g.process_bpm_form_attribute_check(_swdoc,bpm_workflow.pk_workflow_id);
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}

    if (IsObjectDefined("mainform") && strTable == "userdb")
    {
      	//_swdoc.setup_customer_details()
    	
    	//-- rjc - 2.4.0 - customer changed and site selected, confirm site part of new customers organisation
		if(_swdoc.OC().site!="[Use Customers Site]"){
			var recSite = app.g.get_record("site", _swdoc.OC().site);
			if(recSite)
			{
				if(recSite.fk_company_id!=_swdoc.OC().fk_company_id)
				{
					_swdoc.OC().site = "[Use Customers Site]";
					app._ete(_swdoc.MF().lb_oc_site1, "[Use Customers Site]");
				}
			}
		}
		if(_swdoc.OC().fk_dept_code!="[Use Customer Department]"){
			var recCC = app.g.get_record("department", _swdoc.OC().fk_dept_code);
			if(recCC)
			{
				if(recCC.fk_company_id!=_swdoc.OC().fk_company_id)
				{
					_swdoc.OC().fk_dept_code = "[Use Customer Department]";
					app._ete(_swdoc.MF().fld_oc_dept, "[Use Customer Department]");
				}
			}
		}
		
		//-- check if customer is in active    	
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
		
		//-- nwj - 2.3.2 - load company record for customer - and show company on resolve message
		//###20120801
		if(_swdoc.oCust().fk_company_id != "" && _swdoc.oCust().fk_company_id != _swdoc.OC().fk_company_id)
		{
			//-- rjc - 2.4 - resolve company record if company doesnt exist
			_swdoc.ResolveRecord('company','pk_company_id',_swdoc.oCust().fk_company_id);
			if(_swdoc.oCompany())
			{
				if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
			}
			else
			{
				//-- there is adata issue here as the customers organisation does not exist
				MessageBox("The organisation that this customer belongs to cannot be found on the system. Please contact your Supportworks administrator.");
			}
		} else if (_swdoc.oCust().fk_company_id != "" && _swdoc.oCust().fk_company_id == _swdoc.OC().fk_company_id){
			//###to update dropdown for company correctly.
			_swdoc.multi_c_set_company();
		}
		//--F0110887 Start
		var intExpiryEpoch = app.g.fix_epoch(_swdoc.oCompany().supportexpiryx);
		app.g.checkOrgSupport(_swdoc.oCust().fk_company_id, intExpiryEpoch, true);
		//--F0110887 End
		
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
		
    }    
    if (IsObjectDefined("mainform") && strTable == "company")
    {
 
    	//###20120801
    	_swdoc.multi_c_set_company(_swdoc.oCompany().pk_company_id);
        
    	app.g.strLogAndResolveCompany = _swdoc.OC().fk_company_id;
 /*###
    	if(_swdoc.oCompany().pk_company_id!=_swdoc.oCust().fk_company_id)
    	{
    		_swdoc.ResetRecord('userdb');
			if(dd.GetGlobalParamAsNumber("Application Settings/Change/OnOrganisationResolveCustomer")==1)
			{
				_swdoc.boolResolvingCust = true;
				mainform.SetTimer(1, 10);
			}
		}
*/		
		if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
		
		if(_swdoc.OC().site!="[Use Customers Site]"){
			var recSite = app.g.get_record("site", _swdoc.OC().site);
			if(recSite)
			{
				if(recSite.fk_company_id!=_swdoc.oCompany().pk_company_id)
				{
					_swdoc.OC().site = "[Use Customers Site]";
					app._ete(_swdoc.MF().lb_oc_site1, "[Use Customers Site]");
				}
			}
		}
		
		if(_swdoc.OC().costcenter!=""){
			var recCC = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(recCC)
			{
				if(recCC.fk_company_id!=_swdoc.oCompany().pk_company_id)
				{
					_swdoc.OC().costcenter = "";
					app._ete(_swdoc.MF().lb_oc_cc1, "");
				}
			}
		}
		if(_swdoc.OC().fk_dept_code!="[Use Customer Department]"){
			var recCC = app.g.get_record("department", _swdoc.OC().fk_dept_code);
			if(recCC)
			{
				if(recCC.fk_company_id!=_swdoc.oCompany().pk_company_id)
				{
					_swdoc.OC().fk_dept_code = "[Use Customer Department]";
					app._ete(_swdoc.MF().fld_oc_dept, "[Use Customer Department]");
				}
			}
		}
		
		//-- Filter Contracts for Company
		app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract);
		
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
		
  		_swdoc.check_cis();
 		_swdoc.UpdateFormFromData();
	}

}

						OnResolveRecord(strTable, strColumn, strValue)
{
//###MessageBox("Resolve : " + strTable + "." + strColumn + " = " + strValue);
 	//-- resolving the customer record
 	if(IsObjectDefined("mainform") && strTable == 'userdb' && _swdoc.boolResolvingCust == false && strValue!="")
 	{
 		var strWhere = "";
		if ("companyname" == strColumn && "" != strValue){
			_swdoc.ResolveRecord("company","companyname",strValue);
 			_swdoc.strPreSelectedCompany = strValue;
		}
  		if(strColumn =="firstname!surname")
  		{
  			//-- if using customer name search on firstname and lastname
  			//-- 2.4.1 bug fix 79046
   			arrNames = strValue.split(" ");
   			if(arrNames[1]==undefined)
   			{
      			var strParams = "_swc_firstname="+arrNames[0];
      			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   				var strSearch = "firstname="+arrNames[0];
    		}
   			else
   			{
    			var strLastName="";
    			for(var y=1;y<arrNames.length;y++)
    			{
     				if(y>1)strLastName = strLastName+" ";
     				strLastName = strLastName+arrNames[y];
    			}
       			var strParams =  "_swc_firstname="+arrNames[0]+"&_swc_surname="+strLastName;
      			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   				var strSearch = "firstname="+arrNames[0]+"&surname="+strLastName;
   			}
  		}
  		else
  		{
       		var strParams =  "_swc_"+strColumn+"="+strValue;
   			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
  			var strSearch = strColumn+"="+strValue;
   		}
		if(intCount!=1)
		{
			_swdoc.strSearchFor = strSearch;
  			mainform.SetTimer(1, 100);
  			_swdoc.boolResolvingCust = true;
  			return "";
 		}
 	}
 	 if(strTable == "company")
 	{
 		//-- Grab Company Record
 		var oCompanyTemp = app.g.get_record("company",strValue,'companyname');
 		//--F0110887 Start
		var intExpiryEpoch = app.g.fix_epoch(oCompanyTemp.supportexpiryx);
		app.g.checkOrgSupport(_swdoc.oCust().companyname, intExpiryEpoch, true);
		//--F0110887 End
 	}
	//-- resolving org record 	
 	if(IsObjectDefined("mainform") && strTable == 'company' && strValue =="")
 	{
		app.g.strLogAndResolveCompany = "";
  		//2.4 clear down list if user is being unselected
  		_swdoc.ResetRecord('userdb');
 	} 

	//-- resolving org record 	
 	if(IsObjectDefined("mainform") && strTable == 'bpm_workflow' && strValue =="")
 	{
		app._een(mainform.lbl_oc_slad1,true);
		app._een(mainform.label_itsm_sladef,true);
 		app.g.reset_bpm_form_attribute_check(_swdoc);
 	} 
 
 return strValue;
}

						OnLogAndAssignCall(strGroupID, strAnalystID)
{
	// TODO: Add your event handler code here
	_swdoc.OC().suppgroup = strGroupID;
	var strDefaultGroup = dd.GetGlobalParamAsString("Application Settings/Change/DefaultAssignmentGroup");
	if(strDefaultGroup!="")
	{
		if(strDefaultGroup!=_swdoc.OC().suppgroup)
		{
			var strDefaultAnalyst = dd.GetGlobalParamAsString("Application Settings/Change/DefaultAssignmentAnalyst");
			var strDisplayAnalyst = ":"+strDefaultAnalyst;
			if(strDefaultAnalyst=="")
				strDisplayAnalyst = "";
			var boolConfirm = confirm('The default assignment for change records is '+strDefaultGroup+strDisplayAnalyst+'. Do you want to use the default?');
			if(boolConfirm)
			{
				_swdoc.OC().suppgroup = strDefaultGroup;
				_swdoc.OC().owner = strDefaultAnalyst;
			}
		}
	}
	
	_swdoc.OC().custom_i3=0;
	
	return true;
}

						OnLogCall()
{
	// TODO: Add your event handler code here
	var strDefaultGroup = dd.GetGlobalParamAsString("Application Settings/Change/DefaultAssignmentGroup");
	var strDefaultAnalyst = dd.GetGlobalParamAsString("Application Settings/Change/DefaultAssignmentAnalyst");
	if(strDefaultGroup!="")
	{
		_swdoc.OC().suppgroup = strDefaultGroup;
		_swdoc.OC().owner = strDefaultAnalyst;
	}
	
	_swdoc.OC().custom_i3=0;
	
	
	return true;
}

						OnPreValidate()
{
	var funcFinalValidation = function()
	{
		if (!app.g.resolve_default_priority(_swdoc.OC().priority,_swdoc,"Change Proposal"))
		{
			return VALIDATE_FAIL;
		}
		return VALIDATE_DEFAULT;
	}
	
	//--F0110887 Start
	var intExpiryEpoch = app.g.fix_epoch(_swdoc.oCompany().supportexpiryx);
	var boolOrgSupported = new Boolean(app.g.checkOrgSupport(_swdoc.oCust().companyname, intExpiryEpoch, false));
	if (boolOrgSupported==false)
	{
		 MessageBox("You cannot log a call for this customer as the '" + _swdoc.oCust().companyname + "' organisation is no longer supported.");
		 return VALIDATE_FAIL;
	}
	//--F0110887 End
	
	var boolResolve = false;
	if(	_swdoc.OC().itsm_slaname=="[Use Customer SLA]"||_swdoc.OC().itsm_slaname=="[Use Profile SLA]" || _swdoc.OC().itsm_slaname=="[Use Workflow SLA]" || _swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
		boolResolve = true;
		
	if(boolResolve)
	{
		var boolOK = _swdoc.handle_set_sladef(_swdoc.OC().itsm_slaname,true);
		if(!boolOK)
			return VALIDATE_FAIL;
	}
	
	// DTH: F0097667 Impact Level Assessment. Enforce selection of Options for ILA Criteria if ILA is Enabled AND Mandatory
	if (_swdoc.boolILAEnabled && _swdoc.boolILAMandatory)
	{
		if (_swdoc.OC().flg_ila_complete != 1)
		{
			MessageBox("Impact Level Assessment must be complete before the request can be logged");
			return VALIDATE_FAIL;
		}
	}
	
	// DTH: F0097668 Lead Time Check. Enforce certain options and field population if the Lead Time linked to the Impact is > 0
	var intRequestedDate = app.g.fix_epoch(_swdoc.OC().requested_start_datex);
	if ((_swdoc.arrImpactLeadTimes[_swdoc.OC().itsm_impact_level] > 0) && (intRequestedDate > 0))
	{
		var intNow = new Date();
		intNow = Math.round(intNow.getTime() / 1000);
		var intLeadTimeExpiry = app.g.get_sla_end_date(intNow, _swdoc.OC().priority, Number(_swdoc.arrImpactLeadTimes[_swdoc.OC().itsm_impact_level]) + Number(1));
		if (intLeadTimeExpiry > intRequestedDate)
		{
			if ((_swdoc.RFC_EXT().justification == "") || ((_swdoc.OC().itsm_rfc_timing != "Emergency") && (_swdoc.OC().itsm_rfc_timing != "Urgent")))
			{
				var strMessage = "The Desired Start for this request occurs before the Lead Time associated with the Impact.\n";
				strMessage += "You must either alter the Desired Start or enter a Justification and set the Timing to Urgent or Emergency.";
				MessageBox(strMessage);
				return VALIDATE_FAIL;
			}
		}
	}
	
	// DTH: Lead Time Check. Check that the Desired (Requested) End date is after the Desired Start Date
	var intRequestedEnd = app.g.fix_epoch(_swdoc.OC().requested_end_datex);
	if ((intRequestedDate > 0) && (intRequestedEnd > 0) && (intRequestedEnd < intRequestedDate))
	{
		MessageBox("The Desired End Date must occur after the Desired Start Date");
		return VALIDATE_FAIL;
	}


	//###20120802 check cc, dept, site in org?
	var iCaptureMistakes = 0;
	var intMistakeCounter = 0;
	var tDept; // needs to remain outside as department name is NOT the fk_dept_code but dept_name
	if ("" !== _swdoc.OC().site && "[Use Customer's Site]" !== _swdoc.OC().site){
		tSite = app.g.get_record("site", _swdoc.OC().site);
		if (tSite.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 1;
			intMistakeCounter++;
		}
	}
	if ("" !== _swdoc.OC().costcenter ){
		var tCC = app.g.get_record("costcent", _swdoc.OC().costcenter);
		if (tCC.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 2;
			intMistakeCounter++;
		}
	}
	if ("" !== _swdoc.OC().fk_dept_code && "[Use Customer's Department]" !== _swdoc.OC().fk_dept_code){
		tDept = app.g.get_record("department", _swdoc.OC().fk_dept_code);
		if (tDept.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 4;
			intMistakeCounter++;
		}
	}
	if (iCaptureMistakes > 0)
	{
		var strDetectedMistakes = "";
		var intDealtWithMistakes = 0;
		if ( 1 == (iCaptureMistakes % 2) ) {
			strDetectedMistakes += "site (" + _swdoc.OC().site + ")";
			intDealtWithMistakes++;
		}
		if ( 1 == (Math.floor(iCaptureMistakes / 2) % 2) ) {
			strDetectedMistakes += ((0==intDealtWithMistakes)?"":((2==intMistakeCounter)?" and ":", ")) + "charge centre (" + _swdoc.OC().costcenter + ")";
			intDealtWithMistakes++;
		}
		if ( 1 == (Math.floor(iCaptureMistakes / 4) % 2) ) {
			if(!tDept.dept_name)
			{
				//-- F0108424
				_swdoc.oCust = app.g.get_record("userdb", _swdoc.OC().cust_id);
				strDetectedMistakes += ((0==intDealtWithMistakes)?"":" and ") + "department ("+_swdoc.oCust.department+") does not exist and";
			}else
			{
				strDetectedMistakes += ((0==intDealtWithMistakes)?"":" and ") + "department (" + tDept.dept_name + ")";
			}
			//-- F0108424

		}
		MessageBox("The " + strDetectedMistakes + " " + ((1==intMistakeCounter)?"is":"are") + " NOT part of " + _swdoc.OC().companyname + ".\n\nSelect 'OK' to proceed with the current data.", MB_OKCANCEL | MB_ICONEXCLAMATION , function(intResponse)
		{
			if (2==intResponse)
			{
				return VALIDATE_FAIL;
			}
			else
			{
				return funcFinalValidation();
			}
		});
		
	}
	else
	{
		return funcFinalValidation();
	}	
}

