						OnFormLoaded()
{
	//-- Workaround for Analyst Portal to load global js on load functions if not loaded
	if(!app.gbl_initialised)
		app.global_init_configuration();

	//-- F0105032
	if(_swdoc._form.PreloadType==2)
	{
		_swdoc.oOpencall = app.g.get_sys_record("opencall", _swdoc.OC().callref);
		_swdoc.oExtended = app.g.get_record("itsm_opencall_rfc", _swdoc.OC().callref);
	}
	//-- EOF F0105032

	//-- F0105553
	if(_swdoc.oCust().keysearch)
	{
		if(_swdoc.oCust().fk_company_id!=_swdoc.oCompany().pk_company_id)	
		{
			_swdoc.ResolveRecord("company", "pk_company_id", _swdoc.oCust().fk_company_id);
		}
	}
	//-- EOF F0105553
	// -- Update Main Organisation on the form if only the Organisation is set
	if(_swdoc.oCompany().pk_company_id && _swdoc.oCust().keysearch =="")
	{
		_swdoc.ResolveRecord("userdb", "fk_company_id", _swdoc.oCompany().pk_company_id);
	}
		
	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	var boolExternalCompanies =dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1;
	app._een(_swdoc.MF().lb_company, boolExternalCompanies);
	app._evi(_swdoc.MF().lb_contract, boolExternalCompanies);
	app._evi(_swdoc.MF().lbl_contract, boolExternalCompanies);
	if(!_swdoc.MF().lb_company.enable)
	{
		//-- check if we should hide
		var boolHideCompanyField = (dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1);
		app._evi(_swdoc.MF().lb_company, boolHideCompanyField);		
		//alter filter if we do not support external
		
		app._slf(_swdoc.MF().fld_userdb_site,"");
		app._slf(_swdoc.MF().fld_userdb_costcenter,"");
		app._slf(_swdoc.MF().fld_userdb_dept,"");	
		app._slf(_swdoc.MF().lb_oc_cc1,"");
		app._slf(_swdoc.MF().lb_oc_site1,"");
		app._slf(_swdoc.MF().fld_oc_dept,"");
	}
	//###20120801 ensure correct field for organisation is showing - workaround because distinct picklist with Display is broken
	var boolCompanyFieldVisible = (_swdoc.MF().lb_company.visible);
	app._evi(_swdoc.MF().lbl_oc_company, boolCompanyFieldVisible);

	var boolCompanySet = ("" !== _swdoc.OC().fk_company_id);
	
	app._evi(_swdoc.MF().pl_oc_org, boolCompanyFieldVisible);
	app._een(_swdoc.MF().pl_oc_org, boolExternalCompanies);
	
	app._evi(_swdoc.MF().lbl_company, boolCompanyFieldVisible);	
	//-- eof hide / enable	    

	//--
	//-- if logging ofr a customer
	if(app.g.LOG_CALL_FOR_USER!="")
	{
		_swdoc.ResolveRecord("userdb", "keysearch", app.g.LOG_CALL_FOR_USER);
		app.g.LOG_CALL_FOR_USER="";
	}
	else if(app.g.LOG_FOR_ORGID!="")
	{
		_swdoc.ResolveRecord("company", "pk_company_id", app.g.LOG_FOR_ORGID);	
		app.g.LOG_FOR_ORGID="";
	}
	
	
	//-- NWJ - 28.02.2006
	//--      get any incoming cis (logged from ss)
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems1, _swdoc.OC().callref ,"INCOMING","")	

	//F0108068
	if(app.g.LOG_CALL_FOR_CI!="")
	{
		var strCIKey = ""+app.g.LOG_CALL_FOR_CI;
		_swdoc.link_cause_cis_to_change(strCIKey);
		//app.g.sl_appendfilter(_swdoc.EF().sl_configitems1, app.g.LOG_CALL_FOR_CI,'pk_auto_id');
 		app.g.LOG_CALL_FOR_CI="";
	}

	var oAnalyst = app.g.get_analyst_detail(app.session.analystId);
	_swdoc.set_rfc_analyst(oAnalyst,false);	
	

	//--
	//-- check if we are raising this problem from an incident
	if(app.g.LOG_FROM_CALL!=null)
	{	
		//-- store incident record info (uses global params to determine what to store)
		if(app.g.LOG_FROM_CALL.callclass=="Incident")		
		{
			_swdoc.filter_inc_impact(app.g.LOG_FROM_CALL.callref);			
		}
		
		//-- store prob/ke record (uses global params to determine what to store)
		if(app.g.LOG_FROM_CALL.callclass=="Known Error")
		{
			_swdoc.filter_ke_impact(app.g.LOG_FROM_CALL.callref);
		}
		
		//-- show customer impacted by the log from call
		if(app.g.LOG_FROM_CALL.cust_id!="")
		{
			_swdoc.filter_impact_customers(_swdoc.EF());
			_swdoc.ResolveRecord("userdb","keysearch", app.g.LOG_FROM_CALL.cust_id);
		}
		//-- copy over prob text
		updatedb.updatetxt = app.g.CALLINFO[app.g.LOG_FROM_CALL.callref].origupdatetxt
				
		//-- copy over prob code into recog prob code
		_swdoc.OC().probcode = app.g.LOG_FROM_CALL.probcode;
		//-- copy of cost center
		_swdoc.OC().costcenter = app.g.LOG_FROM_CALL.costcenter;
		
		_swdoc.OC().itsm_title = app.g.LOG_FROM_CALL.itsm_title;
		if(_swdoc.OC().itsm_title!="")
			_swdoc.boolRFCTitleSetManually = true;
		
		//-- impact / urgency / priority level 
		_swdoc.OC().itsm_impact_level 	= app.g.LOG_FROM_CALL.itsm_impact_level;
		_swdoc.OC().itsm_urgency_level	= app.g.LOG_FROM_CALL.itsm_urgency_level;
		_swdoc.OC().itsm_sladef			= app.g.LOG_FROM_CALL.itsm_sladef;
		_swdoc.OC().itsm_slaname			= app.g.LOG_FROM_CALL.itsm_slaname;
		_swdoc.OC().priority			= app.g.LOG_FROM_CALL.priority;
		if(_swdoc.OC().itsm_sladef>0)
		{
			app._een(_swdoc.MF().lbl_oc_priority,true);
			app._een(_swdoc.MF().lb_oc_sla, true);
		}
		
		
		//-- update form
		_swdoc.UpdateFormFromData();
		
		//-- important - null record		
		app.g.LOG_FROM_CALL=null;
	}	
	
	//-- RJC - F0105434
	app.g.get_default_process(_swdoc,'Change Proposal',_swdoc.MF().fld_bpm_workflow,_swdoc.MF().lbl_bpm_workflow);
	//-- EOF - F0105434
		
	//-- F0094989
	if(_swdoc.OC().itsm_slaname.indexOf("[Use ")<0 && _swdoc.OC().itsm_slaname.indexOf(" SLA]")<0 )
	{
		if(_swdoc.OC().itsm_slaname!="")
		{
			var slarec = app.g.get_sqrecordset("general/get_itsm_sla","kv="+_swdoc.OC().itsm_slaname);
			if(slarec.Fetch())
			{
				var intSLAId = app.g.get_field(slarec,"slad_id");
				if(intSLAId!="") {
					_swdoc.handle_set_contract(intSLAId);
					_swdoc.handle_set_sladef(intSLAId);					
				}
			}
		}
	}
	
	_swdoc.strDefaultImpact = (_swdoc.OC().itsm_impact_level);
	_swdoc.strDefaultUrgency = (_swdoc.OC().itsm_urgency_level);
	_swdoc.strCurrentPriority = _swdoc.OC().priority;
	_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;

	// DTH: Populate _swdoc variables relating to Impact Level Assessment (ILA) settings
	_swdoc.boolILAEnabled = app.g.ila_changelogcall_enabled(); // Impact Level Assessment Enabled
	_swdoc.boolILAMandatory = app.g.ila_changelogcall_mandatory(); // Impact Level Assessment Mandatory
	_swdoc.boolILAAllowAlteration = app.g.ila_changelogcall_allow_alteration(); // Impact Level Assessment Allow Alteration

	app._een(_swdoc.MF().btn_ila_assess, _swdoc.boolILAEnabled); // enable the Assessment button based on setting
	if (_swdoc.boolILAEnabled && _swdoc.boolILAMandatory)
	{
		app._een(_swdoc.MF().Field9, false); // disable selection of Impact Level until Assessment is completed
	}	
	
	//-- F0105629 RJC
	if(app.oSettings.get_setting("boolChangeHideFromFSCDefault"))
		_swdoc.OC().itsm_hide_fsc = 1;
	//-- EOF F0105629
		
	var strTable = "config_reli";

	// DTH: F0097668 Lead Time Check - populat an array of Lead Time for each Impact
	_swdoc.arrImpactLeadTimes = app.g.get_impact_lead_times();
	
	//ES -- START F0107205 : When logging call from email...
	if(_swdoc.OC().itsm_slaname == "[Use Customer SLA]" && _swdoc.oCust().sld_name != "")
	{
		_swdoc.MF().lb_oc_slad1_OnValueChanged("[Use Customer SLA]");
		
		//-- If priority not set...
		if (_swdoc.OC().priority == "")
		{
			//-- ...try to find priority from matrix
			_swdoc.OC().priority = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
			_swdoc.UpdateFormFromData();
		}
	}
	//-- END F0107205
	
	//-- F0108825 : A newly logged call should not have resolve/close text
	//	         even when it has been copied from a resloved/closed call
	_swdoc.OC().resolve_text="";
	_swdoc.OC().close_text="";
	//	PM00143977 : values copied from parent call
	_swdoc.OC().lastactdatex="";	
	_swdoc.OC().lastactdate="";	
	_swdoc.OC().lastactby="";	
	_swdoc.OC().assigndatx="";	
	_swdoc.OC().withinresp="";	
	_swdoc.OC().resp_time="";	
	_swdoc.OC().slaresp="";	
	_swdoc.OC().assigndate="";	
	//-- TK Set Summary Based on Email Subject
	if((_swdoc._form['emailsubject']) && (app.oSettings.get_setting("boolUseSubjectasSummary")=='True'))
	{
		_swdoc.OC().itsm_title = _swdoc._form['emailsubject'];
		_swdoc.boolRFCTitleSetManually = true;
	}
	_swdoc.strDefaultPriority = _swdoc.MF().lb_oc_sla.value;
}

