						btn_ila_assess_OnPressed()
{
	// TODO: Add your event handler code here
	
	var strURL = "ila_options="+_swdoc.strILAOptions+"&appcode="+app.itsm.get_session_param("dataset")+"&usage_area=Change Log Call Form&mode=log form";
	app.OpenForm("EfImpactAssessmentEntry", strURL, true, function(oRes)
	{
		if (oRes.document.boolApplied)
		{
			_swdoc.strILAOptions = oRes.document.strSelectedKeys;
			_swdoc.OC().flg_ila_complete = 1;		
			_swdoc.OC().ila_weighting = oRes.document.intWeighting;
			_swdoc.OC().itsm_impact_level = oRes.document.strImpactLevel;
			if (_swdoc.boolILAEnabled && _swdoc.boolILAMandatory && _swdoc.boolILAAllowAlteration)
				app._een(_swdoc.MF().Field9, true); // Enable alteration of Impact Level
			_swdoc.process_impact_urgency_change(); // Apply default functionality relating to a change in the Impact Level
			_swdoc.UpdateFormFromData();
		}
	});
}

						mb_email_aba1_OnMenuItem(strName, nItemID)
{
	// TODO: Add your event handler code here
	var nEmailSelected = 1;
	var nEmailAll = 2;
	
	var boolEmailAll = (nItemID == nEmailAll)?true:false;
	

	// Load the configured mailbox and email template
	// if these don't exist, inform the Analyst and exit ???
	var strMailbox = app.g.get_ChangeABAMailbox();
	var strEmailTemplate = app.g.get_ChangeABAMailTemplate();
	if ((strMailbox == "") || (strEmailTemplate == ""))
	{
		MessageBox("The settings which are required to send an email have not been configured.\nPlease contact your Supportworks Administrator.");
		return false;
	}
	
	// Launch a form to capture the extra text to be placed in the body of the email
	app.OpenFormForAdd("popup_aba_adhoc_email", "", "", true, function(aForm){
		var strAdditionalEmailBody = aForm.document.strEmailBody;
		if (aForm.document.boolCancelled){
			return false; // The Analyst cancelled the previous form
		}
	
		var strABA_Details = ""; // this will contain the IDs and Email address to which the email is sent
		// send the emails
		for (x=0; x<sl_ABAs1.rowCount(); x++){
			if ((sl_ABAs1.IsRowSelected(x)) || (boolEmailAll)){
				// Call VPME script to send the email, pass in mailbox, email template, email address, additional body of email			
				var xmlmc = new XmlMethodCall;
			
				// Set up our input parameters
				xmlmc.SetValue("callref", _swdoc.OC().callref);
				xmlmc.SetValue("mailbox", strMailbox);
				xmlmc.SetValue("emailtemplate", strEmailTemplate);
				xmlmc.SetValue("aba_email", sl_ABAs1.GetItemTextRaw(x, 3));
				xmlmc.SetValue("aba_id", sl_ABAs1.GetItemTextRaw(x, 0));
				xmlmc.SetValue("add_content", strAdditionalEmailBody);
			
				// Invoke the method
				if(!xmlmc.Invoke("VPME", "emailAffectedBusArea")){
					MessageBox(xmlmc.GetLastError());
				}
				if (strABA_Details != "") strABA_Details += "\n";
					strABA_Details += sl_ABAs1.GetItemTextRaw(x, 0) + " ("+sl_ABAs1.GetItemTextRaw(x, 3)+")";
			}
		}
	
		// create a diary entry 
		var xmlmc = new XmlMethodCall;

		// Set up our input parameters
		xmlmc.SetValue("callref", _swdoc.OC().callref);
		xmlmc.SetValue("aba_details", strABA_Details);
		xmlmc.SetValue("add_content", strAdditionalEmailBody);
	
		// Invoke the method
		if(!xmlmc.Invoke("VPME", "abaCreateDiary")){
			MessageBox(xmlmc.GetLastError());
		}
	});
}

						btn_remove_aba1_OnPressed()
{
	// TODO: Add your event handler code here
	// check for APP Right if source is Analyst
	var boolProceed = false;		
	boolProceed = app.session.HaveAppRight(app.PG_RFC, app.RFC_BUS_AREA_REMOVE, app.session.dataDictionary);
	if (!boolProceed)
	{
		MessageBox("You are not authorised to remove Affected Business Areas from Change Requests.\nIf you require authorisation please contact your Supportworks Administrator.");
		return false;
	}
// there is no call ref so the following will not work
	//app.g.remove_affected_bus_area_from_call(_swdoc.OC().callref, sl_ABAs1, "", "Analyst");
	
	
	//-- do this by finding unselected rows and making sure they are part of fitler
	var strFilter = "";
	for (var x=sl_ABAs1.rowCount()-1;x > -1;x--)
	{
		if (!sl_ABAs1.IsRowSelected(x))
		{
			//-- add to filter
			var value = sl_ABAs1.GetItemTextRaw(x,"pk_area_id");
			if (value != "")
			{
    			if (strFilter != "") strFilter +=",";
    			strFilter += value;
			}
		}
	}
	if(strFilter=="")
		strFilter="-1";
	strFilter = "aid=2&_kv="+strFilter;

	app._slf(_swdoc.MF().sl_ABAs1, strFilter);  
	_swdoc.MF().sl_ABAs1.Refresh();
	_swdoc.MF().sl_ABAs1.SetRowSelected(0);


	// Refresh the list
//	app.g.sl_load_call_ABAs(_swdoc.MF().sl_ABAs1, _swdoc.OC().callref);
}

						btn_add_aba1_OnPressed()
{
	// Associate an Affected Business Area with the call
	
	// check for APP Right if source is Analyst
	var boolProceed = false;		
	boolProceed = app.session.HaveAppRight(app.PG_RFC, app.RFC_BUS_AREA_ADD, app.session.dataDictionary);
	if (!boolProceed)
	{
		MessageBox("You are not authorised to associate Affected Business Areas with Change Requests.\nIf you require authorisation please contact your Supportworks Administrator.");
		return false;
	}
		
	// Search for an ABA, then link it to the call
	var oRes = app.g.search_for("bus_area", true,"");
	var strKeys = oRes.selectedkeys;

	if(strKeys=="") return;
	var arrKeys = strKeys.split(",");
	for (var x=0; x< arrKeys.length;x++)
	{
		var fk_key = arrKeys[x];
		if(app.g.sl_findrow_byvalue(sl_ABAs1,fk_key,"pk_area_id")==-1)
		{	
			var strKeys = app.g.sqllist_get_valuesdel(_swdoc.MF().sl_ABAs1, "pk_area_id", ",", "");	
			if(strKeys!="")strKeys+=",";
			var strParams = "aid=1&_kv="+strKeys+fk_key;
			app._slf(_swdoc.MF().sl_ABAs1,strParams);
			if(_swdoc.arrABAAssocTypes[fk_key]==undefined)
				_swdoc.arrABAAssocTypes[fk_key] = "Analyst";
			//app.g.add_affected_bus_area_to_call(_swdoc.OC().callref, "Analyst", "", fk_key, false);
		}
	}
	
	_swdoc.MF().sl_ABAs1.Refresh();
	// Refresh the list
	//app.g.sl_load_call_ABAs(_swdoc.MF().sl_ABAs1, _swdoc.OC().callref);

}

