						mbtn_ke_views1_OnMenuItem(strName, nItemID)
{
	var iDetails = 1;
	switch (nItemID)
	{
		case iDetails:
			sl_kes1.EditRecord(sl_kes1.curSel);
			break;
	}	
}

						mbtn_ke_actions1_OnMenuItem(strName, nItemID)
{
	var iClear = 1;
	var iLinkCauseCI = 2;
	var iLinkImpactCI = 3;
	
	switch (nItemID)
	{
		case iClear:
			//-- get select incidents
			var strSelectedKes = app.g.sl_getselected_valuesdel(sl_kes1,0,",","");
			_swdoc.remove_selected_ke_incs(strSelectedKes);			
			_swdoc.remove_selected_ke_cis(strSelectedKes);
			app.g.sl_clearselected(sl_kes1,sl_kes1_OnItemSelected,0);		
			break;
		case iLinkCauseCI:
			var strSelectedKes = app.g.sl_getselected_valuesdel(sl_kes1,0,",","");
			var strLinkedCIs = app.cmdb.get_call_cis(strSelectedKes , "PROBLEM-CAUSE", "");
			_swdoc.link_cause_cis_to_change(strLinkedCIs);	
			break;
		case iLinkImpactCI:
			var strSelectedKes = app.g.sl_getselected_valuesdel(sl_kes1,0,",","");	
			var strLinkedCIs = app.cmdb.get_call_cis(strSelectedKes , "PROBLEM-AFFECT", "");
			_swdoc.link_affect_cis_to_change(strLinkedCIs);
			break;
	}
	_swdoc.EF().sl_kes1.Refresh();	
}

						btn_srch_kes1_OnPressed()
{
	var strURL = "canreset=1";  
	app.itsm.search_calls("KE",strURL,true,function(oResults)
	{
		if(oResults.selectedkeys=="") return;
		
		_swdoc.filter_ke_impact(oResults.selectedkeys);
		_swdoc.EF().sl_kes1.Refresh();	
   });
  
}

