function filter_company_sites()
{
	app._slf(_swdoc.MF().sl_sites, "_swc_fk_company_id=" + _swdoc.oCompany().pk_company_id);
	_swdoc.MF().sl_sites.Refresh();
}

//-- given a ci or list of cis link it to problem cause list
function link_cause_cis_to_problem(strLinkCIs)
{
	var strFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems,'pk_auto_id',",");
	if (strFilter != "") 
	{
		strFilter += "," + strLinkCIs;
	}
	else
	{
		strFilter = strLinkCIs;
	}
	

	app._slf(_swdoc.EF().sl_configitems,"pk_auto_id=" + strFilter + "&type=1");
	_swdoc.EF().sl_configitems.Refresh();
	_swdoc.EF().sl_configitems.SetRowSelected(0,true,false);

	// -- SG, 02/07/2012, F0103324
	// Build array of CI's that have the Bespoke Terms field populated
	// End of array building			
	app.bespokeTermsProcessing(strFilter);
		
	_swdoc.check_ci_assessment();
}

//-- given a ci or list of cis link it to problem cause list
function link_cause_service_to_problem(strLinkCIs)
{
	var strFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_services,'pk_auto_id',",");
	if (strFilter != "") 
	{
		strFilter += "," + strLinkCIs;
	}
	else
	{
		strFilter = strLinkCIs;
	}
	
	
	app._slf(_swdoc.EF().sl_services,"pk_auto_id=" + strFilter + "&type=2");
	_swdoc.EF().sl_services.Refresh();
	_swdoc.EF().sl_services.SetRowSelected(0,true,false);
	
	// -- SG, 02/07/2012, F0103324
	// Build array of CI's that have the Bespoke Terms field populated
	app.bespokeTermsProcessing(strFilter, "Service");	
	
	_swdoc.check_ci_assessment();
}



//-- given a ci or list of cis link it to problem affect list
function link_affect_cis_to_problem(strLinkedCIs)
{
	var strFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_impactcis,'pk_auto_id',",");
	if (strFilter != "") 
	{
		strFilter += "," + strLinkedCIs;
	}
	else
	{
		strFilter = strLinkedCIs;
	}
	
	
	app._slf(_swdoc.EF().sl_impactcis, "_kv=" + strFilter);
	_swdoc.EF().sl_impactcis.Refresh();
	_swdoc.EF().sl_impactcis.SetRowSelected(0,true,false);

	// -- SG, 02/07/2012, F0103324
	// Build array of CI's that have the Bespoke Terms field populated
	app.bespokeTermsProcessing(strFilter);	
	
}

//-- for removed incidents prompt user to remove attached CIs linked to those incidents
function remove_selected_incident_cis(strSelectedIncs)
{
	var array_RemoveImpactCIS = new Array();
	
	if(_swdoc.EF().sl_impactcis.rowCount()<1) return;
	
	//--
	//-- get linked inc cis and see if they are linked to problem
	var strLinkedCIs = app.cmdb.get_call_cis(strSelectedIncs , "INCIDENT", "");
	if(strLinkedCIs=="0")return;

	var arrayCIS = strLinkedCIs.split(",");
	for(var x=0;x<arrayCIS.length;x++)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_impactcis,arrayCIS[x],0)!=-1)	array_RemoveImpactCIS[array_RemoveImpactCIS.length++] = arrayCIS[x];			
	}
		
	//-- if we have linked impact inc cis then ask if want to remove them
	if(array_RemoveImpactCIS.length>0)
	{
		if(confirm("There are associated impact configuration items, that are linked to the incidents you are removing. Do you want to remove these items as well?"))
		{
			for(var y=0;y<array_RemoveImpactCIS.length;y++)
			{
				app.g.sl_delrow_byvalue(_swdoc.EF().sl_impactcis,array_RemoveImpactCIS[y],0, null,0);
			}
		}
	}
}


function filter_impact_customers(oForm)
{
	var strFilter = "";
	//-- get list of customers impacted by these incidents and refresh cust impact list
	var strCustFilter = app.g.sl_get_valuesdel(oForm.sl_incs,'cust_id',",","",true);
	if(strCustFilter!="")strFilter+=strCustFilter;
	
	//-- get customers that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_impactcis,'pk_auto_id',",");	
	
	var strCustIDSFromCIs = app.cmdb.get_ci_customer_ids(strImpactedCIs,true);
	if(strCustIDSFromCIs!="")
	{
		strFilter+=","+strCustIDSFromCIs;
	}
	if(_swdoc.oCust().keysearch!="")
	{
		strFilter+=","+_swdoc.oCust().keysearch;
	}
	app._slf(oForm.sl_customerimpact, "_kv="+strFilter);
	oForm.sl_customerimpact.Refresh();			
}

function filter_impact_orgs(oForm)
{
	var strFilter = "";
	//-- get list of orgs impacted by impacted customer
	var strOrgFilter = app.g.sl_get_valuesdel(oForm.sl_incs,'fk_company_id',",","",true);
	if(strOrgFilter!="")strFilter+=strOrgFilter="''";
	
	//-- get orgs that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_impactcis,'pk_auto_id',",");	
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strImpactedCIs,true);
	if(strOrgIDSFromCIs=="")
	{
		strFilter+=","+strOrgIDSFromCIs;
	}
	if(_swdoc.oCompany().pk_company_id!="")
	{
		strFilter+=","+_swdoc.oCompany().pk_company_id;
	}
	app._slf(oForm.sl_orgimpact,"_kv="+strFilter);
	oForm.sl_orgimpact.Refresh();	
}

function get_company_cis()
{
	var strKeyValues = app.cfg.get_company_cikeys(strCompanyID,true,true);	

	var strParams = "fk_company_id="+app.pfu(strCompanyID);
    var oRS = app.g.get_sqrecordset("general/get_company_sites",strParams);
	while(oRS.Fetch())
	{
		if(strKeyValues!="")strKeyValues+=",";	
		var strSiteName = app.g.get_field(oRS,"site_name");
		strKeyValues+= app.cmdb.get_site_cis(strSiteName);
	}	
	
	if(strKeyValues=="")
		strKeyValues = "-1";
		
	_swdoc.companyCIIDS = strKeyValues;
}

function check_cis()
{
	//-- F0100436 - update _swdoc.check_cis function
	if(_swdoc.EF().sl_configitems.rowCount() =="")
		return;
	var intStartCount = _swdoc.EF().sl_configitems.rowCount();
	var strCIKeys = app.g.sqllist_get_valuesdel(_swdoc.EF().sl_configitems, 'pk_auto_id', ",", "");

	var strAssociatedCIKeys = "-1";
	var strParams = "fk_children="+(strCIKeys)+"&fk_parent="+app.pfu(_swdoc.oCompany().fk_cmdb_id);
    var oRS = app.g.get_sqrecordset("call/incident/cisrelatedtocompany",strParams);
	while(oRS.Fetch())
	{
		strAssociatedCIKeys+=",";	
		strAssociatedCIKeys += app.g.get_field(oRS,"FK_CHILD_ID");
	}	
	
	var strParams = "pk_cis="+(strCIKeys)+"&fk_company_id="+app.pfu(_swdoc.oCompany().pk_company_id);
    var oRS = app.g.get_sqrecordset("call/incident/cisownedbycompany",strParams);
	while(oRS.Fetch())
	{
		strAssociatedCIKeys+=",";	
		strAssociatedCIKeys += app.g.get_field(oRS,"PK_AUTO_ID");
	}	

	var strParams = "fk_company_id="+app.pfu(_swdoc.oCompany().pk_company_id);
    var oSites = app.g.get_sqrecordset("general/get_company_sites",strParams);
	while(oSites.Fetch())
	{
		var strSiteName = app.g.get_field(oSites,"site_name");

		var strParams = "pk_cis="+(strCIKeys)+"&fk_site="+app.pfu(strSiteName);
	    var oRS = app.g.get_sqrecordset("call/incident/cisownedbysites",strParams);
		while(oRS.Fetch())
		{
			strAssociatedCIKeys+=",";	
			strAssociatedCIKeys += app.g.get_field(oRS,"PK_AUTO_ID");
		}	
	}	

	app._slf(_swdoc.EF().sl_configitems, "pk_auto_id="+strAssociatedCIKeys+"&type=1");
	var intEndCount = _swdoc.EF().sl_configitems.rowCount();
	if(intEndCount<intStartCount)
		MessageBox("CIs were removed from this call, as they were not associated with the currently selected Organisation");
	return;
}



//-- check if there is alternative slad settings based on the customers department
function check_customer_department_slad(strSLADID, strDepartment)
{
 	if(strDepartment=="")
		return;
	var strParams = "fk_slad_id=" +app.pfu(strSLADID)+ "&fk_dept=" +app.pfu(strDepartment);
 	var oRecSLD = app.g.get_sqrecordset("call/incident/get_dept_sla",strParams);
	if(oRecSLD.Fetch())
	{
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD,"chargeto_cc");
		switch(strNewCC)
		{
			case "[Use Department Charge Centre]":
				strNewCC = _swdoc.oCust().department;
				break;
			case "[Use Customer Charge Centre]":
				strNewCC = _swdoc.oCust().costcenter;
				break;		
			default:
				break;
		}
		//-- eof cc option
			
		//-- set oc values
		if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
		_swdoc.OC().priority = app.g.get_field(oRecSLD,"fk_sla");
	}
}


function check_ci_assessment()
{
    //-- if use ci assessment levels
   	var intUseCIrow = -1;
    
    if(app.itsm.get_ci_assessmnet_level_setting("Problem")==1)
    {
    	var strUseSLA = "";
		
		//-- get which SLA we should use based on CI/s			
 		var slaArray = new Array();
 
 		var oList = _swdoc.EF().sl_configitems;
		var strSLAs = "";
		var strVal="";
		var strSLAVal="";
		for (var x=0;x<oList.rowCount();x++)
		{
			strVal = oList.GetItemTextRaw(x,"fk_sla");
			strSLAVal = oList.GetItemTextRaw(x,"fk_sld");
			if(strVal =="[Use SLA Default Priority]")
			{
				var oRecSLD = app.g.get_record("itsmsp_slad", strSLAVal);
				if(oRecSLD)
				{	
					strVal = oRecSLD.fk_ssla;
				}
			}
			strVal = app.g.pfs(strVal);
			if(strVal!="")
			{
				if (strSLAs !="")strSLAs +=",";
				//strSLAs  += "'"+strVal+"'";
				strSLAs  += ""+strVal+"";
				slaArray[strVal]=strSLAVal;
			}
		}

 		var oList = _swdoc.EF().sl_services;
		var strVal="";
		for (var x=0;x<oList.rowCount();x++)
		{
			strVal = oList.GetItemTextRaw(x,"fk_sla");
			strSLAVal = oList.GetItemTextRaw(x,"fk_sld");
			if(strVal =="[Use SLA Default Priority]")
			{
				var oRecSLD = app.g.get_record("itsmsp_slad", strSLAVal);
				if(oRecSLD)
				{	
					strVal = oRecSLD.fk_ssla;
				}
			}
			strVal = app.g.pfs(strVal);
			if(strVal!="")
			{
				if (strSLAs !="")strSLAs +=",";
				//strSLAs  += "'"+strVal+"'";
				strSLAs  += ""+strVal+"";
				slaArray[strVal]=strSLAVal;
			}
		}

		//var strSLAs = app.g.get_sld_slas(strSLAs);
  		if(dd.GetGlobalParamAsNumber("Application Settings/Problem/UseCILongestSLA")==1)    			
		{
			strUseSLA = app.g.get_longest_sla(strSLAs);    			
    	}
    	else
    	{
    		strUseSLA = app.g.get_shortest_sla(strSLAs);
    	}			

		//-- if have SLA to use
		intUseCIRow = 0;
		var boolIsService = false;
		if(strUseSLA!="")
		{
			var intSLD = slaArray[strUseSLA]; 					
			//-- we have a prefered SLA - get the CI that has this SLA and use its assess levels
			intUseCIrow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems,strUseSLA,"fk_sla");	
			if(intUseCIrow==-1)
			{
				//not a ci found with priority
				boolIsService = true;
				intUseCIrow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_services,strUseSLA,"fk_sla");
				if(intUseCIrow==-1)
				{
					//not found with priority try with SLA
					intUseCIrow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems,intSLD,"fk_sld");	
					if(intUseCIrow==-1)
					{
						boolIsService = true;
						intUseCIrow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_services,intSLD,"fk_sld");	
   						var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_impact_level");
						var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_urgency_level"); 
						var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIRow,"pk_auto_id");
					}else
					{
    					var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_impact_level");
						var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_urgency_level");    
						var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIRow,"pk_auto_id");
					}
					
				}else{
					//is a app.service found with sla
   					var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_impact_level");
					var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_urgency_level"); 
					var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIRow,"pk_auto_id");
				}
				
			}else{
    			var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_impact_level");
				var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_urgency_level");    
				var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIRow,"pk_auto_id");
			}
		}
		else
		{
			if(_swdoc.EF().sl_configitems.rowCount()==0)
			{
				boolIsService = true;
				//-- no slas defined for CI/s so try base it on assessment levels of first item
    			var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"fk_impact_level");
				var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"fk_urgency_level"); 
				var intSLD =app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"fk_sld"); 
				var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,0,"pk_auto_id");
			}else
			{
				//-- no slas defined for CI/s so try base it on assessment levels of first item
    			var strImpact = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,0,"fk_impact_level");
				var strUrgency =app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,0,"fk_urgency_level"); 
				var intSLD =app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,0,"fk_sld"); 
				var ciKey = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,0,"pk_auto_id");
			}				
			if(intSLD>0)
				strUseSLA = app.itsm.get_sla_from_slad_matrix(intSLD, strImpact, strUrgency);
			
		}
		_swdoc.intAssessmentCI = ciKey;
		
		if(strUrgency=="")strUrgency=_swdoc.strDefaultUrgency;
 		if(strImpact=="")strImpact=_swdoc.strDefaultImpact;
 		
 		
		if(intSLD=="")
		{

		}else
		{
			if(intSLD>0)
			{
				var oRec = app.g.get_record("itsmsp_slad", intSLD);	
				if(oRec)
				{
					//-- F0108327
					_swdoc.handle_set_sladef(oRec.slad_id);
					_swdoc.handle_set_contract(intSLD);	//-- Set Contract Based on Selected SLA
					var boolTemp = _swdoc.boolProblemSLASetManually;
					_swdoc.boolProblemSLASetManually = true;
					_swdoc.handle_set_priority(strUseSLA);
					_swdoc.boolProblemSLASetManually = boolTemp;
					//--END
				}
			}
		}
		 
		if(_swdoc.OC().itsm_sladef>0)
		{
			app._een(_swdoc.MF().label_oc_priority, true);
			app._een(_swdoc.MF().lb_oc_sla, true);
		}		

		_swdoc.OC().itsm_urgency_level = strUrgency;				
		_swdoc.OC().itsm_impact_level = strImpact;				
		
		//-- get and set profile code if it has'nt been set manually by the user
		if(boolIsService)
		{
			var intCIid = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"pk_auto_id");
			var strCode = app.g.sl_getrawcolvalue(_swdoc.EF().sl_services,intUseCIrow,"fk_profilecode");		
		}else
		{
			var intCIid = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"pk_auto_id");
			var strCode = app.g.sl_getrawcolvalue(_swdoc.EF().sl_configitems,intUseCIrow,"fk_profilecode");
		}
		
		if(_swdoc.intProfileCodeCI==-1)
		{
			//-- was manually set so ask if want to change it - but only if one to use is not blank
			
			if((strCode!="")&&(strCode!=_swdoc.OC().probcode)&&(confirm("The recommended profile code to use for this problem differs from that of the one you selected. Do you want to change it?")) )
			{
				_swdoc.OC().probcode = strCode;
				_swdoc.intProfileCodeCI = intCIid;
			}
		}
		else 
		{
			//MessageBox("strCode : "+strCode);
			if(strCode!=_swdoc.OC().probcode)
			{
				//-- only set if not blank
				if(strCode!="")
				{
					_swdoc.OC().probcode = strCode;
					mainform.SetTimer(2, 100)
					//MF().tb_probcode.text="";
				}
			}
			//MessageBox("intProfileCodeCI : "+intCIid);
			_swdoc.UpdateFormFromData();		
 			_swdoc.intProfileCodeCI = intCIid;			
		}
   }
	_swdoc.UpdateFormFromData();		
     
    return ciKey;

}

//-- load sld info and settings
//-- load sld info and settings
function handle_set_sladef(strNewSLD, boolValidate, boolOnResolve)
{
	if(_swdoc.boolSLAEnforced)
	{
		_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
		_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
		_swdoc.UpdateFormFromData();
		MessageBox("SLA is being enforced from the process.");
		return;
	}

	if(boolValidate==undefined)
		boolValidate==false;
	var strDisplaySLD = strNewSLD;

	var strUseSLD = strNewSLD;
	var boolRelative = false;
	var strRelative = "";

	//-- get actual SLD ID if getting from related record
	switch(strNewSLD)
	{
		case "[Use Profile SLA]":
			if(_swdoc.OC().probcode!="")
			{
				var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
				var oRecSLD = app.g.get_record("itsmsp_slad", oRec.itsm_sla);
				if(oRecSLD)
				{
					strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
					boolRelative = true;
					strRelative = "[Use Profile Priority]";
					//return false;
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Profile SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No profile has been set for this request. The Profile SLA cannot be used.",16);
				return false;
			}
			break;
		case "[Use Charge Centre SLA]":
			if(_swdoc.OC().costcenter!="")
			{
				var strCCUse = _swdoc.OC().costcenter;
				if(_swdoc.OC().costcenter=="[Use Customer Charge Centre]")
				{
					strCCUse = _swdoc.oCust().costcenter;
				}
				if(strCCUse=="")
				{
					_swdoc.OC().itsm_sladef = 0;
					_swdoc.OC().itsm_slaname = "[Use Charge Centre SLA]";
					_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
					_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
					_swdoc.UpdateFormFromData();
					if(boolValidate)
						MessageBox("No charge centre has been set for this request. The charge centre SLA cannot be used.");
					return false;

				}
				else
				{
                    //-- 992268 Add the below line
					_swdoc.OC().costcenter = strCCUse;
					var oRec = app.g.get_record("costcent", strCCUse);
					var oRecSLD = app.g.get_record("itsmsp_slad", oRec.itsm_sla);
					if(oRecSLD)
					{
						strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
						boolRelative = true;
						strRelative = "[Use Charge Centre Priority]";
						//return false;
					}
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Charge Centre SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No charge centre has been set for this request. The charge centre SLA cannot be used.");
				return false;
			}
			break;
		case "[Use Customer SLA]":
			if(_swdoc.oCust().keysearch=="")
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Customer SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No customer selected, cannot resolve customer SLA. Please select a valid SLA.",16);
				return false;
			}
			if(_swdoc.oCust().sld=="")
			{
				_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
				_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No Default SLA is associated to this Customer. Please select a valid SLA.",16);
				else
					MessageBox("Customer does not have a default SLA.");
				return false;
			}
			var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.oCust().sld);
			if(oRecSLD)
			{
				strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
				boolRelative = true;
				strRelative = "[Use Customer Priority]";
				//return false;
			}
		break;		
		case "[Use Process SLA]":
			if(bpm_workflow.pk_workflow_id!="")
			{
				if(bpm_workflow.fk_default_sla_new=="")
				{
					_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
					_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
					_swdoc.UpdateFormFromData();
					MessageBox("No SLA has been set for the requests Business Process. The Process SLA cannot be used.");
					return false;
				}
				else
				{
					var strSLA = bpm_workflow.fk_default_sla_name;
					if(strSLA.indexOf("[Use")>-1 && strSLA.indexOf("SLA]")>-1)
					{
						_swdoc.handle_set_sladef(bpm_workflow.fk_default_sla_name);
						return true;
					}
					else
					{
						//var oRec = app.g.get_record("costcent", strCCUse);
						var oRecSLD = app.g.get_record("itsmsp_slad", bpm_workflow.fk_default_sla_new);
						if(oRecSLD)
						{
							strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
							boolRelative = true;
							strRelative = "[Use Process Priority]";
							//return false;
						}
					}
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Process SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No process has been set for this request. The Process SLA cannot be used.");
				return false;
			}
			break;	}
	if(strUseSLD!="")
	{
  		var oRecSLD = app.g.get_sqrecordset("general/get_itsm_sla","kv="+strUseSLD);
		if(oRecSLD.Fetch())
		{
			//-- load cost center option
			var strNewCC = app.g.get_field(oRecSLD,"chargeto_cc");
			switch(strNewCC)
			{
				case "[Use Department Charge Centre]":
					strNewCC = _swdoc.oCust().department;
					break;
				case "[Use Customer Charge Centre]":
					strNewCC = _swdoc.oCust().costcenter;
					break;		
				default:
					break;
			}
			//-- eof cc option
				
			//-- set oc values
			if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
			//OC().priority = oRecSLD.fk_ssla;	
			_swdoc.OC().itsm_sladef = 	app.g.get_field(oRecSLD,"pk_slad_id");
			_swdoc.OC().itsm_slaname = app.g.get_field(oRecSLD,"slad_id");
			_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
			_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
			
			var strFKSsla = app.g.get_field(oRecSLD,"fk_ssla");
			if(boolRelative)
				_swdoc.handle_set_priority(strRelative);
			else
				_swdoc.handle_set_priority(strFKSsla);
			
			//-- check if customer dept has nay alternative settings
			_swdoc.check_customer_department_slad(strNewSLD,_swdoc.oCust().department);
			
			app._een(_swdoc.MF().label_oc_priority, true);
			app._een(_swdoc.MF().lb_oc_sla, true);
			_swdoc.UpdateFormFromData();
		}
		else
		{
			MessageBox("Failed to retrieve service level definition record for '" + strDisplaySLD + "'. Please contact your Supportworks Administrator");
			return false;
		}
	}
	return true;
}

function handle_set_priority(strValue)
{
	if(strValue=="[Use SLA Default Priority]")
	{
		var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.OC().itsm_sladef);
		if(oRecSLD)
		{	
			_swdoc.OC().priority = oRecSLD.fk_ssla;
			_swdoc.strCurrentPriority = _swdoc.OC().priority;
			_swdoc.UpdateFormFromData();
		}
		return false;
	}	
	else if(strValue=="[Use Customer Priority]")
	{
		if(_swdoc.oCust().keysearch!="")
		{
			if(_swdoc.oCust().sld==_swdoc.OC().itsm_sladef)
			{
				if(_swdoc.oCust().priority=="[Use SLA Default Priority]")
					_swdoc.handle_set_priority("[Use SLA Default Priority]");
				else
				{
					_swdoc.OC().priority = _swdoc.oCust().priority;
					_swdoc.UpdateFormFromData();
				}
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The customer SLA is not the same as the currently selected SLA. The Customer priority cannot be used.");
			}
		}
		else
		{
				if(_swdoc.OC().itsm_slaname=="[Use Customer SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("No customer selected. The Customer priority cannot be used.");
				}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Profile Priority]")
	{
		if(_swdoc.OC().probcode!="")
		{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Profile SLA is not the same as the currently selected SLA. The Profile priority cannot be used.");
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Profile SLA]")
			{
				_swdoc.OC().priority="[Use Profile Priority]";
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No profile has been set for this request. The Profile priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Charge Centre Priority]")
	{
		if(_swdoc.OC().costcenter!="")
		{
			var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.priority);
			}
			else
			{
				if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("The cost center SLA is not the same as the currently selected SLA. The cost center priority cannot be used.");
				}
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
			{
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
				_swdoc.OC().priority = strValue;
				_swdoc.strCurrentPriority=_swdoc.OC().priority;
				_swdoc.UpdateFormFromData();
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No cost center has been set for this request. The cost center priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Process Priority]")
	{
		if(bpm_workflow.pk_workflow_id!="")
		{
			//var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(bpm_workflow.fk_default_sla_new==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(bpm_workflow.fk_default_sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Process SLA is not the same as the currently selected SLA. The Process priority cannot be used.");
			}
		}
		else
		{
			_swdoc.OC().priority = _swdoc.strCurrentPriority;
			_swdoc.UpdateFormFromData();
			MessageBox("No process has been set for this request. The process priority cannot be used.");
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else
	{
		if (_swdoc.boolProblemSLASetManually)
		{
			_swdoc.OC().priority = strValue;
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		_swdoc.UpdateFormFromData();
		return false;
	}
}

function handle_set_contract(strNewSLD)
{
	var strDefaultContract = app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract, strNewSLD);
	if(strDefaultContract!="")
	{
		_swdoc.OC().fk_contract_id = strDefaultContract;
		app.g.strLogAndResolveContract = strDefaultContract;
	}
	_swdoc.UpdateFormFromData();
		
}

						OnCallLoggedOK(nCallRef, dateRespondBy, dateFixBy, strCustName, strSla)
{
	//-- NWJ - 12-02.2007 - after logging problem link its CIs and Incidents
    //-- nwj 
    //-- 15.03.2004
    var intCallRef = app.g.callref_unpad(nCallRef);

	//--
	//-- loop through impacted sites and create link
	
	var strText = "";
	var strDiaryText="";
	var boolSites = false;
	var strSiteCode	="";	
	for (var x=0;x < _swdoc.MF().sl_sites.rowCount();x++)
	{
		if (_swdoc.MF().sl_sites.IsRowChecked(x))
		{
			var fk_ci_id = _swdoc.MF().sl_sites.GetItemTextRaw(x,'fk_cmdb_id');
			var strSite = _swdoc.MF().sl_sites.GetItemTextRaw(x,'site_name');
			if(fk_ci_id>0)
			{
				boolSites= true;
				strText += strSite+".\n";
				app.cmdb.relate_ci_to_call(intCallRef,fk_ci_id,'PROBLEM-SITE')
			}
		}
	}	
	
	if(boolSites)
	{
		var strActionSource = "Problem Management";
		var strCode			= "Site Added";	
		var strDiaryText = "The following site (s) have been reported as experiencing this problem.\n" + strText;	
		app.g.new_dairyevent(intCallRef,strDiaryText,strActionSource,strCode);    		
	}

    //-- loop through causing CI and assign them to this call
	var strDiaryText = "The following Configuration Items were identified as the cause of this problem.\n";
	var boolCIs = false;
    var olist = _swdoc.EF().sl_configitems;
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolCIs =true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'PROBLEM-CAUSE'))
		{
			strDiaryText    += "Configuration Item (" + ci_item_key + "). \n";
		}
    }

    //-- loop through causing Services and assign them to this call
	var strServiceDiaryText = "The following Services were identified as the cause of this problem.\n";
	var boolServices = false;
    var olist = _swdoc.EF().sl_services;
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolServices =true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'PROBLEM-CAUSE'))
		{
			strServiceDiaryText    += "Service (" + ci_item_key + "). \n";
		}
    }
    
    //-- loop through causing Releases and assign them to this call
	var strRelDiaryText = "The following Releases were identified as the cause of this problem.\n";
	var boolRel = false;
    var olist = _swdoc.EF().sl_rels;
    var strReleases = app.g.sl_get_valuesdel(_swdoc.EF().sl_rels,0,",");
    if (strReleases !="")
    {
    	var arrReleases = strReleases.split(",");
    	for (var x=0; x < olist.rowCount(); x++)
   		{
    		boolRel =true;
        	var release_id = olist.GetItemTextRaw(x,0);
	
			//-- Relate the release to the call
			if (app.g.relate_calls(release_id,intCallRef,'RELEASE-CPROBLEM'))
			{
				strRelDiaryText    += "Release (" + app.g.pad_callref(release_id) + ") associated\n";
				
				//-- update problem diary
				strRelText = "The problem (" + nCallRef + ") has been associated as being caused by this release";
				app.g.new_diaryevent(arrReleases[x],strRelText,"Problem Management","Problem Association");
			}
    	}
    }

	//-- affected CIs
    var olist = _swdoc.EF().sl_impactcis;
    var boolAffCIs = false;
	var strCauseDiaryText = "\nThe following Configuration Items were identified as being impacted upon by this problem.\n";    
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolAffCIs = true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'PROBLEM-AFFECT'))
		{
			strCauseDiaryText    += "Configuration Item (" + ci_item_key + "). \n"
		}
    }    
    
    //-- if we have CI to add add it
    if (boolCIs)
    {
		var strActionSource = "Problem Management";
		var strCode			= "Linking Configuration Items";
		if(boolAffCIs) strDiaryText += strCauseDiaryText;
		app.g.new_dairyevent(intCallRef,strDiaryText,strActionSource,strCode);    
    }

    //-- if we have CI to add add it
    if (boolServices)
    {
		var strActionSource = "Problem Management";
		var strCode			= "Linking Services";
		app.g.new_dairyevent(intCallRef,strServiceDiaryText,strActionSource,strCode);    
    }
    
    //-- if we have Release to add add it
    if (boolRel)
    {
		var strActionSource = "Problem Management";
		var strCode			= "Linking Releases";
		app.g.new_dairyevent(intCallRef,strRelDiaryText,strActionSource,strCode);    
    }
    
    // -- If we have a CI or Service, then check whether the first Stage requires Authorisations for Service Owner, Technical Owner and CI Owner
    if(boolCIs || boolServices)
    {
		var xmlmc = new XmlMethodCall();
		xmlmc.SetParam("in_callref", intCallRef);
		xmlmc.Invoke("vpme", "checkStageAuth");
	}	


    //-- insert a relationship between incident and problem / ke
    var strIncidents = app.g.sl_get_valuesdel(_swdoc.EF().sl_incs,0,",");
    if (strIncidents !="")
    {
    	var strText = "This problem has been associated with the following Incidents\n\n";
    	var arrIncidents = strIncidents.split(",");
    	for(var x=0; x < arrIncidents.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrIncidents[x],'PROBLEM-INCIDENT'))
			{
				strText += "Incident (" + app.g.pad_callref(arrIncidents[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strIncText = "The problem (" + nCallRef + ") has been associated to this Incident";
				app.g.new_diaryevent(arrIncidents[x],strIncText,"Problem Management","Problem Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Problem Management","Incident Association");
    }

    //-- insert a relationship between incs and doc
    if(!app.g.use_sw_knowledgebase())
	{
		if(app.strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			var strDocs = app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,",");
    		if (strDocs !="")
    		{
    			var strText = "This Problem has been associated with the following Knowledgebase Documents\n\n";
    			var arrDocs = strDocs.split(",");
    			for(var x=0; x < arrDocs.length; x++)
    			{
					//-- call app.g method to link problem to problem
					app.g.relate_call_to_rakb(arrDocs[x],intCallRef,'Used','Problem',false);
					strText += "Knowledgebase Document (" + arrDocs[x] + ") associated\n";
				}			
				app.g.new_diaryevent(intCallRef ,strText,"Problem Management","Knowledgebase Document Association");
    		}
		}
	}
	else
	{
    	var strDocs = app.g.sl_get_valuesdel(_swdoc.EF().sl_kbs1,0,",");
    	if (strDocs !="")
    	{
    		var strText = "This Problem has been associated with the following Knowledgebase Documents\n\n";
    		var arrDocs = strDocs.split(",");
    		for(var x=0; x < arrDocs.length; x++)
    		{
				//-- call app.g method to link problem to incident
				app.oKB.relate_call_to_kb(arrDocs[x],intCallRef,'Used');
				strText += "Knowledgebase Document (" + arrDocs[x] + ") associated\n";
			}			
			app.g.new_diaryevent(intCallRef ,strText,"Problem Management","Knowledgebase Document Association");
    	}
   }
    
    //--F0086525 & PM00128139
    var oRec = app.g.get_sys_record("opencall", intCallRef);
	if(typeof oRec.logdatex === "number")
	{
		oRec.logdatex = app.g.fix_epoch(oRec.logdatex);
		itsm_opencall_problem.logdatex = oRec.logdatex; 
	}
	else
	{
		itsm_opencall_problem.logdatex = app.global.GetCurrentEpocTime();
	}

	//-- F0105032 - audit trail if incoming call	
	if(_swdoc._form.PreloadType==2)
	{
		if(_swdoc.OC().bpm_workflow_id!="")
		{
			var intEpochTime = app.global.GetCurrentEpocTime();
			var oAnalyst = app.g.get_sys_record("swanalysts",app.session.analystId);
			var oGroup = app.g.get_sys_record("swgroups",app.session.groupId);
			var strParams = "fk_workflow_id="+app.pfu(_swdoc.OC().bpm_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				var strTable = arrBinding[0];
				var strColName = arrBinding[1];
				
				var oldValue = "";
				if(strTable=="opencall")
					var oldValue = _swdoc.oOpencall[strColName];
				else
					var oldValue = _swdoc.oExtended[strColName];
				
				var newValue = _swdoc[strTable][strColName];
				if(app.g.dd_isnumeric(strTable, strColName))
				{
					if(newValue<0)
						newValue = app.g.fix_epoch(newValue);
					if(oldValue<0)
						oldValue = app.g.fix_epoch(oldValue);
				}
				if(oldValue!=newValue)
				{
					var strFieldDisplay = dd.tables[app.LC(strTable)].columns[strColName].DisplayName;
					var strFmtOldValue = app.g.dd_format(strTable, strColName, oldValue);
					var strFmtNewValue = app.g.dd_format(strTable, strColName, newValue);
					var oInsertTmp = new app.oXmlmcInsert('oc_audit');
					oInsertTmp.setfield('fk_callref',_swdoc.OC().callref);
					oInsertTmp.setfield('updatetimex',intEpochTime);
					oInsertTmp.setfield('updatedby',app.session.analystId);
					oInsertTmp.setfield('updatedbyname',oAnalyst.name);
					oInsertTmp.setfield('updatedbygroup',app.session.groupId);
					oInsertTmp.setfield('updatedbygroupname',oGroup.name);
					oInsertTmp.setfield('databinding',strBinding);
					oInsertTmp.setfield('bindingfield',arrBinding[1]);
					oInsertTmp.setfield('bindingtable',arrBinding[0]);
					oInsertTmp.setfield('fielddisplay',strFieldDisplay);
					oInsertTmp.setfield('oldvalue',oldValue);
					oInsertTmp.setfield('newvalue',newValue);
					oInsertTmp.setfield('fmtoldvalue',strFmtOldValue);
					oInsertTmp.setfield('fmtnewvalue',strFmtNewValue);
					oInsertTmp.execute();
				}
			}
		}
	}	//-- EOF F0105032 
}

						OnRecordValueChanged(strTable, strColumn, strValue, strOldValue)
{
	//-- if update db is changed add it to problem_desc
	if((strTable == "updatedb") && (strColumn=="updatetxt"))
	{
		_swdoc.PRB().prb_info = strValue;
		_swdoc.UpdateFormFromData();
	}
	
	//-- NWJ - check to see if user has changed the itsm_opencall_rfc table field
	//-- to search for raised by and monitored by analyst - so it works like resolving a customer.
	else if(strTable=="itsm_opencall_problem")
	{
		var oAnalyst = false;
		if((strColumn.indexOf("raised_") == 0))
		{
			//-- get db col name based on passed in col name
			var strDBCol="";
			//MessageBox(strColumn.split("_")[2])
			switch(strColumn.split("_")[2])
			{
				case "analystid":
					strDBCol="analystid";
					break;
				case "name":
					strDBCol="name";
					break;
				case "group":					
					strDBCol="supportgroup";
					break;
				case "telephone":
					strDBCol="contactb";
					break;
				case "email":
					strDBCol="contacte";
					break;
				default:
					MessageBox("The extended problem column (" + strColumn + ") cannot be matched to swanalysts. Please contact your Supportworks Administrator.");
					_swdoc.set_rfc_analyst(false,true);
					return;
					break;								
			}
		}
		if(strColumn.indexOf("raised_") == 0)
		{
			//-- auto search raised by
			app.g.resolve_analyst(strDBCol,strValue,function(oAnalyst)
			{
				if(oAnalyst) _swdoc.set_prb_analyst(oAnalyst);
			});
		}		
	}
	
	//RJC 3.0 - set recognition profile to the same profile as problem profile
	else if((strTable == "opencall") && (strColumn=="probcode"))
	{
		_swdoc.boolChangeProfile = true;
  		mainform.SetTimer(4, 100);
		itsm_opencall_problem.inc_profilecode = strValue;
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
		mainform.UpdateFormFromData();
	}
	
	else if(strColumn=="priority" && strTable == "opencall")
    {
    	if(_swdoc.boolChangeProfile)
    	{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			var oSLA = app.g.get_record("itsmsp_slad", oRec.itsm_sla);

			_swdoc.handle_set_sladef(oSLA.slad_id);
			_swdoc.boolProblemSLASetManually = true;
			_swdoc.handle_set_priority(oRec.sla);
 			_swdoc.UpdateFormFromData();
   		}
    }
    else if( (strColumn=="itsm_impact_level")||(strColumn=="itsm_urgency_level") )
    {
    	_swdoc.boolProblemSLASetManually = true; //--107865
    	
    	if(_swdoc.OC().itsm_sladef!="")
    	{
    		//-- if use incident assessment over ci
    		//if(app.itsm.get_ci_assessmnet_level_setting("Problem")!=1)
    		//{
    			var strPriority = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
    			if(strPriority!="")
   					_swdoc.handle_set_priority(strPriority);
    			_swdoc.UpdateFormFromData();
    		//}
    	}
    }
    else if (strColumn=="fk_company_id"||strColumn=="site"||strColumn=="fk_dept_code"||strColumn=="costcenter"||strColumn=="pk_workflow_id")
	{
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}

    
}

						OnValidate()
{
	_swdoc.PRB().suppgroup = app.session.groupId;
	_swdoc.PRB().prb_callclass = "Problem";
	
	//-- app.itsm 2.4.0 - check if need mandatory ci
	if(bpm_workflow.flg_requireci==1)
	{
		if(_swdoc.EF().sl_configitems.rowCount() < 1)
		{
			if(_swdoc.EF().sl_services.rowCount() < 1)
			{
				MessageBox("Before logging this Problem request you have to associate at least one causing configuration item or service");
				return VALIDATE_FAIL;
			}
		}
	}

	//-- 108840 : Check if we have required number of CIs 
	var strCallType = "Problem";
	var intCurCINo = _swdoc.EF().sl_configitems.rowCount() + _swdoc.EF().sl_services.rowCount();
	if(!app.itsm.mandatory_ci_check(strCallType, intCurCINo))
	{
		var intReqCINo = app.oSettings.get_setting("int" + strCallType + "OnLoggingRequiredCINo");
		var strPlural = "";
		if (intReqCINo > 1)
		{
			strPlural= "s";
		}
		MessageBox("Before logging this " + strCallType + " request you have to associate at least " + intReqCINo + " causing configuration item" + strPlural + " or service" + strPlural + ".");
		return VALIDATE_FAIL;
	}
	
	_swdoc.OC().itsm_title = _swdoc.PRB().prb_title;
	
	
	//-- nwj - hide confirmation if we have no customer
	if(userdb.keysearch=="")
	{
	
		app.g.bPromptToOpenCallDetails = true;
		app.g.bDoNotShowLogCallConfirmation = true;
	}
	else
	{
		app.g.bDoNotShowLogCallConfirmation = false;
	}	
	
	return VALIDATE_DEFAULT;
}

						OnLogAndAssignCall(strGroupID, strAnalystID)
{
	_swdoc.PRB().suppgroup = strGroupID;
	var strDefaultGroup = dd.GetGlobalParamAsString("Application Settings/Problem/DefaultAssignmentGroup");
	if(strDefaultGroup!="")
	{
		if(strDefaultGroup!=_swdoc.PRB().suppgroup)
		{
			var strDefaultAnalyst = dd.GetGlobalParamAsString("Application Settings/Problem/DefaultAssignmentAnalyst");
			var strDisplayAnalyst = ":"+strDefaultAnalyst;
			if(strDefaultAnalyst=="")
				strDisplayAnalyst = "";
			var boolConfirm = confirm('The default assignment for problem records is '+strDefaultGroup+strDisplayAnalyst+'. Do you want to use the default?');
			if(boolConfirm)
			{
				_swdoc.OC().suppgroup = strDefaultGroup;
				_swdoc.OC().owner = strDefaultAnalyst;
				if(_swdoc.OC().owner!="")
				{
					_swdoc.OC().status = 3;
				}
			}
		}
	}
	
	_swdoc.OC().custom_i3=0;
	return true;
}

						OnRecordResolved(strTable)
{
	//-- set sla based on worflow default
	//-- 92592
	if(IsObjectDefined("mainform") && (strTable=="bpm_workflow"))
	{
		var boolSet = true;
		var boolEnable = true;

		if(bpm_workflow.fk_default_sla_new>0)
		{
			var boolSLA = bpm_workflow.flg_sla_apply>0;
			if(boolSLA)
			{
				var boolSet = true;
				if(_swdoc.boolProblemSLASetManually)
				{
					var boolOverride = confirm("It is recommended to use SLA set against the process.\n\n\Would you like to set the SLA to the process value?");
					if(!boolOverride)
						boolSet = false;
				}
				
				if(boolSet)
				{
					if(bpm_workflow.flg_sla_enforce==1)
						boolEnable = false;
				}
			}
			else
			{
				boolSet = false;
			}
		}
		else
		{
			boolSet = false;
		}
		
		if(boolSet)
		{
			//--F0107934
			_swdoc.boolSLAEnforced = false;
			_swdoc.handle_set_sladef(bpm_workflow.fk_default_sla_name,false,true);
			var boolTempSLASetManually = _swdoc.boolProblemSLASetManually;
			_swdoc.boolProblemSLASetManually = true;
			_swdoc.handle_set_priority(bpm_workflow.fk_default_sla);
			_swdoc.boolProblemSLASetManually = boolTempSLASetManually;
			_swdoc.UpdateFormFromData();
			//--End
			if(!boolEnable)
			{
				app._een(mainform.lb_oc_slad1,false);
				app._een(mainform.Label5,false);
				_swdoc.boolSLAEnforced = true;
			}
		}
		
		if(boolEnable)
		{
			app._een(mainform.lb_oc_slad1,true);
			app._een(mainform.Label5,true);
			_swdoc.boolSLAEnforced = false;
		}
		app.g.process_bpm_form_attribute_check(_swdoc,bpm_workflow.pk_workflow_id);
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}
	
	//-- refresh site list of customer co is different
	if (IsObjectDefined("mainform") && strTable == "userdb")
	{
		if(app.LC(_swdoc.oCust().fk_company_id) != app.LC(_swdoc.strCurrentCompanyID))
		{
			_swdoc.strCurrentCompanyID = _swdoc.oCust().fk_company_id;
		}
		
		//-- check if customer is in active    	
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
		//-- log from email fix
		if(app.LC(_swdoc.oCust().fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))	
		{
			_swdoc.ResolveRecord("company", "pk_company_id", _swdoc.oCust().fk_company_id);
		}
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}
	if(strTable == "company")
 	{
 		//-- Grab Company Record
 		var oCompanyTemp = app.g.get_record("company",_swdoc.oCompany().pk_company_id,'companyname');
 		//--F0110887 Start
		var intExpiryEpoch = app.g.fix_epoch(oCompanyTemp.supportexpiryx);
		app.g.checkOrgSupport(_swdoc.oCust().companyname, intExpiryEpoch, true);
		//--F0110887 End
 	}
    if (IsObjectDefined("mainform") && strTable == "company")
    {
    	app.g.strLogAndResolveCompany = _swdoc.oCompany().pk_company_id;
    	
    	// -- Display the Org "Alert Contents" message
    	if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
    	
    	if(app.LC(_swdoc.oCompany().pk_company_id) != app.LC(_swdoc.oCust().fk_company_id))
    	{
    		_swdoc.ResetRecord('userdb');
			if(dd.GetGlobalParamAsNumber("Application Settings/Change/OnOrganisationResolveCustomer")==1)
			{
				_swdoc.boolResolvingCust = true;
				mainform.SetTimer(1, 10);
			}
		}
    		
		if(_swdoc.OC().costcenter!=""){
			var recCC = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(recCC)
			{
				if(app.LC(recCC.fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
				{
					_swdoc.OC().costcenter = "";
					app._ete(_swdoc.MF().lb_oc_cc, "");
				}
			}
		}

		if(_swdoc.OC().fk_dept_code!=""){
			var recCC = app.g.get_record("department", _swdoc.OC().fk_dept_code);
			if(recCC)
			{
				if(app.LC(recCC.fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
				{
					_swdoc.OC().fk_dept_code = "";
					app._ete(_swdoc.MF().fld_oc_dept, "");
				}
			}
		}

		_swdoc.check_cis();
 		_swdoc.strCurrentCompanyID = _swdoc.oCompany().pk_company_id;
 		_swdoc.filter_company_sites();
		
		//-- Filter Contracts for Company
		app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract);
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);

		_swdoc.UpdateFormFromData();
	}


}

						OnResolveRecord(strTable, strColumn, strValue)
{
 	//-- resolving the customer record
 	if(IsObjectDefined("mainform") && strTable == 'userdb' && _swdoc.boolResolvingCust == false && strValue!="")
 	{
  		if(strColumn =="firstname!surname")
  		{
  			//-- if using customer name search on firstname and lastname
  			//-- 2.4.1 bug fix 79046
   			arrNames = strValue.split(" ");
   			if(arrNames[1]==undefined)
   			{
      			var strParams = "_swc_firstname="+arrNames[0];
      			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   				var strSearch = "firstname="+arrNames[0];
    		}
   			else
   			{
    			var strLastName="";
    			for(var y=1;y<arrNames.length;y++)
    			{
     				if(y>1)strLastName = strLastName+" ";
     				strLastName = strLastName+arrNames[y];
    			}
      			var strParams =  "_swc_firstname="+arrNames[0]+"&_swc_surname="+strLastName;
      			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   				var strSearch = "firstname="+arrNames[0]+"&surname="+strLastName;
   			}
  		}
  		else
  		{
      		var strParams =  "_swc_"+strColumn+"="+strValue;
      		var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   			var strSearch = strColumn+"="+strValue;
   		}
		if(intCount!=1 && !_swdoc.boolCustid_fromCall)
		{
			_swdoc.strSearchFor = strSearch;
  			mainform.SetTimer(1, 100);
  			_swdoc.boolResolvingCust = true;
  			return "";
 		}
 	}
 	
	//-- resolving org record 	
 	if(IsObjectDefined("mainform") && strTable == 'company' && strValue =="")
 	{
  		//2.4 clear down list if user is being unselected
  		_swdoc.ResetRecord('userdb');
 		app.g.strLogAndResolveCompany = "";
	} 

	//-- resolving org record 	
 	if(IsObjectDefined("mainform") && strTable == 'bpm_workflow' && strValue =="")
 	{
		app._een(mainform.lb_oc_slad1,true);
		app._een(mainform.Label5,true);

		app.g.reset_bpm_form_attribute_check(_swdoc);
	} 
 
 return strValue;
}

						OnLogCall()
{
	// TODO: Add your event handler code here
	var strDefaultGroup = dd.GetGlobalParamAsString("Application Settings/Problem/DefaultAssignmentGroup");
	var strDefaultAnalyst = dd.GetGlobalParamAsString("Application Settings/Problem/DefaultAssignmentAnalyst");
	if(strDefaultGroup!="")
	{
		_swdoc.OC().suppgroup = strDefaultGroup;
		_swdoc.OC().owner = strDefaultAnalyst;
		if(_swdoc.OC().owner!="")
		{
			_swdoc.OC().status = 3;
		}
	}
	
	_swdoc.OC().custom_i3=0;
	
	return true;
}

						OnPreValidate()
{
    //--F0110887 Start
    var intExpiryEpoch = app.g.fix_epoch(_swdoc.oCompany().supportexpiryx);
	var boolOrgSupported = new Boolean(app.g.checkOrgSupport(_swdoc.oCompany().companyname, intExpiryEpoch, false));
	if (boolOrgSupported==false)
	{
		 MessageBox("You cannot log this call as the '" + _swdoc.oCompany().companyname + "' organisation is no longer supported.");
		 return VALIDATE_FAIL;
	}
    //--F0110887 End
    
    var boolResolve = false;
	if(	_swdoc.OC().itsm_slaname=="[Use Customer SLA]"||_swdoc.OC().itsm_slaname=="[Use Profile SLA]" || _swdoc.OC().itsm_slaname=="[Use Workflow SLA]" || _swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
		boolResolve = true;
	if(boolResolve)
	{
		var boolOK = _swdoc.handle_set_sladef(_swdoc.OC().itsm_slaname,true);
		if(!boolOK)
			return VALIDATE_FAIL;
	}
	
	if (!app.g.resolve_default_priority(_swdoc.OC().priority,_swdoc,"Problem")){
	return VALIDATE_FAIL;
	}
	
	return VALIDATE_DEFAULT;
}

