}

						Button1_OnPressed()
{
   var pc = new ChooseProfileCodeDialog;
    if(pc.Open(false,false) == IDOK)
    {
        var strInfo;
        strInfo = "Code: " + pc.code + "\n"
        strInfo += "SLA: " + pc.sla + "\n"
        strInfo += "CodeDesc: " + pc.codeDescription + "\n\n";
        strInfo += pc.description;
        MessageBox(strInfo);
    }

    var dlg = new PickAnalystDialog;
    if(dlg.Open("Pick an Analyst/Group") == IDOK)
    {
        MessageBox("Group: " + dlg.groupid + "  Analyst:" + dlg.analystid);
    }
}

						btn_pick_sb_OnPressed()
{
  //-- NWJ 17.03.2004
   //-- open analyst search form in select mode
        var oe = elements;
    // Create an instance of a PopupForm class
    var form = new PopupForm();
    var strUrl = "analystid=" + oe["db.rfc_rb_id"].text + "&suppgroup=" + oe["db.rfc_rb_sg"].text + "&name=" + oe["db.rfc_rb_name"].text;
    // Open the form TbSites
    if(form.Open("tbAnalysts","",strUrl))
    {
        //-- Access the forms _swdoc and call the _swdoc.GetVariable() method with the name of the variable
        var stranalystid = form.document.analystid;
        if (stranalystid == "") return;

        //-- assign values
        app._ete(oe["db.rfc_rb_id"] , stranalystid);
        app._ete(oe["db.rfc_rb_name"] , form.document.name);
        app._ete(oe["db.rfc_rb_sg"] , form.document.group);
        app._ete(oe["db.rfc_rb_tel"] ,form.document.phone);
        app._ete(oe["db.rfc_rb_email"] , form.document.email);
        _swdoc.opencall.itsm_rfc_rb_id = stranalystid;
        _swdoc.opencall.itsm_rfc_rb_name = form.document.name;
        _swdoc.opencall.itsm_rfc_rb_group = form.document.group;
        _swdoc.opencall.itsm_rfc_rb_tel = form.document.phone;
        _swdoc.opencall.itsm_rfc_rb_email = form.document.email;
    }
    
}

