		{
			_swdoc.set_prb_analyst(oAnalyst);
		}
	});
}

						OnFormLoaded()
{
	//-- F0105032
	if(_swdoc._form.PreloadType==2)
	{
		_swdoc.oOpencall = app.g.get_sys_record("opencall",_swdoc.OC().callref);
		_swdoc.oExtended = app.g.get_record("itsm_opencall_problem", _swdoc.OC().callref);
	}
	//-- EOF F0105032
	//-- save class
	_swdoc.PRB().prb_callclass = _swdoc.OC().callclass;

	//-- NWJ - 28.02.2006
	//--      get any incoming cis
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems, _swdoc.OC().callref ,"INCOMING","")	
	
	
	//-- load CIs if call has been logged from another problem
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems, _swdoc.OC().callref ,"PROBLEM-CAUSE","cis_excludeservice");
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_services, _swdoc.OC().callref ,"","cis_servicesonly");
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_impactcis, _swdoc.OC().callref ,"PROBLEM-AFFECT","cis_excludeservice");

	var oAnalyst = app.g.get_analyst_detail(app.session.analystId);
	_swdoc.set_prb_analyst(oAnalyst);

	//--
	//-- check if we are raising this problem from an incident
	if(app.g.LOG_FROM_CALL!=null)
	{
		//-- load from call items into this problem
		//-- ITSM 2.2.0 removed -- app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems, app.g.LOG_FROM_CALL.callref);
		app.cmdb.sl_load_call_cis(_swdoc.EF().sl_impactcis, app.g.LOG_FROM_CALL.callref);
		strCalls = app.cmdb.get_call_cis(app.g.LOG_FROM_CALL.callref ," ");
		_swdoc.link_affect_cis_to_problem(strCalls);  
		_swdoc.EF().sl_impactcis.Refresh();
		//-- store incident record
		app._slf(_swdoc.EF().sl_incs, "_kv=" + app.g.LOG_FROM_CALL.callref);
		_swdoc.EF().sl_incs.Refresh();	
		//-- show customer impacted by the log from call
		if(app.g.LOG_FROM_CALL.cust_id!="")_swdoc.filter_impact_customers(_swdoc.EF());
		//-- copy over prob text
		itsm_opencall_problem.prb_info = app.g.CALLINFO[app.g.LOG_FROM_CALL.callref].origupdatetxt

				//-- copy over prob code into recog prob code
		itsm_opencall_problem.inc_profilecode = app.g.LOG_FROM_CALL.probcode;
		//-- F0105872 - pull through profile code from incident
		opencall.probcode = app.g.LOG_FROM_CALL.probcode;
	
		//-- copy of cost center
		_swdoc.OC().costcenter = app.g.LOG_FROM_CALL.costcenter;
		
		_swdoc.OC().itsm_title = app.g.LOG_FROM_CALL.itsm_title;
		
		itsm_opencall_problem.prb_title = _swdoc.OC().itsm_title;
		if(itsm_opencall_problem.prb_title!="")
		{
			_swdoc.boolProblemTitleSetManually = true;
		}

		
		
		//-- impact / urgency / priority level 
		_swdoc.OC().itsm_impact_level 	= app.g.LOG_FROM_CALL.itsm_impact_level;
		_swdoc.OC().itsm_urgency_level	= app.g.LOG_FROM_CALL.itsm_urgency_level;
		//OC().priority			= app.g.LOG_FROM_CALL.priority;
		
		if(app.g.LOG_FROM_CALL.cust_id!="")
		{
			_swdoc.boolCustid_fromCall = true;
			_swdoc.ResolveRecord("userdb", "keysearch", app.g.LOG_FROM_CALL.cust_id);
		}
		
		//-- update form
		_swdoc.UpdateFormFromData();
		
		//-- 75900 - prob_text not being set
		_swdoc.UPD().updatetxt = app.g.CALLINFO[app.g.LOG_FROM_CALL.callref].origupdatetxt;
		
		//-- important - null record		
		app.g.LOG_FROM_CALL=null;
	}
	else if(app.g.LOG_CALL_FOR_USER!="")
	{
		//-- if logging ofr a customer
		_swdoc.ResolveRecord("userdb", "keysearch", app.g.LOG_CALL_FOR_USER);
		app.g.LOG_CALL_FOR_USER="";
	}
	else if(app.g.LOG_FOR_ORGID!="")
	{
		_swdoc.ResolveRecord("company", "pk_company_id", app.g.LOG_FOR_ORGID);	
		app.g.LOG_FOR_ORGID="";
	}
	else if(app.g.LOG_CALL_FOR_CI!="")
	{
		app.g.sl_appendfilter(_swdoc.EF().sl_configitems, app.g.LOG_CALL_FOR_CI,'pk_auto_id');
		_swdoc.EF().sl_configitems.Refresh();
 		app.g.LOG_CALL_FOR_CI="";
		_swdoc.check_ci_assessment();
	}
	else
	{
		if(_swdoc.oCust().keysearch!="")
		{
			if(_swdoc.oCust().fk_company_id!=_swdoc.strCurrentCompanyID)
			{
				_swdoc.filter_company_sites();
				_swdoc.strCurrentCompanyID = _swdoc.oCust().fk_company_id;
			}
		}
	}
	
	
	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	app._een(_swdoc.MF().lb_company,((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	app._evi(_swdoc.MF().lb_contract,((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	app._evi(_swdoc.MF().lbl_contract,((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	
	if(!_swdoc.MF().lb_company.enable)
	{
		//-- check if we should hide
		app._evi(_swdoc.MF().lb_company,(dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));		

		//alter filter if we do not support external
		app._slf(_swdoc.MF().lb_oc_cc,"");
		app._slf(_swdoc.MF().fld_oc_dept,"");
	}
	app._evi(_swdoc.MF().lbl_company, _swdoc.MF().lb_company.visible);	
	//-- eof hide / enable		

	//-- RJC - F0105434
	app.g.get_default_process(_swdoc,'Problem',_swdoc.MF().fld_bpm_workflow,_swdoc.MF().lbl_bpm_workflow);
//	app.g.apply_appcode_bpm_filter(_swdoc.MF().fld_bpm_workflow, 'BPM');
	//-- EOF - F0105434
		
	//OC().priority = "[Use Customer Priority]";
	_swdoc.UpdateFormFromData();	


	//-- F0094989
	if(_swdoc.OC().itsm_slaname.indexOf("[Use ")<0 && _swdoc.OC().itsm_slaname.indexOf(" SLA]")<0 )
	{
		if(_swdoc.OC().itsm_slaname!="")
		{
			var slarec = app.g.get_sqrecordset("general/get_itsm_sla","kv="+_swdoc.OC().itsm_slaname);
			if(slarec.Fetch())
			{
				var intSLAId = app.g.get_field(slarec,"slad_id");
				if(intSLAId!="")
				{
					_swdoc.handle_set_contract(intSLAId);
					_swdoc.handle_set_sladef(intSLAId);					
				}
			}
		}
	}
	
	_swdoc.strDefaultImpact = (_swdoc.OC().itsm_impact_level);
	_swdoc.strDefaultUrgency = (_swdoc.OC().itsm_urgency_level);
	_swdoc.strCurrentPriority = _swdoc.OC().priority;
	_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
//	app.g.apply_appcode_sla_filter(_swdoc.MF().lb_oc_slad1, 'SLA-CALL');

	//-- F0108825 : A newly logged call should not have resolve/close text
	//	         even when it has been copied from a resloved/closed call
	_swdoc.OC().resolve_text="";
	_swdoc.OC().close_text="";	
	//	PM00143977 : values copied from parent call
	_swdoc.OC().lastactdatex="";	
	_swdoc.OC().lastactdate="";	
	_swdoc.OC().lastactby="";	
	_swdoc.OC().assigndatx="";	
	_swdoc.OC().withinresp="";	
	_swdoc.OC().resp_time="";	
	_swdoc.OC().slaresp="";	
	_swdoc.OC().assigndate="";	
	//-- TK Set Summary Based on Email Subject
	if((_swdoc._form['emailsubject']) && (app.oSettings.get_setting("boolUseSubjectasSummary")=='True'))
	{
		_swdoc.PRB().prb_title = _swdoc._form['emailsubject'];
		_swdoc.boolProblemTitleSetManually = true;
	} 
	if(_swdoc.oCompany().pk_company_id)
	{
		_swdoc.ResolveRecord("company", "pk_company_id", _swdoc.oCompany().pk_company_id);
	}
	// START 105553
	if(_swdoc.oCust().keysearch)
	{
		if(_swdoc.oCust().fk_company_id!=_swdoc.oCompany().pk_company_id)	
		{
			_swdoc.ResolveRecord("company", "pk_company_id", _swdoc.oCust().fk_company_id);
		}
	}
	// END 105553
	_swdoc.strDefaultPriority = _swdoc.MF().lb_oc_sla.value;
}

