						mbtn_ici_actions_OnMenuItem(strName, nItemID)
{
	var iRemoveCI = 1;

	//-- Remove Selected config items
	if(nItemID == iRemoveCI)
	{
   	
		//-- do this by finding unselected rows and making sure they are part of fitler
		var strFilter = "-1";
		for (var x=sl_impactcis.rowCount()-1;x > -1;x--)
		{
			if (!sl_impactcis.IsRowSelected(x))
			{
				//-- add to filter
				var value = sl_impactcis.GetItemTextRaw(x,0);
				if (value != "")
				{
    				if (strFilter != "") strFilter +=",";
    				strFilter += value;
				}
			}
		}
		if(strFilter!="")
			strFilter = "_kv="+strFilter;
       app._slf(sl_impactcis, strFilter);  
       _swdoc.EF().sl_impactcis.Refresh();
       sl_impactcis.SetRowSelected(0);
	   sl_impactcis_OnItemSelected(0);       
	  // _swdoc.filter_impact_customers(_swdoc.EF());	   
       return;
	}

}

