						mbtn_inc_actions_OnMenuItem(strName, nItemID)
{

	var iClear = 1;
	var iLinkCauseCI = 2;
	var iLinkImpactCI = 3;
	
	switch (nItemID)
	{
		case iClear:
			//-- do this by finding unselected rows and making sure they are part of fitler
			var strFilter = "-1";
			for (var x=sl_incs.rowCount()-1;x > -1;x--)
			{
				if (!sl_incs.IsRowSelected(x))
				{
					//-- add to filter
					var value = sl_incs.GetItemTextRaw(x,0);
					if (value != "")
					{
    					if (strFilter != "") strFilter +=",";
    					strFilter += value;
					}
				}
			}
			if(strFilter!="")
				strFilter = "_kv="+strFilter;
       		app._slf(sl_incs, strFilter);  
       		_swdoc.EF().sl_incs.Refresh();
       		sl_incs.SetRowSelected(0);
	   		sl_incs_OnItemSelected(0);  
			break;
		case iLinkCauseCI:
			var strSelectedIncs = app.g.sl_getselected_valuesdel(sl_incs,0,",","");
			var strLinkedCIs = app.cmdb.get_call_cis(strSelectedIncs , "INCIDENT", "");
			_swdoc.link_cause_cis_to_problem(strLinkedCIs);
			_swdoc.EF().sl_incs.Refresh();		
			break;
		case iLinkImpactCI:
			var strSelectedIncs = app.g.sl_getselected_valuesdel(sl_incs,0,",","");	
			var strLinkedCIs = app.cmdb.get_call_cis(strSelectedIncs , "INCIDENT", "");
			_swdoc.link_affect_cis_to_problem(strLinkedCIs);
			_swdoc.EF().sl_incs.Refresh();	
			//_swdoc.filter_impact_customers(_swdoc.EF());

			break;
	}

}

						btn_search_inc_OnPressed()
{
   	var strURL = "canreset=1";
    app.itsm.search_calls("INC",strURL,true,function(oResults)
    {
    	if(oResults.selectedkeys=="") return;

		var strFilter = app.g.sl_get_valuesdel(sl_incs,0,",");
		if (strFilter != "") 
		{
			strFilter += "," + oResults.selectedkeys;
		}
		else
		{
			strFilter = oResults.selectedkeys;
		}
		app._slf(sl_incs, "_kv=" + strFilter);
		sl_incs.SetRowSelected(0, true, false);
	
		//-- Now check if we should link new ci items as being affect
		if(dd.GetGlobalParamAsNumber("Application Settings/Problem/CopyIncidentCIs")==1)
		{
			//-- if dont have info get it otherwise use info we go above
			var strLinkedCIs = app.cmdb.get_call_cis(oResults.selectedkeys , "INCIDENT", "");
			if(strLinkedCIs!="0")
				_swdoc.link_affect_cis_to_problem(strLinkedCIs);
		}
		_swdoc.EF().sl_incs.Refresh();
		//--
		//-- get list of customers impacted by these incidents and refresh cust impact list
		//_swdoc.filter_impact_customers(_swdoc.EF());
    });
}

