						mbtn_inc_views_OnMenuItem(strName, nItemID)
{
	var iDetails = 1;
	var iCust = 2
	if(nItemID == iDetails)
	{
		if(sl_incs.curSel > -1)sl_incs.EditRecord(sl_incs.curSel);
	}
	else if (nItemID==iCust)
	{
		if(sl_incs.curSel > -1)
		{
			var strKeySearch = app.g.sl_getcolvalue(sl_incs,-1,"cust_id");
			if(strKeySearch!="")app.OpenFormForEdit("tfCustomer", strKeySearch, "", true);
		}
	}
}

						TabRel_OnTabSelected(nTab)
{
	if(nTab==5)
	{
		_swdoc.filter_impact_customers(_swdoc.EF()); 
	}
	else if (nTab==6)
	{
		_swdoc.filter_impact_orgs(_swdoc.EF()); 
	}
	else if (nTab==3)
	{
		sl_incs.Refresh();
	}
	
}

						OnFormLoaded(bDataReloaded)
{
	TabRel.ShowTabItem(4,((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	app._etab(TabRel,0);

	//-- Show appropriate KB Tab
	var tTab = _swdoc.EF().TabRel.tab;
	if(!app.g.use_sw_knowledgebase())
	{
		if(app.strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			_swdoc.EF().TabRel.ShowTabItem(8,true);
			_swdoc.EF().TabRel.ShowTabItem(4,false);
			app._etab(_swdoc.EF().TabRel, tTab);
		}
	}
	else
	{
		_swdoc.EF().TabRel.ShowTabItem(8,false);
		_swdoc.EF().TabRel.ShowTabItem(4,true);
		app._etab(_swdoc.EF().TabRel, tTab);
	}

}

						mbtn_cci_views1_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_services,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_services,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}	
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						mbtn_cci_actions1_OnMenuItem(strName, nItemID)
{
	var iRemoveCI = 1;

	//-- Remove Selected config items
	if(nItemID == iRemoveCI)
	{
   	
		//-- do this by finding unselected rows and making sure they are part of fitler
		var strFilter = "-1";
		for (var x=sl_services.rowCount()-1;x > -1;x--)
		{
			var value = sl_services.GetItemTextRaw(x,"pk_auto_id");
			if (!sl_services.IsRowSelected(x))
			{
				//-- add to filter
				if (value != "")
				{
    				if (strFilter != "") strFilter +=",";
    				strFilter +=  value;
				}
			}
			else
			{
				//-- 29.04.2008 - 2.2.0 if this ci that has been removed was used to set the pref supp group then change it
				if(_swdoc.intAssessmentCI == value)
				{
					_swdoc.OC().itsm_sladef = 0;
					_swdoc.OC().itsm_slaname = "[Use Customer SLA]";
					_swdoc.OC().priority = "[Use Customer Priority]";
					_swdoc.OC().itsm_prefered_suppgroup = "";
					_swdoc.OC().itsm_prefered_text = "";
		
					app._eva(_swdoc.MF().lb_oc_slad1, 0);
					app._ete(_swdoc.MF().lb_oc_slad1, 0);
					app._een(_swdoc.MF().label_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
		
					_swdoc.UpdateFormFromData();
				}
			}
		}
	if(strFilter!="")
		strFilter = "pk_auto_id="+strFilter+"&type=2";

		 app._slf(sl_services, strFilter);  
		//sl_configitems1.Refresh();
		sl_services.SetRowSelected(0);
		sl_services_OnItemSelected(0);
		_swdoc.EF().sl_services.Refresh();
		_swdoc.check_ci_assessment();
		return;
	}
       
}

