						mbtn_inc_actions2_OnMenuItem(strName, nItemID)
{

	var iClear = 1;
	var iLinkCauseCI = 2;
	var iLinkImpactCI = 3;
	
	switch (nItemID)
	{
		case iClear:
			//-- do this by finding unselected rows and making sure they are part of fitler
			var strFilter = "-1";
			for (var x=sl_kbs1.rowCount()-1;x > -1;x--)
			{
				if (!sl_kbs1.IsRowSelected(x))
				{
					//-- add to filter
					var value = sl_kbs1.GetItemTextRaw(x,0);
					if (value != "")
					{
    					if (strFilter != "") strFilter +=",";
    					strFilter += value;
					}
				}
			}
			if(strFilter!="")
				strFilter = "_kv="+strFilter;
       		app._slf(sl_kbs1, strFilter);  
       		_swdoc.EF().sl_kbs1.Refresh();
       		sl_kbs1.SetRowSelected(0);
	   		sl_kbs1_OnItemSelected(0);  
			break;
	}

}

						btn_search_inc2_OnPressed()
{
    app.g.search_for("swkb", true,"",function(oRes)
    {
    	var strKeys = oRes.selectedkeys;
		if(strKeys=="")return;
		var arrKeys = strKeys.split(",");
		for (var x=0; x< arrKeys.length;x++)
		{
			var fk_key = arrKeys[x];
			if(app.g.sl_findrow_byvalue(sl_kbs1,fk_key,"docref")==-1)
			{
				app.g.sqllist_kv_appendfilter(sl_kbs1, fk_key, "docref");		
			}
		}
		sl_kbs1.Refresh();
		sl_kbs1.SetRowSelected(0);
		sl_kbs1_OnItemSelected(0);
		return;
    });
}

						TabChanges1_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here

	if(nTab==0)
	{
		//-- only load list once (will keep trying to load records each time you click if there are no linked recs)
		if (sl_rfccause.rowCount() < 1) 
		{
			app.g.sl_load_call_masters(sl_rfccause, _swdoc.OC().callref , "RFC-CPROBLEM") ;
		}
	}else
	{
		if (sl_rels1.rowCount() < 1) 
		{
			app.g.sl_load_call_masters(sl_rels1, _swdoc.OC().callref , "RELEASE-CPROBLEM") ;
		}		
	}

}

