//-- given an analyst object (from app.global.js) load values for either monitored / raised by analyst
//-- for the rfc
function set_rfc_analyst(oAnalyst,boolMonitoredBy)
{
	if(!oAnalyst) 
	{
		//-- invalid so clear fields
		oAnalyst = app.g.get_blank_analyst();
	}
	
	if(boolMonitoredBy)
	{
		_swdoc.RFC_EXT().monitored_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().monitored_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().monitored_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().monitored_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().monitored_by_email = oAnalyst.email;	
	}
	else
	{
		_swdoc.RFC_EXT().raised_by_analystid = oAnalyst.id;
		_swdoc.RFC_EXT().raised_by_name = oAnalyst.name;
		_swdoc.RFC_EXT().raised_by_group = oAnalyst.supportgroup;
		_swdoc.RFC_EXT().raised_by_telephone =  oAnalyst.telephone;
		_swdoc.RFC_EXT().raised_by_email = oAnalyst.email;
	}
	_swdoc.UpdateFormFromData();	
}




//--
//-- FUNCTIONS TO ASSOCIATE CALL RECORDS AND CIS TO RELEASE

//--
//-- given a list of incident refs link them to release
function associate_incidents_to_release(strIncRefs)
{
	var strFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_incs1,'callref',",");
	if (strFilter != "") 
	{
		strFilter += "," + strIncRefs;
	}
	else
	{
		strFilter = strIncRefs;
	}
	app._slf(_swdoc.EF().sl_incs1, "_kv=" + strFilter);
	_swdoc.EF().sl_incs1.SetRowSelected(0, true, false);
	_swdoc.EF().sl_incs1.Refresh();
	
}

//--
//-- given a list of error refs link them to release
function associate_errors_to_release(strErrorRefs)
{
	var strFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_kes1,'callref',",");
	if (strFilter != "") 
	{
		strFilter += "," + strErrorRefs;
	}
	else
	{
		strFilter = strErrorRefs;
	}
	app._slf(_swdoc.EF().sl_kes1,"_kv=" + strFilter);
	_swdoc.EF().sl_kes1.SetRowSelected(0, true, false);
	_swdoc.EF().sl_kes1.Refresh();

	//-- check if we should add ke incidents to the release
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyErrorIncidents")==1)
	{
		//-- get error incident records
		var strLinkIncidents = app.g.get_slave_calls(strErrorRefs,"PROBLEM-INCIDENT");
		if(strLinkIncidents!="0")
		{
 			_swdoc.associate_incidents_to_release(strLinkIncidents);
		}
	}

	//-- Now check if we should link ke cause ci items to the release
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyErrorCauseCIs")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkedCIs = app.cmdb.get_call_cis(strErrorRefs , "PROBLEM-CAUSE", "");
		_swdoc.associate_cis_to_release(strLinkedCIs);
	}

}

//--
//-- given a list of rfc refs link them to release
function associate_rfcs_to_release(strRFCRefs)
{
	var strFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_rels1,'callref',",");
	if (strFilter != "") 
	{
		strFilter += "," + strRFCRefs;
	}
	else
	{
		strFilter = strRFCRefs;
	}
	app.g.sl_filter(_swdoc.EF().sl_rels1,"_kv=" + strFilter,null,0);
	_swdoc.EF().sl_rels1.Refresh();
	
	//-- check if we should add rfc incidents to the release
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCIncidents")==1)
	{
		//-- get rfc incident records
		var strLinkIncidents = app.g.get_slave_calls(strRFCRefs,"RFC-INCIDENT");
		if(strLinkIncidents!="0")
		{
 			_swdoc.associate_incidents_to_release(strLinkIncidents);
		}
	}

	//-- check if we should add rfc incidents to the release
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCErrors")==1)
	{
		//-- get rfc error records
		var strLinkErrors = app.g.get_slave_calls(strRFCRefs,"RFC-ERROR");
		if(strLinkErrors!="0")
		{
 			_swdoc.associate_errors_to_release(strLinkErrors);
		}
	}

	//-- Now check if we should link ke cause ci items to the release
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCChangeCIs")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkedCIs = app.cmdb.get_call_cis(strRFCRefs , "RFC-CAUSE", "");
		_swdoc.associate_cis_to_release(strLinkedCIs,_swdoc.EF().sl_configitems1,"CK_CONFIG_TYPE!='ME->SERVICE'");
	}
}

//--
//-- given a list of rfc refs link them to release
function associate_srs_to_release(strSRRefs)
{
	var strFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_srs,'callref',",");
	if (strFilter != "") 
	{
		strFilter += "," + strSRRefs;
	}
	else
	{
		strFilter = strSRRefs;
	}
	app.g.sl_filter(_swdoc.EF().sl_srs,"_kv=" + strFilter,null,0);
	_swdoc.EF().sl_srs.Refresh();
}

//--
//-- given a list of ci item ids link them the to release items
function associate_cis_to_release(strCiIDs, strSqlList,strDataFilter)
{
 	if(strSqlList==undefined || strSqlList=="") strSqlList=_swdoc.EF().sl_configitems1;
 	if(strDataFilter==undefined) strDataFilter="";
 	  
	var strFilter = app.g.sl_get_valuesdel(strSqlList,"pk_auto_id",",");
	if (strFilter != "") 
	{
		strFilter += "," + strCiIDs;
	}
	else
	{
		strFilter = strCiIDs;
	}
	
	// -- SG, 02/07/2012, F0103324
	// -- RJC, move to app.global.js
	// Build array of CI's that have the Bespoke Terms field populated
	app.bespokeTermsProcessing(strFilter);
	// End of array building
	if(strDataFilter!="") app._slf(strSqlList, "_kv=" + strFilter + "&_filter=" + strDataFilter);
	else app._slf(strSqlList, "_kv=" + strFilter);
	strSqlList.SetRowSelected(0,true,false);
	strSqlList.Refresh();
}

//--
//-- given a list of ci item ids link them the to release items
function associate_services_to_release(strCiIDs)
{
   
	var strFilter = app.g.sl_get_valuesdel(_swdoc.EF().sl_services,"pk_auto_id",",");
	if (strFilter != "") 
	{
		strFilter += "," + strCiIDs;
	}
	else
	{
		strFilter = strCiIDs;
	}

	app._slf(_swdoc.EF().sl_services, "_kv=" + strFilter);
	_swdoc.EF().sl_services.SetRowSelected(0,true,false);
	_swdoc.EF().sl_services.Refresh();
	// -- SG, 02/07/2012, F0103324
	// Build array of CI's that have the Bespoke Terms field populated
	app.bespokeTermsProcessing(strFilter);
}

//-- FUNCTIONS TO REMOVE CALL AND CI ASSOCIATION FROM RELEASE

//-- generic function to remove release related callrefs
function remove_release_relatedcalls(strRemoveCallrefs, sl_removefrom)
{
	var arrRemoveRefs = strRemoveCallrefs.split(",");
	for(var y=0;y<arrRemoveRefs.length;y++)
	{
		app.g.sl_delrow_byvalue(sl_removefrom,arrRemoveRefs[y],"callref", null,0);	
		sl_removefrom.SetRowSelected(0,true,false);
		sl_removefrom.Refresh();
	}
}

//-- function to remove related release cis
function remove_release_cis(strRemoveCis)
{
	var arrRemoveCis = strRemoveCis.split(",");
	for(var y=0;y<arrRemoveCis.length;y++)
	{
		app.g.sl_delrow_byvalue(_swdoc.EF().sl_configitems1,arrRemoveCis[y],"pk_auto_id",null,0);
		_swdoc.EF().sl_configitems1.SetRowSelected(0,true,false);
		_swdoc.EF().sl_configitems1.Refresh();
	}
}

//-- function to remove related release cis
function remove_release_services(strRemoveCis)
{
	var arrRemoveCis = strRemoveCis.split(",");
	for(var y=0;y<arrRemoveCis.length;y++)
	{
		app.g.sl_delrow_byvalue(_swdoc.EF().sl_services,arrRemoveCis[y],"pk_auto_id",null,0);
		_swdoc.EF().sl_services.SetRowSelected(0,true,false);
		_swdoc.EF().sl_services.Refresh();
	}
}
//-- remove incidents from release
function remove_release_incidents(strRemoveRefs)
{
	_swdoc.remove_release_relatedcalls(strRemoveRefs, _swdoc.EF().sl_incs1);
	
}

//-- remove errors from release
function remove_release_errors(strRemoveRefs)
{
	_swdoc.remove_release_relatedcalls(strRemoveRefs, _swdoc.EF().sl_kes1);
	_swdoc.EF().sl_kes1.Refresh();
	//-- if auto assoc incidents then check if we should remove
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyErrorIncidents")==1)
	{
		//--
		//-- get incident refs for the errors we are removing - if any are linked to rfc prompt user to remove them
		var strLinkedINCs = app.g.get_slave_calls(strRemoveRefs , "PROBLEM-INCIDENT", "");
		if(strLinkedINCs=="0")return;

		if(confirm("There may be associated incident records, that are linked to the known errors you are removing. Do you want to remove these incidents as well?"))
		{
			_swdoc.remove_release_incidents(strLinkedINCs);
		}
	}
	
	//-- if auto assoc cis check if we should remove
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyErrorCauseCIs")==1)	
	{
		var array_RemoveCIS = new Array();
		var strLinkedCauseCIs = app.cmdb.get_call_cis(strRemoveRefs , "PROBLEM-CAUSE", "");
		if(strLinkedCauseCIs=="0")return;

		if(confirm("There may be associated relase configuration items, that are linked to the known errors you are removing. Do you want to remove these items as well?"))
		{
			_swdoc.remove_release_cis(strLinkedCauseCIs);			
		}
	}	
}

//- -remove rfcs from release
function remove_release_rfcs(strRemoveRefs)
{
	_swdoc.remove_release_relatedcalls(strRemoveRefs, _swdoc.EF().sl_rels1);
	_swdoc.EF().sl_rels1.Refresh();
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCIncidents")==1)
	{
		//--
		//-- get incident refs for the rfcs we are removing - if any are linked to rfc prompt user to remove them
		var strLinkedINCs = app.g.get_slave_calls(strRemoveRefs , "RFC-INCIDENT", "");
		if(strLinkedINCs=="0")return;

		if(confirm("There may be associated incident records, that are linked to the change requests you are removing. Do you want to remove these incidents as well?"))
		{
			_swdoc.remove_release_incidents(strLinkedINCs);
		}
	}
	
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCErrors")==1)
	{
		//--
		//-- get incident refs for the rfcs we are removing - if any are linked to rfc prompt user to remove them
		var strLinkedErrors = app.g.get_slave_calls(strRemoveRefs , "RFC-ERROR", "");
		if(strLinkedErrors=="0")return;

		if(confirm("There may be associated known errors records, that are linked to the change requests you are removing. Do you want to remove these known errors as well?"))
		{
			_swdoc.remove_release_errors(strLinkedErrors);
		}
	}	
	
	//-- if auto assoc cis check if we should remove
	if(dd.GetGlobalParamAsNumber("Application Settings/Release/CopyRFCChangeCIs")==1)	
	{
		var array_RemoveCIS = new Array();
		var strLinkedCauseCIs = app.cmdb.get_call_cis(strRemoveRefs , "RFC-CAUSE", "");
		if(strLinkedCauseCIs=="0")return;

		if(confirm("There may be associated relase configuration items, that are linked to the change requests you are removing. Do you want to remove these items as well?"))
		{
			_swdoc.remove_release_cis(strLinkedCauseCIs);			
		}
	}	
	
}


//- -remove rfcs from release
function remove_release_srs(strRemoveRefs)
{
	_swdoc.remove_release_relatedcalls(strRemoveRefs, _swdoc.EF().sl_srs);
}


function filter_impact_customers(oForm)
{
	//-- get list of customers impacted by these incidents and refresh cust impact list
	var strCustFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'cust_id',",","'",true);
	if(strCustFilter=="")strCustFilter="''";
	
	//-- get customers that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,'pk_auto_id',",");	
	var strCustIidsFromImpactCIs = app.cmdb.get_ci_customer_ids(strImpactedCIs,true);
	if(strCustIidsFromImpactCIs!="")
	{
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=strCustIidsFromImpactCIs;	
	}

	//-- get customers affected by the associated kes
	var strKeCallrefs = app.g.sl_get_valuesdel(oForm.sl_kes1,'callref',",");
	if(strKeCallrefs=="")strKeCallrefs="0";
	var strCustIidsFromImpactKes = app.itsm.get_problem_impact_customerids(strKeCallrefs,true);
	if(strCustIidsFromImpactKes!="")
	{
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=strCustIidsFromImpactKes;	
	}

	//-- get customers affected by the associated rfcs
	var strRfcCallrefs = app.g.sl_get_valuesdel(oForm.sl_rels1,'callref',",");
	if(strRfcCallrefs=="")strRfcCallrefs="0";
	var strCustIdsFromImpactRfcs = app.itsm.get_rfc_impact_customerids(strRfcCallrefs,true);
	if(strCustIidsFromImpactKes!="")
	{
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=strCustIdsFromImpactRfcs;
	}

	app._slf(oForm.sl_customerimpact, "_kv=" + strCustFilter);
	oForm.sl_customerimpact.Refresh();			
}

function filter_impact_orgs(oForm)
{
	//-- get list of orgs impacted by impacted customer
	var strOrgFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'fk_company_id',",","'",true);
	
	//-- get orgs that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,'pk_auto_id',",");	
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strImpactedCIs,true);
	if(strOrgIDSFromCIs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIDSFromCIs;
	}

	//-- get orgs affected by the associated kes
	var strKeCallrefs = app.g.sl_get_valuesdel(oForm.sl_kes1,'callref',",");
	if(strKeCallrefs=="")strKeCallrefs="0";
	var strOrgIdsFromImpactKes = app.itsm.get_problem_impact_orgids(strKeCallrefs,true);
	if(strOrgIdsFromImpactKes!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIdsFromImpactKes;
	}

	//-- get orgs affected by the associated rfcs
	var strRfcCallrefs = app.g.sl_get_valuesdel(oForm.sl_rels1,'callref',",");
	if(strRfcCallrefs=="")strRfcCallrefs="0";
	var strOrgidsFromImpactRfcs = app.itsm.get_rfc_impact_orgids(strRfcCallrefs,true);
	if(strOrgidsFromImpactRfcs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgidsFromImpactRfcs;
	}
	
	app._slf(oForm.sl_orgimpact, "_kv=" + strOrgFilter);
	oForm.sl_incs1.Refresh();
	oForm.sl_orgimpact.Refresh();	
}

function search_for_cis(funcCallback)
{
	app.cmdb.search_ci(true,"",function(sResults)
	{
		if (sResults.selectedkeys=="")
		{
			if ((funcCallback != undefined) && (funcCallback != null))
			{
				funcCallback();
			}
		}
		else
		{
			_swdoc.associate_cis_to_release(sResults.selectedkeys);
			app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
			if ((funcCallback != undefined) && (funcCallback != null))
			{
				funcCallback();
			}
		}
	});
}

function search_for_services()
{
	var strURL = "searchmode=1&multiselect=1&canreset=1";
	app.g.popup("manage_portfolio", strURL, function(sResults)
	{
		if(!sResults) return;
		if (sResults.document.strSelectedKeys=="") return; 
	   
   		_swdoc.associate_services_to_release(sResults.document.strSelectedKeys);
   		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	});   	

 	//-- nwj - 15.07.2008 - check ci contract
 	//--	   12.05.2009 - remove check as release does not have an associated org or customer	
	//app.cfg.process_call_contract_check(sResults.selectedkeys, true,_swdoc);   	
}


//ITSM MEROPE

//-- check if there is alternative slad settings based on the customers department
function check_customer_department_slad(strSLADID, strDepartment)
{
	if(strDepartment=="")
		return;
	var strParams = "fk_slad_id=" +app.pfu(strSLADID)+ "&fk_dept=" +app.pfu(strDepartment);
 	var oRecSLD = app.g.get_sqrecordset("call/incident/get_dept_sla",strParams);
	if(oRecSLD.Fetch())
	{
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD,"chargeto_cc");
		//-- set oc values
		if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
		_swdoc.OC().priority = oRecSLD.fk_sla;			
	}
}


//-- load sld info and settings
//-- load sld info and settings
function handle_set_sladef(strNewSLD, boolValidate, boolOnResolve)
{
	if(_swdoc.boolSLAEnforced)
	{
		_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
		_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
		_swdoc.UpdateFormFromData();
		MessageBox("SLA is being enforced from the process.");
		return;
	}

	if(boolValidate==undefined)
		boolValidate==false;
	var strDisplaySLD = strNewSLD;

	var strUseSLD = strNewSLD;
	var boolRelative = false;
	var strRelative = "";

	//-- get actual SLD ID if getting from related record
	switch(strNewSLD)
	{
		case "[Use Customer SLA]":
			_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
			_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
			_swdoc.OC().priority =_swdoc.strCurrentPriority;
			_swdoc.UpdateFormFromData();
			MessageBox("No customer can be set for this request type. The Customer SLA cannot be used.",16);
			return;
			break;
		case "[Use Profile SLA]":
			if(_swdoc.OC().probcode!="")
			{
				var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
				var oRecSLD = app.g.get_record("itsmsp_slad", oRec.itsm_sla);
				if(oRecSLD)
				{
					strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
					boolRelative = true;
					strRelative = "[Use Profile Priority]";
					//return false;
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Profile SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No profile has been set for this request. The Profile SLA cannot be used.",16);
				return false;
			}
			break;
		case "[Use Charge Centre SLA]":
			if(_swdoc.OC().costcenter!="")
			{
				var strCCUse = _swdoc.OC().costcenter;
				if(strCCUse=="")
				{
					_swdoc.OC().itsm_sladef = 0;
					_swdoc.OC().itsm_slaname = "[Use Charge Centre SLA]";
					_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
					_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
					_swdoc.UpdateFormFromData();
					if(boolValidate)
						MessageBox("No charge centre has been set for this request. The charge centre SLA cannot be used.");
					return false;

				}
				else
				{
                    //-- 992268 Add the below line
					_swdoc.OC().costcenter = strCCUse;
					var oRec = app.g.get_record("costcent", strCCUse);
					var oRecSLD = app.g.get_record("itsmsp_slad", oRec.itsm_sla);
					if(oRecSLD)
					{
						strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
						boolRelative = true;
						strRelative = "[Use Charge Centre Priority]";
						//return false;
					}
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Charge Centre SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No charge centre has been set for this request. The charge centre SLA cannot be used.");
				return false;
			}
			break;
		case "[Use Process SLA]":
			if(bpm_workflow.pk_workflow_id!="")
			{
				if(bpm_workflow.fk_default_sla_new=="")
				{
					_swdoc.OC().itsm_sladef = _swdoc.strCurrentSLADef;
					_swdoc.OC().itsm_slaname = _swdoc.strCurrentSLAName;
					_swdoc.UpdateFormFromData();
					MessageBox("No SLA has been set for the requests Business Process. The Process SLA cannot be used.");
					return false;
				}
				else
				{
					var strSLA = bpm_workflow.fk_default_sla_name;
					if(strSLA.indexOf("[Use")>-1 && strSLA.indexOf("SLA]")>-1)
					{
						_swdoc.handle_set_sladef(bpm_workflow.fk_default_sla_name);
						return true;
					}
					else
					{
						//var oRec = app.g.get_record("costcent", strCCUse);
						var oRecSLD = app.g.get_record("itsmsp_slad", bpm_workflow.fk_default_sla_new);
						if(oRecSLD)
						{
							strUseSLD =oRecSLD.slad_id;// _swdoc.handle_set_sladef(oRecSLD.slad_id,false);
							boolRelative = true;
							strRelative = "[Use Process Priority]";
							//return false;
						}
					}
				}
			}
			else
			{
				_swdoc.OC().itsm_sladef = 0;
				_swdoc.OC().itsm_slaname = "[Use Process SLA]";
				_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
				_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No process has been set for this request. The Process SLA cannot be used.");
				return false;
			}
			break;	}

  	var oRecSLD = app.g.get_sqrecordset("general/get_itsm_sla","kv="+strUseSLD);
	if(oRecSLD.Fetch())
	{
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD,"chargeto_cc");
		switch(strNewCC)
		{
			case "[Use Department Charge Centre]":
				strNewCC = oCust().department;
				break;
			case "[Use Customer Charge Centre]":
				strNewCC = oCust().costcenter;
				break;		
			default:
				break;
		}
		//-- eof cc option
			
		//-- set oc values
		if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
		_swdoc.OC().itsm_sladef = 	app.g.get_field(oRecSLD,"pk_slad_id");
		_swdoc.OC().itsm_slaname = app.g.get_field(oRecSLD,"slad_id");
		_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
		_swdoc.strCurrentSLADef = _swdoc.OC().itsm_sladef;
		
		var strFKSsla = app.g.get_field(oRecSLD,"fk_ssla");
		if(boolRelative)
			_swdoc.handle_set_priority(strRelative);
		else
			_swdoc.handle_set_priority(strFKSsla);
				
		app._een(_swdoc.MF().lbl_oc_priority, true);
		app._een(_swdoc.MF().lb_oc_sla, true);
		_swdoc.UpdateFormFromData();
	}
	else
	{
		MessageBox("Failed to retrieve service level definition record for '" + strDisplaySLD + "'. Please contact your Supportworks Administrator");
		return false;
	}
	return true;
}


function handle_set_priority(strValue)
{
	if(strValue=="[Use SLA Default Priority]")
	{
		var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.OC().itsm_sladef);
		if(oRecSLD)
		{	
			_swdoc.OC().priority = oRecSLD.fk_ssla;
			_swdoc.strCurrentPriority = _swdoc.OC().priority;
			_swdoc.UpdateFormFromData();
		}
		return false;
	}	
	else if(strValue=="[Use Customer Priority]")
	{
		if(oCust().keysearch!="")
		{
			if(oCust().sld==_swdoc.OC().itsm_sladef)
			{
				if(oCust().priority=="[Use SLA Default Priority]")
					_swdoc.handle_set_priority("[Use SLA Default Priority]");
				else
				{
					_swdoc.OC().priority = oCust().priority;
					_swdoc.UpdateFormFromData();
				}
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The customer SLA is not the same as the currently selected SLA. The Customer priority cannot be used.");
			}
		}
		else
		{
				if(_swdoc.OC().itsm_slaname=="[Use Customer SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("No customer selected. The Customer priority cannot be used.");
				}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Profile Priority]")
	{
		if(_swdoc.OC().probcode!="")
		{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Profile SLA is not the same as the currently selected SLA. The Profile priority cannot be used.");
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Profile SLA]")
			{
				_swdoc.OC().priority="[Use Profile Priority]";
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No profile has been set for this request. The Profile priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Charge Centre Priority]")
	{
		if(_swdoc.OC().costcenter!="")
		{
			var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.priority);
			}
			else
			{
				if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("The cost center SLA is not the same as the currently selected SLA. The cost center priority cannot be used.");
				}
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
			{
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
				_swdoc.OC().priority = strValue;
				_swdoc.strCurrentPriority=_swdoc.OC().priority;
				_swdoc.UpdateFormFromData();
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No cost center has been set for this request. The cost center priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Process Priority]")
	{
		if(bpm_workflow.pk_workflow_id!="")
		{
			//var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(bpm_workflow.fk_default_sla_new==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(bpm_workflow.fk_default_sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Process SLA is not the same as the currently selected SLA. The Process priority cannot be used.");
			}
		}
		else
		{
			_swdoc.OC().priority = _swdoc.strCurrentPriority;
			_swdoc.UpdateFormFromData();
			MessageBox("No process has been set for this request. The process priority cannot be used.");
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else
	{
		if (_swdoc.boolReleaseSLASetManually)
		{
			_swdoc.OC().priority = strValue;
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		_swdoc.UpdateFormFromData();
		return false;
	}
}

						OnFormLoading(strFormName, strCallclass)
{
	//-- can they raise a record of this class
	if(app.itsm.can_create(strCallclass,true)==false) return false;
	return true;
}

						OnRecordValueChanged(strTable, strColumn, strValue, strOldValue)
{
	//-- NWJ - check to see if user has changed the itsm_opencall_rfc table field
	//-- to search for raised by and monitored by analyst - so it works like resolving a customer.
	if(strTable=="itsm_opencall_rfc")
	{
		var oAnalyst = false;
		if((strColumn.indexOf("raised_") == 0)||(strColumn.indexOf("monitored_") == 0))
		{
			//-- get db col name based on passed in col name
			var strDBCol="";
			//MessageBox(strColumn.split("_")[2])
			switch(strColumn.split("_")[2])
			{
				case "analystid":
					strDBCol="analystid";
					break;
				case "name":
					strDBCol="name";
					break;
				case "group":					
					strDBCol="supportgroup";
					break;
				case "telephone":
					strDBCol="contactb";
					break;
				case "email":
					strDBCol="contacte";
					break;
				default:
					MessageBox("The extended rfc column (" + strColumn + ") cannot be matched to swanalysts. please contact your Supportworks Administrator.");
					_swdoc.set_rfc_analyst(false,true);
					return;
					break;								
			}
		}
		if(strColumn.indexOf("raised_") == 0)
		{
			//-- auto search raised by
			app.g.resolve_analyst(strDBCol,strValue,function(oAnalyst)
			{
				if(oAnalyst) _swdoc.set_rfc_analyst(oAnalyst,false);
			});
		}
		else if (strColumn.indexOf("monitored_") == 0)
		{
			//-- auto search monitored by
			app.g.resolve_analyst(strDBCol,strValue,function(oAnalyst)
			{
				if(oAnalyst) _swdoc.set_rfc_analyst(oAnalyst,true);
			});			
		}
	}
	else if(strColumn=="probcode" && strTable == "opencall")
    {
		_swdoc.boolChangeProfile = true;
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
  		mainform.SetTimer(4, 100);
	}
	else if(strColumn=="priority" && strTable == "opencall")
    {
    	if(_swdoc.boolChangeProfile)
    	{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			var oSLA = app.g.get_record("itsmsp_slad", oRec.itsm_sla);

			_swdoc.handle_set_sladef(oSLA.slad_id);
			_swdoc.boolReleaseSLASetManually = true;
			_swdoc.handle_set_priority(oRec.sla);
 			_swdoc.UpdateFormFromData();
   		}
    }
    else if( (strColumn=="itsm_impact_level")||(strColumn=="itsm_urgency_level") )
    {
    	_swdoc.boolReleaseSLASetManually = true; //--107865
    	
    	if(_swdoc.OC().itsm_sladef!="")
    	{
    		//-- if use incident assessment over ci
    		//if(dd.GetGlobalParamAsNumber("Application Settings/Incident/UseCIAssessmentLevels")!=1)
    		//{
    			var strPriority = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
    			if(strPriority!="")
   					_swdoc.handle_set_priority(strPriority);
    			_swdoc.UpdateFormFromData();
    		//}
    	}
    }
    else if (strColumn=="fk_company_id"||strColumn=="site"||strColumn=="fk_dept_code"||strColumn=="costcenter"||strColumn=="pk_workflow_id")
	{
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}

    

}

						OnValidate()
{
	itsm_opencall_rfc.dow_note = updatedb.updatetxt;

	//-- nwj - hide confirmation if we have no customer
	app.g.bPromptToOpenCallDetails = true;
	app.g.bDoNotShowLogCallConfirmation = true;


	//-- app.itsm 2.4.0 - check if need mandatory ci
	if(bpm_workflow.flg_requireci==1)
	{
		if(_swdoc.EF().sl_configitems1.rowCount() < 1)
		{
			if(_swdoc.EF().sl_services.rowCount() < 1)
			{
				MessageBox("Before logging this Release request you have to associate at least one release configuration item or service");
				return VALIDATE_FAIL;
			}
		}
	}

	//-- 108840 : Check if we have required number of CIs 
	var strCallType = "Release";
	var intCurCINo = _swdoc.EF().sl_configitems1.rowCount() + _swdoc.EF().sl_services.rowCount();
	if(!app.itsm.mandatory_ci_check(strCallType, intCurCINo))
	{
		var intReqCINo = app.oSettings.get_setting("int" + strCallType + "OnLoggingRequiredCINo");
		var strPlural = "";
		if (intReqCINo > 1)
		{
			strPlural= "s";
		}
		MessageBox("Before logging this " + strCallType + " request you have to associate at least " + intReqCINo + " release configuration item" + strPlural + " or service" + strPlural + ".");
		return VALIDATE_FAIL;
	}

	//-- check that planned dates dont conflict / prompt user to confirm
	var strMessage = "";
	var strCIids = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,"pk_auto_id",",","");
	
	//MessageBox(_swdoc.OC().itsm_schedstartx + " : " + _swdoc.MF().tb_schedstart.text)
	
	//-- get schedule for ci or cis
	/*var array_schedule = app.cmdb.check_release_schedule(strCIids, _swdoc.MF().tb_schedstart.value, _swdoc.MF().tb_schedend.value);
	for(intCI in array_schedule)
	{
		//-- for each ci in that has a conflicting schedule get callref (note ci may have more than one conflicting schedule)
		for(intCallref in array_schedule[intCI])
		{
			var arrayInfo 	 = array_schedule[intCI][intCallref].split(",");
			var intRow = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems1,intCI,"pk_auto_id");
			var conflictItem = _swdoc.EF().sl_configitems1.GetItemText(intRow, "ck_config_item");
			var startDate = app.g.convert_epochdate(arrayInfo[0]);
			var endDate = app.g.convert_epochdate(arrayInfo[1]);			
			
			strMessage += "Change Item (" + conflictItem + ") is already scheduled for a change (" + app.g.pad_callref(intCallref) + ") between the dates of " + startDate + " and " + endDate + "\n";
		}
	}*/
	if(strMessage!="")
	{
		strMessage = "The following items are already scheduled for release during the schedule dates your provided. Do you still wish to submit this request? \n \n" + strMessage;
		if(confirm(strMessage))
		{
		
		}
		else
		{
			return VALIDATE_FAIL;
		}
	}
	
	return VALIDATE_DEFAULT;
}

						OnRecordResolved(strTable)
{
	if(IsObjectDefined("mainform") && (strTable=="bpm_workflow"))
	{
		var boolSet = true;
		var boolEnable = true;

		if(bpm_workflow.fk_default_sla_new>0)
		{
			var boolSLA = bpm_workflow.flg_sla_apply>0;
			if(boolSLA)
			{
				var boolSet = true;
				if(_swdoc.boolReleaseSLASetManually)
				{
					var boolOverride = confirm("It is recommended to use SLA set against the process.\n\n\Would you like to set the SLA to the process value?");
					if(!boolOverride)
						boolSet = false;
				}
				
				if(boolSet)
				{
					if(bpm_workflow.flg_sla_enforce==1)
						boolEnable = false;
				}
			}
			else
			{
				boolSet = false;
			}
		}
		else
		{
			boolSet = false;
		}
		
		if(boolSet)
		{
			//-- F0107934
			_swdoc.boolSLAEnforced = false;
			_swdoc.handle_set_sladef(bpm_workflow.fk_default_sla_name,false,true);
			var boolTempSLASetManually = _swdoc.boolReleaseSLASetManually;
			_swdoc.boolReleaseSLASetManually = true;
			_swdoc.handle_set_priority(bpm_workflow.fk_default_sla);
			_swdoc.boolReleaseSLASetManually = boolTempSLASetManually;
			_swdoc.UpdateFormFromData();
			//-- END
			if(!boolEnable)
			{
				app._een(mainform.lb_oc_slad1,false);
				app._een(mainform.label_itsm_sladef,false);
				_swdoc.boolSLAEnforced = true;
			}
		}
		
		if(boolEnable)
		{
			app._een(mainform.lb_oc_slad1,true);
			app._een(mainform.label_itsm_sladef,true);
			_swdoc.boolSLAEnforced = false;
		}
		app.g.process_bpm_form_attribute_check(_swdoc,bpm_workflow.pk_workflow_id);
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}
}

						OnLogCall()
{
	// TODO: Add your event handler code here
	var strDefaultGroup = dd.GetGlobalParamAsString("Application Settings/Release/DefaultAssignmentGroup");
	var strDefaultAnalyst = dd.GetGlobalParamAsString("Application Settings/Release/DefaultAssignmentAnalyst");
	if(strDefaultGroup!="")
	{
		_swdoc.OC().suppgroup = strDefaultGroup;
		_swdoc.OC().owner = strDefaultAnalyst;
	}
	
	_swdoc.OC().custom_i3=0;
	
	
	return true;
}

						OnLogAndAssignCall(strGroupID, strAnalystID)
{
	// TODO: Add your event handler code here
	// TODO: Add your event handler code here
	_swdoc.OC().suppgroup = strGroupID;
	var strDefaultGroup = dd.GetGlobalParamAsString("Application Settings/Release/DefaultAssignmentGroup");
	if(strDefaultGroup!="")
	{
		if(strDefaultGroup!=_swdoc.OC().suppgroup)
		{
			var strDefaultAnalyst = dd.GetGlobalParamAsString("Application Settings/Release/DefaultAssignmentAnalyst");
			var strDisplayAnalyst = ":"+strDefaultAnalyst;
			if(strDefaultAnalyst=="")
				strDisplayAnalyst = "";
			var boolConfirm = confirm('The default assignment for change records is '+strDefaultGroup+strDisplayAnalyst+'. Do you want to use the default?');
			if(boolConfirm)
			{
				_swdoc.OC().suppgroup = strDefaultGroup;
				_swdoc.OC().owner = strDefaultAnalyst;
			}
		}
	}
	_swdoc.OC().custom_i3=0;

	return true;
}

						OnPreValidate()
{
    var boolResolve = false;
	if(	_swdoc.OC().itsm_slaname=="[Use Customer SLA]"||_swdoc.OC().itsm_slaname=="[Use Profile SLA]" || _swdoc.OC().itsm_slaname=="[Use Workflow SLA]" || _swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
		boolResolve = true;
	if(boolResolve)
	{
		var boolOK = _swdoc.handle_set_sladef(_swdoc.OC().itsm_slaname,true);
		if(!boolOK)
			return VALIDATE_FAIL;
	}

	if (!app.g.resolve_default_priority(_swdoc.OC().priority,_swdoc,"Release Request")){
	return VALIDATE_FAIL;
	}

	return VALIDATE_DEFAULT;
}

						OnResolveRecord(strTable, strColumn, strValue)
{
	// TODO: Add your event handler code here

	if(IsObjectDefined('mainform')&&strTable=='bpm_workflow' && strValue=='')
	{
		app._een(mainform.label_itsm_sladef,true);
		app._een(mainform.lb_oc_slad1,true);
		app.g.reset_bpm_form_attribute_check(_swdoc);
	}
	return strValue;
}

						OnFormClosing()
{
	// TODO: Add your event handler code here
	app.strRAKbDocFilter="";

	return true;
}

						OnCallLoggedOK(nCallRef, dateRespondBy, dateFixBy, strCustName, strSla)
{
	// TODO: Add your event handler code here
		//-- NWJ - 12-02.2007 - after logging problem link its CIs and Incidents
    //-- nwj 
    //-- 15.03.2004
    var intCallRef = app.g.callref_unpad(nCallRef);

    //-- loop through causing CI and assign them to this call
	var strDiaryText = "The following Configuration Items are to be released.\n";
	var boolCIs = false;
    var olist = _swdoc.EF().sl_configitems1;
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolCIs =true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'RELEASE'))
		{
			strDiaryText    += "Configuration Item (" + ci_item_key + "). \n";
		}
    }
   
    //-- if we have CI to add add it
    if (boolCIs)
    {
		var strActionSource = "Release Management";
		var strCode			= "Release Configuration Items";
		app.g.new_dairyevent(intCallRef,strDiaryText,strActionSource,strCode);    
    }

    //-- loop through causing CI and assign them to this call
	var strServiceDiaryText = "The following Services are to be released.\n";
	var boolServices = false;
    var olist = _swdoc.EF().sl_services;
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolServices =true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'RELEASE'))
		{
			strServiceDiaryText    += "Service (" + ci_item_key + "). \n";
		}
    }
   
    //-- if we have CI to add add it
    if (boolServices)
    {
		var strActionSource = "Release Management";
		var strCode			= "Release Services";
		app.g.new_dairyevent(intCallRef,strServiceDiaryText,strActionSource,strCode);    
    }

    // -- If we have a CI or Service, then check whether the first Stage requires Authorisations for Service Owner, Technical Owner and CI Owner
    if(boolCIs || boolServices)
    {
		var xmlmc = new XmlMethodCall();
		xmlmc.SetParam("in_callref", intCallRef);
		xmlmc.Invoke("vpme", "checkStageAuth");
	}
	
    //-- insert a relationship between rfc and incs
    var strIncidents = app.g.sl_get_valuesdel(_swdoc.EF().sl_incs1,0,",");
    if (strIncidents !="")
    {
    	var strText = "This release request has been associated with the following Incidents\n\n";
    	var arrIncidents = strIncidents.split(",");
    	for(var x=0; x < arrIncidents.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrIncidents[x],'RELEASE-INCIDENT'))
			{
				strText += "Incident (" + app.g.pad_callref(arrIncidents[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strIncText = "The release request (" + nCallRef + ") has been associated to this Incident";
				app.g.new_diaryevent(arrIncidents[x],strIncText,"Release Management","Release Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Release Management","Incident Association");
    }
   
   //-- insert a relationship between rfc and problem / ke
    var strProblems = app.g.sl_get_valuesdel(_swdoc.EF().sl_kes1,0,",");
    if (strProblems !="")
    {
    	var strText = "This release request has been associated with the following Known Errors\n\n";
    	var arrProblems = strProblems.split(",");
    	for(var x=0; x < arrProblems.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrProblems[x],'RELEASE-ERROR'))
			{
				strText += "Known Error (" + app.g.pad_callref(arrProblems[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strPrbText = "The release request (" + nCallRef + ") has been associated to this known error.";
				app.g.new_diaryevent(arrProblems[x],strPrbText,"Release Management","Release Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Release Management","Known Error Association");
    }

   //-- insert a relationship between rfc and rels
    var strChanges = app.g.sl_get_valuesdel(_swdoc.EF().sl_rels1,0,",");
    if (strChanges !="")
    {
    	var strText = "This release request has been associated with the following changes\n\n";
    	var arrChanges = strChanges.split(",");
    	for(var x=0; x < arrChanges.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrChanges[x],'RELEASE-RFC'))
			{
				strText += "Change (" + app.g.pad_callref(arrChanges[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strRelText = "The release request (" + nCallRef + ") has been associated to this change.";
				app.g.new_diaryevent(arrChanges[x],strRelText,"Release Management","Release Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Release Management","Change Association");
    }
    
       //-- insert a relationship between sr and rels
    var strSRs = app.g.sl_get_valuesdel(_swdoc.EF().sl_srs,0,",");
    if (strSRs !="")
    {
    	var strText = "This release request has been associated with the following service request\n\n";
    	var arrSRs = strSRs.split(",");
    	for(var x=0; x < arrSRs.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrSRs[x],'RELEASE-REQUEST'))
			{
				strText += "Service Request (" + app.g.pad_callref(arrSRs[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strRelText = "The release request (" + nCallRef + ") has been associated to this service request.";
				app.g.new_diaryevent(arrSRs[x],strRelText,"Release Management","Release Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,"Release Management","Service Request Association");
    }

	//-- insert a relationship between incs and doc
    if(!app.g.use_sw_knowledgebase())
	{
		if(app.strKnowledgebaseDefaultTool.toLowerCase()=="rightanswers")
		{
			var strDocs = app.g.sl_get_valuesdel(_swdoc.EF().sl_rakb,0,",");
    		if (strDocs !="")
    		{
    			var strText = "This release request has been associated with the following Knowledgebase Documents\n\n";
    			var arrDocs = strDocs.split(",");
    			for(var x=0; x < arrDocs.length; x++)
    			{
					//-- call app.g method to link problem to incident
					app.g.relate_call_to_rakb(arrDocs[x],intCallRef,'Used','Release',false);
					strText += "Knowledgebase Document (" + arrDocs[x] + ") associated\n";
				}			
				app.g.new_diaryevent(intCallRef ,strText,"Release Management","Knowledgebase Document Association");
    		}
		}
	}
	else
	{
    	var strDocs = app.g.sl_get_valuesdel(_swdoc.EF().sl_kbs1,0,",");
    	if (strDocs !="")
    	{
    		var strText = "This release request has been associated with the following Knowledgebase Document\n\n";
    		var arrDocs = strDocs.split(",");
    		for(var x=0; x < arrDocs.length; x++)
    		{
				//-- call app.g method to link release to kb doc
				app.oKB.relate_call_to_kb(arrDocs[x],intCallRef,'REL-KB');
				strText += "Knowledgebase Document (" + arrDocs[x] + ") associated\n";
			}			
			app.g.new_diaryevent(intCallRef ,strText,"Release Management","Knowledgebase Document Association");
    	}
   }
	//-- F0105032 - audit trail if incoming call	
	if(_swdoc._form.PreloadType==2)
	{
		if(_swdoc.OC().bpm_workflow_id!="")
		{
			var intEpochTime = app.global.GetCurrentEpocTime();
			var oAnalyst = app.g.get_record_from_syscache("swanalysts","analystid='"+app.g.pfs(app.session.analystId)+"'");
			var oGroup = app.g.get_record_from_syscache("swgroups","id='"+app.g.pfs(app.session.groupId)+"'");
			var oRS = app.g.get_recordset("select * from bpm_audit_fields where fk_workflow_id='"+app.g.pfs(_swdoc.OC().bpm_workflow_id)+"'","swdata");
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				var strTable = arrBinding[0];
				var strColName = arrBinding[1];
				
				var oldValue = "";
				if(strTable=="opencall")
					var oldValue = _swdoc.oOpencall[strColName];
				else
					var oldValue = _swdoc.oExtended[strColName];
				
				var newValue = _swdoc[strTable][strColName];
				if(app.g.dd_isnumeric(strTable, strColName))
				{
					if(newValue<0)
						newValue = app.g.fix_epoch(newValue);
					if(oldValue<0)
						oldValue = app.g.fix_epoch(oldValue);
				}
				if(oldValue!=newValue)
				{
					var strFieldDisplay = dd.tables[app.LC(strTable)].columns[strColName].DisplayName;
					var strFmtOldValue = app.g.dd_format(strTable, strColName, oldValue);
					var strFmtNewValue = app.g.dd_format(strTable, strColName, newValue);
					var oInsertTmp = new app.oXmlmcInsert('oc_audit');
					oInsertTmp.setfield('fk_callref',_swdoc.OC().callref);
					oInsertTmp.setfield('updatetimex',intEpochTime);
					oInsertTmp.setfield('updatedby',app.session.analystId);
					oInsertTmp.setfield('updatedbyname',oAnalyst.name);
					oInsertTmp.setfield('updatedbygroup',app.session.groupId);
					oInsertTmp.setfield('updatedbygroupname',oGroup.name);
					oInsertTmp.setfield('databinding',strBinding);
					oInsertTmp.setfield('bindingfield',arrBinding[1]);
					oInsertTmp.setfield('bindingtable',arrBinding[0]);
					oInsertTmp.setfield('fielddisplay',strFieldDisplay);
					oInsertTmp.setfield('oldvalue',oldValue);
					oInsertTmp.setfield('newvalue',newValue);
					oInsertTmp.setfield('fmtoldvalue',strFmtOldValue);
					oInsertTmp.setfield('fmtnewvalue',strFmtNewValue);
					oInsertTmp.execute();
				}
			}
		}
	}	//-- EOF F0105032 
	
	// F0111914
    // If Risk Assessment was completed (not catering for any settings at this point), load the selected options into cmn_rel_opencall_ila
	if (_swdoc.strRiskOptions != "")
	{
		app.g.relate_ila_options_to_call(intCallRef, "Release Risk", _swdoc.strRiskOptions);
	}
	
	
	
	
    // DTH: Create a history record for the selected Risk Assessment Options
	if (_swdoc.strRiskOptions != "")
	{	
		var xmlmc = new XmlMethodCall;

		// Set up our input parameters
		xmlmc.SetValue("analystid", app.session.analystId);
		xmlmc.SetValue("callref", parseInt(intCallRef));
		xmlmc.SetValue("weighting", _swdoc.OC().itsm_risk_weighting);
		xmlmc.SetValue("impact_level", _swdoc.OC().itsm_risk_level);
		xmlmc.SetValue("usage_area", "Release Risk");
		xmlmc.SetValue("ila_options", _swdoc.strRiskOptions);
		xmlmc.SetValue("create_diary", 0); // Not creating a Diary entry when logging a call
		xmlmc.SetValue("prev_impact_level", "");
		xmlmc.SetValue("prev_weighting", 0);
		
		// Invoke the method
		if(!xmlmc.Invoke("VPME", "ilaCreateCallHist"))
		{
			MessageBox(xmlmc.GetLastError());
		}
	}
	// F0111914 EOF
}

