						btn_logrfc_OnPressed()
{
    //-- NWJ - 17.03.2004
    //-- log new rfc

    MessageBox("RFC Logged")

    var hdcon = new HelpdeskSession;
    If(hdcon.Connect())
    {

        if(hdcon.BeginLogCall())
        {   
            // We send our transaction values one by one
            hdcon.SendText ("description", "Attached is my win.ini");

            if(hdcon.Commit())MessageBox("RFC Created");
        }

        // We have finished with the connection, close it.    
        hdcon.Close();
    } 
    
}

