						OnFormLoaded()
{
	
	//-- F0105032
	if(_swdoc._form.PreloadType==2)
	{
		_swdoc.oOpencall = app.g.get_sys_record("opencall", _swdoc.OC().callref);
		_swdoc.oExtended = app.g.get_record("itsm_opencall_rfc", _swdoc.OC().callref);
	}
	//-- EOF F0105032
	var oAnalyst = app.g.get_analyst_detail(app.session.analystId);
	_swdoc.set_rfc_analyst(oAnalyst,false);	
	
	// -- Load CI and Services that come through from another Call
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems1, _swdoc.OC().callref ,"RELEASE","cis_excludeservice");
	app.cmdb.sl_load_call_cis(_swdoc.EF().sl_services, _swdoc.OC().callref ,"","cis_servicesonly");

	//--
	//-- check if we are raising this release from a Change
	if(app.g.LOG_FROM_CALL!=null)
	{	
		app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems1, app.g.LOG_FROM_CALL.callref ,"RFC-CAUSE","cis_excludeservice");
		app.cmdb.sl_load_call_cis(_swdoc.EF().sl_services, app.g.LOG_FROM_CALL.callref ,"RFC-CAUSE","cis_servicesonly");
		
		//-- Associate CIs and Services
		var strCIs = app.cmdb.get_call_cis(app.g.LOG_FROM_CALL.callref,"RFC-CAUSE");
		_swdoc.associate_cis_to_release(strCIs,_swdoc.EF().sl_configitems1,"CK_CONFIG_TYPE!='ME->SERVICE'");  
		_swdoc.associate_cis_to_release(strCIs,_swdoc.EF().sl_services,"CK_CONFIG_TYPE='ME->SERVICE'");  
				
		//-- store incident record info (uses global params to determine what to store)
		if(app.g.LOG_FROM_CALL.callclass=="Incident")		
		{
			_swdoc.associate_incidents_to_release(app.g.LOG_FROM_CALL.callref);			
		}
		//-- store incident record info (uses global params to determine what to store)
		if(app.g.LOG_FROM_CALL.callclass=="Change Request")		
		{
			_swdoc.associate_rfcs_to_release(app.g.LOG_FROM_CALL.callref);			
		}

		//-- store incident record info (uses global params to determine what to store)
		if(app.g.LOG_FROM_CALL.callclass=="Known Error")		
		{
			_swdoc.associate_errors_to_release(app.g.LOG_FROM_CALL.callref);			
		}
		//-- store Service Request record info (uses global params to determine what to store)
		if(app.g.LOG_FROM_CALL.callclass=="Service Request")		
		{
			_swdoc.associate_srs_to_release(app.g.LOG_FROM_CALL.callref);
			if(app.g.LOG_CALL_FOR_PK_SERVICE)
			{
				app.g.sl_kv_appendfilter(_swdoc.EF().sl_services, app.g.LOG_CALL_FOR_PK_SERVICE,0);
				_swdoc.EF().sl_services.Refresh();
			}
		}
		//-- show customer impacted by the log from call
		if(app.g.LOG_FROM_CALL.cust_id!="")
		{
			_swdoc.filter_impact_customers(_swdoc.EF());
			_swdoc.ResolveRecord("userdb","keysearch", app.g.LOG_FROM_CALL.cust_id);
		}
		//-- copy over prob text
		updatedb.updatetxt = app.g.CALLINFO[app.g.LOG_FROM_CALL.callref].origupdatetxt
				
		//-- copy over prob code into recog prob code
		_swdoc.OC().probcode = app.g.LOG_FROM_CALL.probcode;
		//-- copy of cost center
		_swdoc.OC().costcenter = app.g.LOG_FROM_CALL.costcenter;
		
		//-- if there is a rfc type copy 
		if(app.g.LOG_FROM_CALL.itsm_rfc_type!="")
			_swdoc.OC().itsm_rfc_type 	= app.g.LOG_FROM_CALL.itsm_rfc_type;

		//-- impact / urgency / priority level 
		_swdoc.OC().itsm_impact_level 	= app.g.LOG_FROM_CALL.itsm_impact_level;
		_swdoc.OC().itsm_urgency_level	= app.g.LOG_FROM_CALL.itsm_urgency_level;
		//-- Do not inlude SLA from change
		//OC().priority			= app.g.LOG_FROM_CALL.priority;
		
		
		//-- update form
		_swdoc.UpdateFormFromData();
		
		//-- important - null record		
		app.g.LOG_FROM_CALL=null;
	}	
	
	//-- RJC - F0105434
	app.g.get_default_process(_swdoc,'Release Request',_swdoc.MF().fld_bpm_workflow,_swdoc.MF().lbl_bpm_workflow);
	//-- EOF - F0105434
	
	//-- F0094989
	if(_swdoc.OC().itsm_slaname.indexOf("[Use ")<0 && _swdoc.OC().itsm_slaname.indexOf(" SLA]")<0 )
	{
		if(_swdoc.OC().itsm_slaname!="")
		{
			var slarec = app.g.get_sqrecordset("general/get_itsm_sla","kv="+_swdoc.OC().itsm_slaname);
			if(slarec.Fetch())
			{
				var intSLAId = app.g.get_field(slarec,"slad_id");
				if(intSLAId!="")
					_swdoc.handle_set_sladef(intSLAId);
			}
		}
	}
	
	//ES - F0111914 - Add check for Assessment within Release Request
	boolRLAEnable = app.g.rla_releaselogform_enable();
	boolRLAMandatory = app.g.rla_releaselogform_mandatory();
	boolRLAAllowAlternation = app.g.rla_releaselogform_allow_alteration();

	app._een(_swdoc.MF().btn_risk_assess, boolRLAEnable); // enable the Assessment button based on setting
	if (boolRLAEnable && boolRLAMandatory)
	{
		app._een(_swdoc.MF().pl_risk_assessment, false); // disable selection of Risk until Assessment is completed
		app._ema(_swdoc.MF().pl_risk_assessment , true);
	}
	//ES - F0111914 - END
	
	_swdoc.strDefaultImpact = (_swdoc.OC().itsm_impact_level);
	_swdoc.strDefaultUrgency = (_swdoc.OC().itsm_urgency_level);
	_swdoc.strCurrentPriority = _swdoc.OC().priority;
	_swdoc.strCurrentSLAName = _swdoc.OC().itsm_slaname;
	
	//-- F0108825 : A newly logged call should not have resolve/close text
	//	         even when it has been copied from a resloved/closed call
	_swdoc.OC().resolve_text="";
	_swdoc.OC().close_text="";
	//	PM00143977 : values copied from parent call
	_swdoc.OC().lastactdatex="";	
	_swdoc.OC().lastactdate="";	
	_swdoc.OC().lastactby="";	
	_swdoc.OC().assigndatx="";	
	_swdoc.OC().withinresp="";	
	_swdoc.OC().resp_time="";	
	_swdoc.OC().slaresp="";	
	_swdoc.OC().assigndate="";	
	
	//-- NWJ - 28.02.2006
	//--      get any incoming cis (logged from ss)
	//app.cmdb.sl_load_call_cis(_swdoc.EF().sl_configitems1, _swdoc.OC().callref ,"INCOMING","")	
	//-- TK Set Summary Based on Email Subject
	if((_swdoc._form['emailsubject']) && (app.oSettings.get_setting("boolUseSubjectasSummary")=='True'))
	{
		_swdoc.OC().itsm_title = _swdoc._form['emailsubject'];
	}
	_swdoc.strDefaultPriority = _swdoc.MF().lb_oc_sla.value;
}

