						btn_risk_assess_OnPressed()
{
	// TODO: Add your event handler code here
	
	var strURL = "ila_options="+_swdoc.strRiskOptions+"&appcode="+app.itsm.get_session_param("dataset")+"&usage_area=Release Risk&mode=log form";
	app.OpenForm("EfImpactAssessmentEntry", strURL, true,function(oRes)
	{
	if (oRes.document.boolApplied)
		{
		_swdoc.strRiskOptions = oRes.document.strSelectedKeys;
		//OC().flg_ila_complete = 1;		
		_swdoc.OC().itsm_risk_weighting = oRes.document.intWeighting;
		_swdoc.OC().itsm_risk_level = oRes.document.strImpactLevel;
		if (boolRLAEnable && boolRLAMandatory || !boolRLAAllowAlternation)
			{	
			app._een(_swdoc.MF().pl_risk_assessment, false);
			}
		if (boolRLAEnable && boolRLAMandatory && boolRLAAllowAlternation)
			{
			app._een(_swdoc.MF().pl_risk_assessment, true); // Enable alteration of Risk Level
			}
		_swdoc.UpdateFormFromData();
		}
	});

}

