function filter_ke_impact(strKErefs)
{
	app.g.sl_kv_appendfilter(_swdoc.EF().sl_kes1, strKErefs,0);
	_swdoc.EF().sl_kes1.Refresh();
	//-- check if we should add ke incidents
	if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorIncidents")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkIncidents = app.g.get_slave_calls(strKErefs,"PROBLEM-INCIDENT");
		if(strLinkIncidents!="0")
		{
 			_swdoc.filter_inc_impact(strLinkIncidents);
		}
	}

	//-- Now check if we should link new ke ci items as being affect or cause (or both)
	if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorCauseCIs")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkedCIs = app.cmdb.get_call_cis(strKErefs , "PROBLEM-CAUSE", "");
		_swdoc.link_cause_cis_to_change(strLinkedCIs);
	}

	if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyErrorImpactCIs")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkedCIs = app.cmdb.get_call_cis(strKErefs , "PROBLEM-AFFECT", "");
		_swdoc.link_affect_cis_to_change(strLinkedCIs);
	}
}

//-- link incidents
function filter_inc_impact(strIncidentRefs)
{
	app.g.sl_kv_appendfilter(_swdoc.EF().sl_incs1,strIncidentRefs ,0);
	_swdoc.EF().sl_incs1.Refresh();	

	//-- Now check if we should link new ci items as being affect
	if(dd.GetGlobalParamAsNumber("Application Settings/Change/CopyIncidentCIs")==1)
	{
		//-- if dont have info get it otherwise use info we go above
		var strLinkedCIs = app.cmdb.get_call_cis(strIncidentRefs , "INCIDENT", "");
		_swdoc.link_affect_cis_to_change(strLinkedCIs);
	}

}

function filter_impact_customers(oForm)
{
	//-- get list of customers impacted by these incidents and refresh cust impact list
	var strCustFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'cust_id',",","'",true);
	
	var strCustIdsFromRFCs = app.g.sl_get_valuesdel(oForm.sl_rfcs,'cust_id',",","'",true);
	if(strCustIdsFromRFCs!="")
	{	
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=strCustIdsFromRFCs;
	}

	//-- get customers that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,'pk_auto_id',",");	
	var strCustIidsFromImpactCIs = app.cmdb.get_ci_customer_ids(strImpactedCIs,true);
	if(strCustIidsFromImpactCIs!="")
	{	
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=strCustIidsFromImpactCIs;	
	}

	if(_swdoc.oCust().keysearch!="")
	{	
		if(strCustFilter!="")strCustFilter+=",";
		strCustFilter+=_swdoc.oCust().keysearch;	
	}

	app._slf(oForm.sl_customerimpact, "_kv=" + strCustFilter);
	oForm.sl_customerimpact.Refresh();			
}

function filter_impact_orgs(oForm)
{
	//-- get list of orgs impacted by impacted customer
	var strOrgFilter = app.g.sl_get_valuesdel(oForm.sl_incs1,'fk_company_id',",","'",true);

	var strOrgIdsFromRFCs = app.g.sl_get_valuesdel(oForm.sl_rfcs,'fk_company_id',",","'",true);
	if(strOrgIdsFromRFCs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIdsFromRFCs;
	}
	
	//-- get orgs that use affected cis
	var strImpactedCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,'pk_auto_id',",");	
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strImpactedCIs,true);
	if(strOrgIDSFromCIs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIDSFromCIs;
	}


	//-- 2.4 include orgs that use change ci
	//-- get orgs that use change cis
	var strChangeCIs = app.g.sl_get_valuesdel(_swdoc.EF().sl_configitems1,'pk_auto_id',",");	
	var strOrgIDSFromCIs = app.cmdb.get_ci_company_ids(strChangeCIs,true);
	if(strOrgIDSFromCIs!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=strOrgIDSFromCIs;
	}

	if(_swdoc.OC().fk_company_id!="")
	{
		if(strOrgFilter!="")strOrgFilter+=",";
		strOrgFilter+=_swdoc.OC().fk_company_id;
	}
	
	//### change to call Org
	app._slf(oForm.sl_orgimpact, "_kv=" + strOrgFilter);
	oForm.sl_orgimpact.Refresh();	
}


function get_company_cis(strCompanyID)
{
	//###20120801 - rewrite to accommodate multiple organisations
	var strKeyValues = app.cfg.get_company_cikeys(strCompanyID,true,true);	

	var strParams = "fk_company_id="+app.pfu(strCompanyID);
    var oRS = app.g.get_sqrecordset("general/get_company_sites",strParams);
	while(oRS.Fetch())
	{
		if(strKeyValues!="")strKeyValues+=",";	
		var strSiteName = app.g.get_field(oRS,"site_name");
		strKeyValues+= app.cmdb.get_site_cis(strSiteName);
	}	
	
	if(strKeyValues=="")
		strKeyValues = "-1";
	_swdoc.multi_companyCIIDS[strCompanyID] = strKeyValues;

}


// remove any currently selected CI's that are not related to the currently selected organisation
function check_cis()
{
	var oList =_swdoc.EF().sl_configitems1;
	if(oList.rowCount() =="")
		return;
	var boolCIRemoved = false;
	//###20120801
	var strCompanyID = _swdoc.OC().fk_company_id;
	if(typeof _swdoc.multi_companyCIIDS[strCompanyID] === "undefined")
		_swdoc.get_company_cis(strCompanyID);
		
	var arrCIS = _swdoc.multi_companyCIIDS[strCompanyID].split(",");
	for (var x=oList.rowCount();x > -1;x--)
	{
		var strValue = oList.GetItemText(x,0);
		if(strValue !=""){
			if(app.in_array(arrCIS,strValue))
			{
				oList.SetRowSelected(x,false,true);
			}
			else
			{
				oList.SetRowSelected(x,true,true);
				boolCIRemoved = true;
			}
		}
	}
	_swdoc.EF().sl_configitems1.Refresh();
	_swdoc.EF().mbtn_cci_actions1_OnMenuItem('test',1);
	if(boolCIRemoved)
		MessageBox("CIs were removed from this call, as they were not associated with the currently selected Organisation");
}

// load the app.service information on selection
// checks that the app.service exists, and then loads preset informations for the app.service
function load_service_details(pkey)
{
	_swdoc.OC().request_qty = 1;
	oRec = app.g.get_record("sc_folio", pkey);
	if(!oRec)
	{
		_swdoc.reset_service(oRec);
		return;
	}

	if(_swdoc.MF().fld_service_subscribe.value == 0)
	{
		_swdoc.load_on_request(oRec);
	}else
	{
		_swdoc.load_on_subscription(oRec);
	}
	_swdoc.UpdateFormFromData();
	return true;
}

// reload the services availaable, taking in whether we are looking for requests or available services to subscribe to
function reload_services(boolSubscribed)
{

	var strFilter = "";
	//###20120607
	var CIIDS = "";
	if(boolSubscribed)
	{
		CIIDS = app.service.get_customer_services(_swdoc.oCust().keysearch, true, false, "SR");
	}
	else
	{
		CIIDS = app.service.get_customer_view_services(_swdoc.oCust().keysearch, true, false, "SR");
	}

	strFilter = "pk_auto_id="+CIIDS;
	if(boolSubscribed)
		strFilter += "&bls=1";
	else
		strFilter += "&bls=0";

	app._slf(_swdoc.MF().fld_fk_service, strFilter);
	_swdoc.load_service_details("-1");
}

// deselect the current app.service - when change of customer / organisation / app.service
function reset_service()
{
	_swdoc.OC().itsm_fk_service = 0;
	app._slf(_swdoc.MF().sl_component1, "fks=-1");
	app._slf(_swdoc.MF().sl_optional_component1, "fks=-1");
	_swdoc.MF().sl_component1.Refresh();
	_swdoc.MF().sl_optional_component1.Refresh();
	_swdoc.arrSLACost = "";
	_swdoc.arrSLAPrice = "";
	_swdoc.OC().request_qty = 1;
	app._een(_swdoc.MF().fld_req_qty,false);
	app._een(_swdoc.MF().lbl_req_qty,false);
	_swdoc.UpdateFormFromData();
}

// load any on request values defined in the app.service
function load_on_request(oRec)
{
	if(oRec.fk_cmdb_id!=undefined)
		opencall.itsm_fk_service = oRec.fk_cmdb_id;
	else
		return false;

	if(oRec.fk_df_onreq>0)
	{
		_swdoc.load_dataform(oRec.fk_df_onreq);	
	}

	_swdoc.get_service_slas(oRec.fk_cmdb_id,"SUBSCRIPTION",oRec.fk_default_sla);
	app._slf(_swdoc.MF().sl_component1, "fks="+oRec.fk_cmdb_id+"&bls=0");
	app._slf(_swdoc.MF().sl_optional_component1, "fks="+oRec.fk_cmdb_id+"&bls=0");
	_swdoc.MF().sl_component1.Refresh();
	_swdoc.MF().sl_optional_component1.Refresh();
	_swdoc.handle_set_contract(oRec.fk_default_sla);
}



// load any on subscription values defined in the app.service
function load_on_subscription(oRec)
{
	if(oRec.fk_cmdb_id!=undefined)
		opencall.itsm_fk_service = oRec.fk_cmdb_id;
	else
		return false;
		
	if(oRec.fk_df_onsub>0)
	{
		_swdoc.load_dataform(oRec.fk_df_onsub);	
	}

	_swdoc.get_service_slas(oRec.fk_cmdb_id,"VIEW",oRec.fk_default_sla);
	app._slf(_swdoc.MF().sl_component1, "fks="+_swdoc.OC().itsm_fk_service+"&bls=1");
	app._slf(_swdoc.MF().sl_optional_component1, "fks="+_swdoc.OC().itsm_fk_service+"&bls=1");
	_swdoc.MF().sl_component1.Refresh();
	_swdoc.MF().sl_optional_component1.Refresh();
	_swdoc.handle_set_contract(oRec.fk_default_sla);
}

// given a app.service id, and whether a subscription or request, find the current customers available slas return an array
// item 0 - string of delimited sla keys, item 1 - default sla key
function get_service_slas(serviceID,strRelType,strServiceDefaultSLA)
{
	var strSLA = "";
	var strDefaultSLA = "";

	var strSLACost = new Array();
	var strSLAPrice = new Array();

	var strSubscriptionID = app.service.get_customer_subscription(_swdoc.oCust().keysearch,serviceID,strRelType);

	var strParams = "fks="+strSubscriptionID;
	var oRS = app.g.get_sqrecordset("call/service/subscription_sla_prices",strParams);
	while(oRS.Fetch())
	{
		var strSLAField = app.g.get_field(oRS,"fk_sla");
		var strCost = app.g.get_field(oRS,"cost");
		var strPrice = app.g.get_field(oRS,"price");

		strSLACost[strSLAField] = strCost;
		strSLAPrice[strSLAField] = strPrice;
		
		if(strSLA!="")strSLA+=",";	
		strSLA+= strSLAField;
	}
	if(strSLA=="")
	{
		var strParams = "fks="+serviceID;
		var oRS = app.g.get_sqrecordset("call/service/service_sla_prices",strParams);
		while(oRS.Fetch())
		{
			var strSLAField = app.g.get_field(oRS,"fk_sla");
			var strCost = app.g.get_field(oRS,"cost");
			var strPrice = app.g.get_field(oRS,"price");
	
			strSLACost[strSLAField] = strCost;
			strSLAPrice[strSLAField] = strPrice;
			
			if(strSLA!="")strSLA+=",";	
			strSLA+= strSLAField;
		}
		//-- 29.03.2010 - remove as causes sql error where old data has links to string sla name
		strDefaultSLA = strServiceDefaultSLA;
	}if(strSLA=="")
		strSLA="-1";
	var strDefaultPriority = "";

	// get default sla and price for subscription
	var strParams = "fks="+strSubscriptionID;
	var oRS = app.g.get_sqrecordset("call/service/subscription_prices",strParams);
	if(oRS.Fetch())
	{
		if(strDefaultSLA=="")
			strDefaultSLA = app.g.get_field(oRS,"fk_default_sla");
		if(strRelType=='SUBSCRIPTION')
			_swdoc.strCurrentPrice = app.g.get_field(oRS,"request_price");
		else
			_swdoc.strCurrentPrice = app.g.get_field(oRS,"subscription_price");
		if(_swdoc.strCurrentPrice=="")
			_swdoc.strCurrentPrice = 0;
		strDefaultPriority = app.g.get_field(oRS,"fk_priority");
	}
	
	_swdoc.arrSLACost = strSLACost;
	_swdoc.arrSLAPrice = strSLAPrice;

	if(strDefaultSLA!="") 
	{
		//F0101290
		var oSLARec = app.g.get_record("itsmsp_slad", strDefaultSLA);
		if(oSLARec)
		{
			_swdoc.OC().itsm_sladef = strDefaultSLA;
			_swdoc.OC().itsm_slaname = oSLARec.slad_id;
			_swdoc.handle_set_sladef(_swdoc.OC().itsm_slaname);
			if(strDefaultPriority!="")
			{
				_swdoc.OC().priority = strDefaultPriority;
			}
		}
	}

	app._slf(_swdoc.MF().lb_oc_slad, "kvs="+strSLA);
}

function load_dataform(dfKey)
{
	oDf = app.g.get_record("sc_dataform", dfKey);
	if(oDf.suppgroup!="")
		opencall.suppgroup = oDf.suppgroup;
	if(oDf.owner!="")
		opencall.owner = oDf.owner;
	if(oDf.bpm_wf_id!="")
	{
		bpm_workflow.pk_workflow_id = oDf.bpm_wf_id;
		_swdoc.ResolveRecord("bpm_workflow","pk_workflow_id",oDf.bpm_wf_id);
	}
	if(oDf.probcode!="")
		opencall.probcode = oDf.probcode;
	if(oDf.prob_text!="")
	{
		if(updatedb.updatetxt=="")
			updatedb.updatetxt += oDf.prob_text;
		else
		{
			var x = confirm('Would you like to append the default text to the description?');
			if(x)
				updatedb.updatetxt += "\n\n"+oDf.prob_text;
		}
	}

}

function service_select(strValue)
{
	_swdoc.load_service_details(strValue);
	app._een(_swdoc.MF().mbtn_service_views, true);
	if(_swdoc.MF().fld_service_subscribe.value=="1")
	{
		_swdoc.OC().itsm_catreq_type = 'SUBSCRIPTION';
	}
	else
	{
		_swdoc.OC().itsm_catreq_type = 'REQUEST';
	}
	app._een(_swdoc.MF().fld_req_qty,true);
	app._een(_swdoc.MF().lbl_req_qty, true);

}

//-- load sld info and settings
function handle_set_sladef(strNewSLD, boolValidate)
{
	if(boolValidate==undefined)
		boolValidate==false;
	var strDisplaySLD = strNewSLD;

	//-- get actual SLD ID if getting from related record
	switch(strNewSLD)
	{
		case "[Use Configuration SLA]":
			break;
		case "[Use Customer SLA]":
			if(_swdoc.oCust().keysearch=="")
			{
				app._eva(_swdoc.MF().lb_oc_slad1, "");
				app._eva(_swdoc.MF().lb_oc_slad1, strNewSLD);
				opencall.itsm_slaname = strNewSLD;
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
				_swdoc.OC().priority = "[Use Customer Priority]"
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No customer selected, cannot resolve customer SLA. Please select a valid SLA.",16);
				else
					MessageBox("No customer selected.");
				return false;
			}
			if(_swdoc.oCust().sld=="")
			{
				app._eva(_swdoc.MF().lb_oc_slad1, strNewSLD);
				opencall.itsm_slaname = strNewSLD;
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
				_swdoc.OC().priority = "[Use Customer Priority]"
				_swdoc.UpdateFormFromData();
				if(boolValidate)
					MessageBox("No Default SLA is associated to this Customer. Please select a valid SLA.",16);
				else
					MessageBox("Customer does not have a default SLA.");
				return false;
			}
			var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.oCust().sld);
			if(oRecSLD)
			{
				strNewSLD = oRecSLD.slad_id;
				_swdoc.OC().itsm_slaname = strNewSLD;
			}
			break;		
	}

  	var oRecSLD = app.g.get_sqrecordset("general/get_itsm_sla","kv="+strNewSLD);
	if(oRecSLD.Fetch())
	{	
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD, "chargeto_cc");
		switch(strNewCC)
		{
			case "[Use Department Charge Centre]":
				strNewCC = _swdoc.oCust().department;
				break;
			case "[Use Customer Charge Centre]":
				strNewCC = _swdoc.oCust().costcenter;
				break;		
			default:
				break;
		}
		//-- eof cc option
			
		//-- set oc values
		if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
		if (_swdoc.boolServiceSLASetManually)
		{
			_swdoc.OC().priority = app.g.get_field(oRecSLD, "fk_ssla");
		}		
		_swdoc.OC().itsm_sladef = 	app.g.get_field(oRecSLD, "pk_slad_id");
		_swdoc.OC().itsm_slaname = app.g.get_field(oRecSLD, "slad_id");
		_swdoc.handle_set_priority(_swdoc.OC().priority);
		
		//-- check if customer dept has nay alternative settings
		_swdoc.check_customer_department_slad(strNewSLD,_swdoc.oCust().department);
		
		app._een(_swdoc.MF().lbl_oc_priority, true);
		app._een(_swdoc.MF().lb_oc_sla, true);
		_swdoc.UpdateFormFromData();
	}
	else
	{
		MessageBox("Failed to retrieve service level definition record for '" + strDisplaySLD + "'. Please contact your Supportworks Administrator");
		return false;
	}
	return true;
}


//-- check if there is alternative slad settings based on the customers department
function check_customer_department_slad(strSLADID, strDepartment)
{
 	if(strDepartment=="")
		return;
	var strParams = "fk_slad_id=" +app.pfu(strSLADID)+ "&fk_dept=" +app.pfu(strDepartment);
 	var oRecSLD = app.g.get_sqrecordset("call/incident/get_dept_sla",strParams);
	if(oRecSLD.Fetch())
	{
		//-- load cost center option
		var strNewCC = app.g.get_field(oRecSLD,"chargeto_cc");
		switch(strNewCC)
		{
			case "[Use Department Charge Centre]":
				strNewCC = _swdoc.oCust().department;
				break;
			case "[Use Customer Charge Centre]":
				strNewCC = _swdoc.oCust().costcenter;
				break;		
			default:
				break;
		}
		//-- eof cc option
			
		//-- set oc values
		if(strNewCC!="") _swdoc.OC().costcenter = strNewCC;
		_swdoc.OC().priority = app.g.get_field(oRecSLD,"fk_sla");
	}
}


function handle_set_priority(strValue)
{
	if(strValue=="[Use SLA Default Priority]")
	{
		var oRecSLD = app.g.get_record("itsmsp_slad", _swdoc.OC().itsm_sladef);
		if(oRecSLD)
		{	
			_swdoc.OC().priority = oRecSLD.fk_ssla;
			_swdoc.strCurrentPriority = _swdoc.OC().priority;
			_swdoc.UpdateFormFromData();
		}
		return false;
	}	
	else if(strValue=="[Use Customer Priority]")
	{
		if(_swdoc.oCust().priority=="[Use SLA Default Priority]")
			_swdoc.handle_set_priority("[Use SLA Default Priority]");
		else
		{
			_swdoc.OC().priority = _swdoc.oCust().priority;
			_swdoc.UpdateFormFromData();
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}
	else if(strValue=="[Use Profile Priority]")
	{
		if(_swdoc.OC().probcode!="")
		{
			var oRec = app.g.get_record("pcinfo", _swdoc.OC().probcode);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.sla);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("The Profile SLA is not the same as the currently selected SLA. The Profile priority cannot be used.");
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Profile SLA]")
			{
				_swdoc.OC().priority="[Use Profile Priority]";
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No profile has been set for this request. The Profile priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}	
	else if(strValue=="[Use Charge Centre Priority]")
	{
		if(_swdoc.OC().costcenter!="")
		{
			var oRec = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(oRec.itsm_sla==_swdoc.OC().itsm_sladef)
			{
				_swdoc.handle_set_priority(oRec.priority);
			}
			else
			{
				if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
				{
					app._een(_swdoc.MF().lbl_oc_priority, false);
					app._een(_swdoc.MF().lb_oc_sla, false);
					_swdoc.OC().priority = strValue;
					_swdoc.strCurrentPriority=_swdoc.OC().priority;
					_swdoc.UpdateFormFromData();
				}
				else
				{
					_swdoc.OC().priority = _swdoc.strCurrentPriority;
					_swdoc.UpdateFormFromData();
					MessageBox("The cost center SLA is not the same as the currently selected SLA. The cost center priority cannot be used.");
				}
			}
		}
		else
		{
			if(_swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
			{
				app._een(_swdoc.MF().lbl_oc_priority, false);
				app._een(_swdoc.MF().lb_oc_sla, false);
				_swdoc.OC().priority = strValue;
				_swdoc.strCurrentPriority=_swdoc.OC().priority;
				_swdoc.UpdateFormFromData();
			}
			else
			{
				_swdoc.OC().priority = _swdoc.strCurrentPriority;
				_swdoc.UpdateFormFromData();
				MessageBox("No cost center has been set for this request. The cost center priority cannot be used.");
			}
		}
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		return false;
	}		
	else
	{
		_swdoc.OC().priority = strValue;
		_swdoc.strCurrentPriority = _swdoc.OC().priority;
		_swdoc.UpdateFormFromData();
		return false;
	}
}

function handle_set_contract(strNewSLD)
{
	var strDefaultContract = app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract, strNewSLD);
	//###20120730 addition of typeof and else
	if((typeof strDefaultContract !== "undefined") && strDefaultContract!="")
	{
		_swdoc.OC().fk_contract_id = strDefaultContract;
		app.g.strLogAndResolveContract = strDefaultContract;
	} else {
		_swdoc.OC().fk_contract_id = "";
		app._eva(_swdoc.MF().lb_contract , "");
		app._ete(_swdoc.MF().lb_contract , "");
	}
	_swdoc.UpdateFormFromData();
		
}
//--Generic Function to Remove Calls
function remove_service_relatedcalls(strRemoveCallrefs, sl_removefrom)
{
	var arrRemoveRefs = strRemoveCallrefs.split(",");
	for(var y=0;y<arrRemoveRefs.length;y++)
	{
		app.g.sl_delrow_byvalue(sl_removefrom,arrRemoveRefs[y],"callref", null,0);	
		sl_removefrom.SetRowSelected(0,true,false);
		sl_removefrom.Refresh();
	}
}

function set_preferred_assignment(boolFromLogAndResolve)
{
    //-- 10.06.2004
    //-- NWJ
    //-- if just logging a call if we have 
    //-- a support group assign to them
    if (_swdoc.OC().itsm_prefered_suppgroup != "")
    {
    	//-- see if we have a matching analyst (if not try the suppo groups)   	
    	var anAnalyst = app.g.get_analyst_detail(_swdoc.OC().itsm_prefered_suppgroup);
    	if(anAnalyst)
    	{
	        _swdoc.OC().suppgroup = anAnalyst.supportgroup;
    	    _swdoc.OC().owner = anAnalyst.id;
    	}
    	else
    	{
    		//-- analyst not found so check if we are assigning to a group
			var aGroup = app.g.get_group_detail(_swdoc.OC().itsm_prefered_suppgroup);    	
			if (aGroup)
			{
	        	_swdoc.OC().suppgroup = _swdoc.OC().itsm_prefered_suppgroup;
    	    }
		}
		
        if ((!boolFromLogAndResolve)&&(_swdoc.OC().owner != ""))
        {
            //-- set call to unaccepted
            //-- when assigned to an analyst
            _swdoc.OC().status = 3;
        }
    }
}

function determine_prefered_support(strKeyValue)
{		

	var boolSetByCI=false;    
	var strSupportG = "";
	var oCiRec = app.g.get_sqrecordset("cmdb/common/select.config_item", "cid="+strKeyValue);
	while(oCiRec.Fetch())
	{
		strSupportA = app.g.get_field(oCiRec,"ci_support_analyst");
		strSupportG = app.g.get_field(oCiRec,"ci_support_group");
	}
	if(strSupportA!="")
	{			
		//-- have an analyst
		var anAnalyst = app.g.get_analyst_detail(strSupportA);		
		if(anAnalyst)							
		{
			_swdoc.OC().itsm_prefered_suppgroup = strSupportA;
			_swdoc.OC().itsm_prefered_text = anAnalyst.name;
			boolSetByCI=true;												
		}
	}
	else if(strSupportG != "")
	{
		//-- have a supp group
		var aGroup = app.g.get_group_detail(strSupportG);    	
		if(aGroup)
		{
			_swdoc.OC().itsm_prefered_suppgroup = strSupportG;
			_swdoc.OC().itsm_prefered_text = aGroup.name;
			boolSetByCI=true;						
		}
	}
	else
	{
		//-- there is no prefer so reset
		//-- 29.04.2008 - out of 2.2.0 testing
		_swdoc.OC().itsm_prefered_suppgroup = "";
		_swdoc.OC().itsm_prefered_text = "";	
		
	}

    _swdoc.UpdateFormFromData();
}

						OnFormLoading(strFormName, strCallclass)
{
	//-- can they raise a record of this class

	if(app.itsm.can_create(strCallclass,true)==false) return false;


	return true;
}

						OnRecordValueChanged(strTable, strColumn, strValue, strOldValue)
{
	//-- NWJ - check to see if user has changed the itsm_opencall_rfc table field
	//-- to search for raised by and monitored by analyst - so it works like resolving a customer.

	if(strTable=="itsm_opencall_rfc")
	{
		var oAnalyst = false;
		if((strColumn.indexOf("raised_") == 0)||(strColumn.indexOf("monitored_") == 0))
		{
			//-- get db col name based on passed in col name
			var strDBCol="";
			//MessageBox(strColumn.split("_")[2])
			switch(strColumn.split("_")[2])
			{
				case "analystid":
					strDBCol="analystid";
					break;
				case "name":
					strDBCol="name";
					break;
				case "group":					
					strDBCol="supportgroup";
					break;
				case "telephone":
					strDBCol="contactb";
					break;
				case "email":
					strDBCol="contacte";
					break;
				default:
					MessageBox("The extended rfc column (" + strColumn + ") cannot be matched to swanalysts. please contact your Supportworks Administrator.");
					_swdoc.set_rfc_analyst(false,true);
					return;
					break;								
			}
		}
		if(strColumn.indexOf("raised_") == 0)
		{
			//-- auto search raised by
			app.g.resolve_analyst(strDBCol,strValue,function(oAnalyst)
			{
				if(oAnalyst) _swdoc.set_rfc_analyst(oAnalyst,false);
			});
		}
		else if (strColumn.indexOf("monitored_") == 0)
		{
			//-- auto search monitored by
			app.g.resolve_analyst(strDBCol,strValue,function(oAnalyst)
			{
				if(oAnalyst) _swdoc.set_rfc_analyst(oAnalyst,true);
			});			
		}
	}
	else if(strColumn=="probcode" && strTable == "opencall")
    {
		_swdoc.boolChangeProfile = true;
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
  		mainform.SetTimer(4, 100);
	}
	else if(strColumn=="priority" && strTable == "opencall")
    {
     	if(_swdoc.boolChangeProfile)
        {
	 		_swdoc.OC().priority =strOldValue;
	 		_swdoc.UpdateFormFromData();
	   		MessageBox("SLAs and Priorities are dependant on Service, so cannot be set from the request profile.");
	   	}
    }
    else if( (strColumn=="itsm_impact_level")||(strColumn=="itsm_urgency_level") )
    {
    	if(_swdoc.OC().itsm_sladef!="")
    	{
    		//-- if use incident assessment over ci
    		//if(dd.GetGlobalParamAsNumber("Application Settings/Incident/UseCIAssessmentLevels")!=1)
    		//{
    			var strPriority = app.itsm.get_sla_from_slad_matrix(_swdoc.OC().itsm_sladef, _swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
    			if(strPriority!="")
   					_swdoc.handle_set_priority(strPriority);
    			_swdoc.UpdateFormFromData();
    		//}
    	}
    }
    else if (strColumn=="fk_company_id"||strColumn=="site"||strColumn=="fk_dept_code"||strColumn=="costcenter"||strColumn=="pk_workflow_id")
	{
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	}
}

						OnCallLoggedOK(nCallRef, dateRespondBy, dateFixBy, strCustName, strSla)
{
	//-- NWJ - 12-02.2007 - after logging problem link its CIs and Incidents
    //-- nwj 
    //-- 15.03.2004
    var intCallRef = app.g.callref_unpad(nCallRef);

	//-- affected CIs
    var olist = _swdoc.MF().sl_component1;
    var boolComponents = false;
	var strCauseDiaryText = "\nThe following Service Option's will be included in Request.\n";    
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolComponents = true;
        var pk_ci_id = olist.GetItemTextRaw(x,4);
        var intQty = olist.GetItemTextRaw(x,7);
        var ci_item_key = app.g.sl_getcolvalue(olist, x, 'service_id');//olist.GetItemTextRaw(x,1);

        var gl_code = app.g.sl_getcolvalue(olist, x, 'gl_code');//olist.GetItemTextRaw(x,1);

        var type = olist.GetItemTextRaw(x,2);
        var description = app.g.sl_getcolvalue(olist, x, 'description');//olist.GetItemTextRaw(x,3);

		intQty = intQty * _swdoc.OC().request_qty;
		
		var price = app.g.sl_getcolvalue(olist, x, 'price');
		if(isNaN(price))price=0;
		var price = price*_swdoc.OC().request_qty;
		var price = app.format_float_to_decimal_str(price);

		var cost = app.g.sl_getcolvalue(olist, x, 'total_unit_cost');
		if(isNaN(cost))cost=0;
		var cost = cost*_swdoc.OC().request_qty;
		var cost = app.format_float_to_decimal_str(cost);

		var oInsertTmp = new app.oSqlInsert('request_comp');
		oInsertTmp.setfield('fk_callref',intCallRef);
		oInsertTmp.setfield('fk_comp_id',pk_ci_id);
		oInsertTmp.setfield('name',ci_item_key);
		oInsertTmp.setfield('type',type);
		oInsertTmp.setfield('description',description);
		oInsertTmp.setfield('qty',intQty);
		oInsertTmp.setfield('comp_price',price);
		oInsertTmp.setfield('comp_cost',cost);
		oInsertTmp.setfield('gl_code',gl_code);
		oInsertTmp.execute();
		//-- Relate the CI to the call
		//if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'REQUEST-COMPONENT'))
		//{
		strCauseDiaryText    += "Service Name (" + description + "). \n"
		//}
    }    
    var olist = _swdoc.MF().sl_optional_component1;
    for (var x=0; x < olist.rowCount(); x++)
    {
		if(olist.IsRowChecked(x))
		{
    		boolComponents = true;
        	var pk_ci_id = app.g.sl_getcolvalue(olist, x, 'fk_key');//olist.GetItemTextRaw(x,1);
        	var intQty = olist.GetItemTextRaw(x,6);
        	var ci_item_key = app.g.sl_getcolvalue(olist, x, 'service_id');//olist.GetItemTextRaw(x,1);
	
        	var gl_code = app.g.sl_getcolvalue(olist, x, 'gl_code');//olist.GetItemTextRaw(x,1);

	      	var type = olist.GetItemTextRaw(x,2);
        	var description = app.g.sl_getcolvalue(olist, x, 'description');//olist.GetItemTextRaw(x,3);
	
			intQty = intQty * _swdoc.OC().request_qty;
			
			var price = app.g.sl_getcolvalue(olist, x, 'price');
			if(isNaN(price))price=0;
			var price = price*_swdoc.OC().request_qty;
			var price = app.format_float_to_decimal_str(price);
	
			var cost = app.g.sl_getcolvalue(olist, x, 'total_unit_cost');
			if(isNaN(cost))cost=0;
			var cost = cost*_swdoc.OC().request_qty;
			var cost = app.format_float_to_decimal_str(cost);
	
			var oInsertTmp = new app.oSqlInsert('request_comp');
			oInsertTmp.setfield('fk_callref',intCallRef);
			oInsertTmp.setfield('fk_comp_id',pk_ci_id);
			oInsertTmp.setfield('name',ci_item_key);
			oInsertTmp.setfield('type',type);
			oInsertTmp.setfield('description',description);
			oInsertTmp.setfield('qty',intQty);
			oInsertTmp.setfield('comp_price',price);
			oInsertTmp.setfield('comp_cost',cost);
			oInsertTmp.setfield('gl_code',gl_code);
			oInsertTmp.execute();
			//-- Relate the CI to the call
			//if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'REQUEST-COMPONENT'))
			//{
			strCauseDiaryText    += "Service Name (" + description + "). \n"
			//}	
		}
    }    
    //-- if we have Components to add add it
    if (boolComponents)
    {
		var strActionSource = "Service Request Management";
		var strCode			= "Linking Component Items";
		app.g.new_dairyevent(intCallRef,strCauseDiaryText,strActionSource,strCode);    
    }


    //-- loop through causing CI and assign them to this call
	var strDiaryText = "The following Configuration Items are to be associated.\n";
	var boolCIs = false;
    var olist = _swdoc.EF().sl_configitems1;
    for (var x=0; x < olist.rowCount(); x++)
    {
    	boolCIs =true;
        var pk_ci_id = olist.GetItemTextRaw(x,0);
        var ci_item_key = olist.GetItemTextRaw(x,1);

		//-- Relate the CI to the call
		if (app.cmdb.relate_ci_to_call(intCallRef,pk_ci_id,'REQUEST'))
		{
			strDiaryText    += "Configuration Item (" + ci_item_key + "). \n";
		}
    }

  
    //-- if we have CI to add add it
    if (boolCIs)
    {
		var strActionSource = "Service Request Management";
		var strCode			= "Linking Configuration Items";
		strDiaryText += strCauseDiaryText;
		app.g.new_dairyevent(intCallRef,strDiaryText,strActionSource,strCode);
		// -- If we have a CI,  check whether the first Stage requires Authorisations for CI Owner
		var xmlmc = new XmlMethodCall();
		xmlmc.SetParam("in_callref", intCallRef);
		if(!xmlmc.Invoke("VPME", "checkStageAuthSR"))
		{
			MessageBox(xmlmc.GetLastError());
		}
		
	}
	
    //-- insert a relationship between app.service request and incs
    var strIncidents = app.g.sl_get_valuesdel(_swdoc.EF().sl_incs1,0,",");
    if (strIncidents !="")
    {
 		var strActionSource = "Service Request Management";
	   	var strText = "This service request has been associated with the following Incidents\n\n";
    	var arrIncidents = strIncidents.split(",");
    	for(var x=0; x < arrIncidents.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrIncidents[x],'REQUEST-INCIDENT'))
			{
				strText += "Incident (" + app.g.pad_callref(arrIncidents[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strIncText = "The service request (" + nCallRef + ") has been associated to this Incident";
				app.g.new_diaryevent(arrIncidents[x],strIncText,strActionSource,"Service Request Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,strActionSource,"Incident Association");
    }
   
   //-- insert a relationship between app.service request and incs
    var strChanges = app.g.sl_get_valuesdel(_swdoc.EF().sl_rfcs,0,",");
    if (strChanges !="")
    {
 		var strActionSource = "Service Request Management";
	   	var strText = "This service request has been associated with the following Change Request\n\n";
    	var arrChanges = strChanges.split(",");
    	for(var x=0; x < arrChanges.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(arrChanges[x],intCallRef,'REQUEST-CHANGE'))
			{
				strText += "Change (" + app.g.pad_callref(arrChanges[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strIncText = "The service request (" + nCallRef + ") has been associated to this Change";
				app.g.new_diaryevent(arrChanges[x],strIncText,strActionSource,"Service Request Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,strActionSource,"Change Association");
    }
   


   //-- insert a relationship between rfc and problem / ke
    var strProblems = app.g.sl_get_valuesdel(_swdoc.EF().sl_srs,0,",");
    if (strProblems !="")
    {
    	var strText = "This service request has been associated with the following Service Requests\n\n";
  		var strActionSource = "Service Request Management";
	   	var arrProblems = strProblems.split(",");
    	for(var x=0; x < arrProblems.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(intCallRef,arrProblems[x],'REQUEST-REQUEST'))
			{
				app.g.relate_calls(arrProblems[x],intCallRef,'REQUEST-REQUEST');
				strText += "Service Request (" + app.g.pad_callref(arrProblems[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strPrbText = "The service request (" + nCallRef + ") has been associated to this service request.";
				app.g.new_diaryevent(arrProblems[x],strPrbText,strActionSource,"Service Request Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,strActionSource,"Service Request Association");
    }

   //-- insert a relationship between rfc and rels
    var strReleases = app.g.sl_get_valuesdel(_swdoc.EF().sl_rels1,0,",");
    if (strReleases !="")
    {
    	var strText = "This service request has been associated with the following Releases\n\n";
  		var strActionSource = "Service Request Management";
	   	var arrReleases = strReleases.split(",");
    	for(var x=0; x < arrReleases.length; x++)
    	{
			//-- call app.g method to link problem to incident
			if(app.g.relate_calls(arrReleases[x],intCallRef,'RELEASE-REQUEST'))
			{
				strText += "Release (" + app.g.pad_callref(arrReleases[x]) + ") associated\n";
				
				//-- update ke or problem diary
				strRelText = "The service request (" + nCallRef + ") has been associated to this release.";
				app.g.new_diaryevent(arrReleases[x],strRelText,strActionSource,"Service Request Association");
				
			}
		}			
		app.g.new_diaryevent(intCallRef ,strText,strActionSource,"Release Association");
    }
    
	//-- F0105032 - audit trail if incoming call	
	if(_swdoc._form.PreloadType==2)
	{
		if(_swdoc.OC().bpm_workflow_id!="")
		{
			var intEpochTime = app.global.GetCurrentEpocTime();
			var oAnalyst = app.g.get_sys_record("swanalysts",app.session.analystId);
			var oGroup = app.g.get_sys_record("swgroups",app.session.groupId);
			var strParams = "fk_workflow_id="+app.pfu(_swdoc.OC().bpm_workflow_id);
   			var oRS = app.g.get_sqrecordset("call/common/get_audit_fields",strParams);
			while(oRS.Fetch())
			{
				var strBinding = app.g.get_field(oRS,"fieldname");
				arrBinding = strBinding.split(".");
				var strTable = arrBinding[0];
				var strColName = arrBinding[1];
				
				var oldValue = "";
				if(strTable=="opencall")
					var oldValue = _swdoc.oOpencall[strColName];
				else
					var oldValue = _swdoc.oExtended[strColName];
				
				var newValue = _swdoc[strTable][strColName];
				if(app.g.dd_isnumeric(strTable, strColName))
				{
					if(newValue<0)
						newValue = app.g.fix_epoch(newValue);
					if(oldValue<0)
						oldValue = app.g.fix_epoch(oldValue);
				}
				if(oldValue!=newValue)
				{
					var strFieldDisplay = dd.tables[app.LC(strTable)].columns[strColName].DisplayName;
					var strFmtOldValue = app.g.dd_format(strTable, strColName, oldValue);
					var strFmtNewValue = app.g.dd_format(strTable, strColName, newValue);
					var oInsertTmp = new app.oXmlmcInsert('oc_audit');
					oInsertTmp.setfield('fk_callref',_swdoc.OC().callref);
					oInsertTmp.setfield('updatetimex',intEpochTime);
					oInsertTmp.setfield('updatedby',app.session.analystId);
					oInsertTmp.setfield('updatedbyname',oAnalyst.name);
					oInsertTmp.setfield('updatedbygroup',app.session.groupId);
					oInsertTmp.setfield('updatedbygroupname',oGroup.name);
					oInsertTmp.setfield('databinding',strBinding);
					oInsertTmp.setfield('bindingfield',arrBinding[1]);
					oInsertTmp.setfield('bindingtable',arrBinding[0]);
					oInsertTmp.setfield('fielddisplay',strFieldDisplay);
					oInsertTmp.setfield('oldvalue',oldValue);
					oInsertTmp.setfield('newvalue',newValue);
					oInsertTmp.setfield('fmtoldvalue',strFmtOldValue);
					oInsertTmp.setfield('fmtnewvalue',strFmtNewValue);
					oInsertTmp.execute();
				}
			}
		}
	}	//-- EOF F0105032 
}

						OnRecordResolved(strTable)
{
	//###20120730 fix for "Company")
	strTable = strTable.toLowerCase();
	
	//-- set sla based on worflow default
	if(IsObjectDefined("mainform") && strTable=="bpm_workflow")
	{
		//OC().priority = bpm_workflow.fk_default_sla;
		app.g.process_bpm_form_attribute_check(_swdoc,bpm_workflow.pk_workflow_id);
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
		_swdoc.UpdateFormFromData();
		return;
	}

    if (IsObjectDefined("mainform") && strTable == "userdb")
    {
    	//_swdoc.setup_customer_details()
    	//-- rjc - 2.4.0 - customer changed and site selected, confirm site part of new customers organisation
		if(_swdoc.OC().site!="[Use Customers Site]"){
			var recSite = app.g.get_record("site", _swdoc.OC().site);
			if(recSite)
			{
				if(app.LC(recSite.fk_company_id) != app.LC(_swdoc.oCust().fk_company_id))
				{

					_swdoc.OC().site = "[Use Customers Site]";
					//##### rename of lb_oc_site to lb_oc_site1
					app._ete(_swdoc.MF().lb_oc_site1, "[Use Customers Site]");


				}
			}
		}
		//-- check if customer is in active    	
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
	
		
		//-- nwj - 2.3.2 - load company record for customer - and show company on resolve message
		if(_swdoc.oCust().fk_company_id != "" && app.LC(_swdoc.oCust().fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
		{
			//-- rjc - 2.4 - resolve company record if company doesnt exist
			_swdoc.ResolveRecord('company','pk_company_id',_swdoc.oCust().fk_company_id);
			if(_swdoc.oCompany())
			{
				if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
			}
			else
			{
				//-- there is adata issue here as the customers organisation does not exist
				MessageBox("The organisation that this customer belongs to cannot be found on the system. Please contact your Supportworks administrator.");
			}
		}

		_swdoc.setup_customer_details();

		_swdoc.reload_services(true);


		//###20120730
		_swdoc.set_org_filter();
		
		//--F0110887 Start
		var intExpiryEpoch = app.g.fix_epoch(_swdoc.oCompany().supportexpiryx);
		app.g.checkOrgSupport(_swdoc.oCust().fk_company_id, intExpiryEpoch, true);
		//--F0110887 End
		
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
			
    }    
    if (IsObjectDefined("mainform") && strTable == "company")
    {
    
        //###20120716
    	_swdoc.multi_c_set_company(_swdoc.oCompany().pk_company_id);
    
    	app.g.strLogAndResolveCompany = _swdoc.oCompany().pk_company_id;
		if(app.LC(_swdoc.oCust().fk_company_id) != app.LC(_swdoc.oCompany().pk_company_id))
		{   
    		_swdoc.ResetRecord('userdb');
			if(dd.GetGlobalParamAsNumber("Application Settings/Service Request/OnOrganisationResolveCustomer")==1)
			{
				_swdoc.boolResolvingCust = true;
				mainform.SetTimer(1, 10);
			}
		}
		
		if(_swdoc.oCompany().recordresolvedmsg!="")MessageBox(_swdoc.oCompany().recordresolvedmsg);
		
		if(_swdoc.OC().site!="[Use Customers Site]"){
			var recSite = app.g.get_record("site", _swdoc.OC().site);
			if(recSite)
			{
				if(recSite.fk_company_id!=_swdoc.oCompany().pk_company_id)
				{
					_swdoc.OC().site = "[Use Customers Site]";
					app._ete(_swdoc.MF().lb_oc_site1,  "[Use Customers Site]");
				}
			}
		}
		
		if(_swdoc.OC().costcenter!=""){
			var recCC = app.g.get_record("costcent", _swdoc.OC().costcenter);
			if(recCC)
			{
				if(recCC.fk_company_id!=_swdoc.oCompany().pk_company_id)
				{
					_swdoc.OC().costcenter = "";
					app._ete(_swdoc.MF().lb_oc_cc1,  "");
				}
			}
		}
		
		//-- Filter Contracts for Company
		//###20120730 app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract);
		app.g.filter_company_contracts(_swdoc.OC().fk_company_id, _swdoc.MF().lb_contract);
		
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
		
  		_swdoc.check_cis();
 		_swdoc.UpdateFormFromData();
	}

}

						OnResolveRecord(strTable, strColumn, strValue)
{
 	//-- resolving the customer record
 	if(IsObjectDefined("mainform") && strTable == 'userdb' && _swdoc.boolResolvingCust == false && strValue!="")
 	{
 		if ("companyname" == strColumn && "" != strValue){
			_swdoc.ResolveRecord("company","companyname",strValue);
 			_swdoc.strPreSelectedCompany = strValue;
		}
		
  		if(strColumn =="firstname!surname")
  		{
  			//-- if using customer name search on firstname and lastname
  			//-- 2.4.1 bug fix 79046
   			arrNames = strValue.split(" ");
   			if(arrNames[1]==undefined)
   			{
      			var strParams = "_swc_firstname="+arrNames[0];
      			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   				var strSearch = "firstname="+arrNames[0];
    		}
   			else
   			{
    			var strLastName="";
    			for(var y=1;y<arrNames.length;y++)
    			{
     				if(y>1)strLastName = strLastName+" ";
     				strLastName = strLastName+arrNames[y];
    			}
       			var strParams = "_swc_firstname="+arrNames[0]+"&_swc_surname="+strLastName;
      			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
  				var strSearch = "firstname="+arrNames[0]+"&surname="+strLastName;
   			}
  		}
  		else
  		{
       		var strParams = "_swc_"+strColumn+"="+strValue;
   			var intCount = app.g.sqs_rowcount("call/common/customer_count",strParams);
   			var strSearch = strColumn+"="+strValue;
   		}
		if(intCount!=1 && !_swdoc.boolCustid_fromCall)
		{
			_swdoc.strSearchFor = strSearch;
  			mainform.SetTimer(1, 100);
  			_swdoc.boolResolvingCust = true;
  			return "";
 		}
 	}
 	 if(strTable == "company")
 	{
 		//-- Grab Company Record
 		var oCompanyTemp = app.g.get_record("company",strValue,'companyname');
 		//--F0110887 Start
		var intExpiryEpoch = app.g.fix_epoch(oCompanyTemp.supportexpiryx);
		app.g.checkOrgSupport(_swdoc.oCust().companyname, intExpiryEpoch, true);
		//--F0110887 End
 	}
	//-- resolving org record 	
 	if(IsObjectDefined("mainform") && strTable == 'company' && strValue =="")
 	{
		app.g.strLogAndResolveCompany = "";
  		//2.4 clear down list if user is being unselected
  		_swdoc.ResetRecord('userdb');
 	} 
	//-- resolving org record 	
 	if(IsObjectDefined("mainform") && strTable == 'bpm_workflow' && strValue =="")
 	{
		app.g.reset_bpm_form_attribute_check(_swdoc);
 	} 
 
 return strValue;
}

						OnValidate()
{
	//-- Check Service status is still "catalog" and available for logging
	//-- This check is used for requests stored as quick log calls with the app.service selected
	if(_swdoc.OC().itsm_fk_service>0)
	{
		var rec = app.g.get_record("config_itemi", _swdoc.OC().itsm_fk_service);
		if(rec.status_portfolio.toLowerCase()!="catalog")
		{
			MessageBox('This service is no longer available in the service catalog. Please select an alternative service.');
			_swdoc.OC().itsm_fk_service=0;
			_swdoc.UpdateFormFromData();
			return VALIDATE_FAIL;
		}
	}
	
	if(_swdoc.OC().request_qty<1)
	{
		MessageBox('You must request a quantity greater than 0');
		return VALIDATE_FAIL;
	}
	
	itsm_opencall_rfc.dow_note = updatedb.updatetxt;
	var fltPrice = 0;
	var fltCost = 0;
	//sl_optional_component1.rowCount()
	for(var x = 0; x<_swdoc.MF().sl_optional_component1.rowCount();x++)
	{
		if(_swdoc.MF().sl_optional_component1.IsRowChecked(x))
		{
			var price = app.g.sl_getcolvalue(_swdoc.MF().sl_optional_component1, x, 'price');
			if(isNaN(price))price=0;
			price = price*_swdoc.OC().request_qty;
			fltPrice = fltPrice + parseFloat(price);

			var cost = app.g.sl_getcolvalue(_swdoc.MF().sl_optional_component1, x, 'total_unit_cost');
			if(isNaN(cost))cost=0;
			fltCost = fltCost*_swdoc.OC().request_qty;
			fltCost = fltCost + parseFloat(cost);
		}
	}

	var strManCompCost = app.g.sqllist_gettotalvalue(_swdoc.MF().sl_component1, 'total_unit_cost');
	if(isNaN(strManCompCost))strManCompCost=0;
	fltCompCost = parseFloat(strManCompCost)+fltCost;
	fltCompCost = fltCompCost*_swdoc.OC().request_qty;
	opencall.request_comp_cost = app.format_float_to_decimal_str(fltCompCost);

	var fltSLACost = _swdoc.arrSLACost[_swdoc.OC().itsm_sladef];
	if(isNaN(fltSLACost))fltSLACost=0;
	opencall.request_sla_cost = app.format_float_to_decimal_str(fltSLACost);

	var fltCost = parseFloat(fltSLACost)+parseFloat(fltCompCost);
	if(isNaN(fltCost))fltCost=0;
	opencall.request_cost = app.format_float_to_decimal_str(fltCost);

	if(isNaN(_swdoc.strCurrentPrice))_swdoc.strCurrentPrice=0;
	var fltCompPrice = parseFloat(_swdoc.strCurrentPrice) + fltPrice;
	if(isNaN(fltCompPrice))fltCompPrice=0;
	fltCompPrice = fltCompPrice*_swdoc.OC().request_qty;
	opencall.request_comp_price = app.format_float_to_decimal_str(fltCompPrice);
	
	var fltSLAPrice = _swdoc.arrSLAPrice[_swdoc.OC().itsm_sladef];
	if(isNaN(fltSLAPrice))fltSLAPrice=0;
	opencall.request_sla_price = app.format_float_to_decimal_str(fltSLAPrice);
	
	fltPrice = parseFloat(fltSLAPrice)+parseFloat(fltCompPrice);
	if(isNaN(fltPrice))fltPrice=0;
	opencall.request_price = app.format_float_to_decimal_str(fltPrice);

	if(bpm_workflow.flg_requireci==1)
	{
		if(_swdoc.EF().sl_configitems1.rowCount() < 1)
		{
			MessageBox("Before logging this Service request you have to associate at least one configuration item or service");
			return VALIDATE_FAIL;
		}
	}

	return VALIDATE_DEFAULT;
}

						OnPreValidate()
{
	//--F0110887 Start
	var intExpiryEpoch = app.g.fix_epoch(_swdoc.oCompany().supportexpiryx);
	var boolOrgSupported = new Boolean(app.g.checkOrgSupport(_swdoc.oCust().companyname, intExpiryEpoch, false));
	if (boolOrgSupported==false)
	{
		 MessageBox("You cannot log a call for this customer as the '" + _swdoc.oCust().companyname + "' organisation is no longer supported.");
		 return VALIDATE_FAIL;
	}
	//--F0110887 End
	
	if(_swdoc.OC().itsm_fk_service<1)
	{
		MessageBox("The field 'Service' is a mandatory field and must be completed",MB_ICONSTOP);
		return VALIDATE_FAIL;
	}

    var boolResolve = false;
	if(	_swdoc.OC().itsm_slaname=="[Use Customer SLA]"||_swdoc.OC().itsm_slaname=="[Use Profile SLA]" || _swdoc.OC().itsm_slaname=="[Use Workflow SLA]" || _swdoc.OC().itsm_slaname=="[Use Charge Centre SLA]")
		boolResolve = true;
	if(boolResolve)
	{
		var boolOK = _swdoc.handle_set_sladef(_swdoc.OC().itsm_slaname,true);
		if(!boolOK)
			return VALIDATE_FAIL;
	}

	//###20120802 check cc, dept, site in org?
	var iCaptureMistakes = 0;
	var intMistakeCounter = 0;
	var tDept; // needs to remain outside as department name is NOT the fk_dept_code but dept_name
	if ("" !== _swdoc.OC().site){
		tSite = app.g.get_record("site", _swdoc.OC().site);
		if (tSite.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 1;
			intMistakeCounter++;
		}
	}
	if ("" !== _swdoc.OC().costcenter){
		var tCC = app.g.get_record("costcent", _swdoc.OC().costcenter);
		if (tCC.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 2;
			intMistakeCounter++;
		}
	}
	if ("" !== _swdoc.OC().fk_dept_code){
		tDept = app.g.get_record("department", _swdoc.OC().fk_dept_code);
		if (tDept.fk_company_id != _swdoc.OC().fk_company_id){
			iCaptureMistakes += 4;
			intMistakeCounter++;
		}
	}
	if (iCaptureMistakes > 0){
		var strDetectedMistakes = "";
		var intDealtWithMistakes = 0;
		if ( 1 == (iCaptureMistakes % 2) ) {
			strDetectedMistakes += "site (" + _swdoc.OC().site + ")";
			intDealtWithMistakes++;
		}
		if ( 1 == (Math.floor(iCaptureMistakes / 2) % 2) ) {
			strDetectedMistakes += ((0==intDealtWithMistakes)?"":((2==intMistakeCounter)?" and ":", ")) + "charge centre (" + _swdoc.OC().costcenter + ")";
			intDealtWithMistakes++;
		}
		if ( 1 == (Math.floor(iCaptureMistakes / 4) % 2) ) {
			if(!tDept.dept_name)
			{
				//-- F0108424
				var oCust2 = app.g.get_record("userdb", _swdoc.OC().cust_id);
				strDetectedMistakes += ((0==intDealtWithMistakes)?"":" and ") + "department ("+oCust2.department+") does not exist and";
			}else
			{
				strDetectedMistakes += ((0==intDealtWithMistakes)?"":" and ") + "department (" + tDept.dept_name + ")";
			}
			//-- F0108424

		}
		var intResponse = MessageBox("The " + strDetectedMistakes + " " + ((1==intMistakeCounter)?"is":"are") + " NOT part of " + _swdoc.OC().companyname + ".\n\nSelect 'OK' to proceed with the current data.", MB_OKCANCEL | MB_ICONEXCLAMATION);
		if (2==intResponse) return VALIDATE_FAIL;
	}




	return VALIDATE_DEFAULT;
}

