						btn_pick_raiseda_OnPressed()
{
    //-- NWJ 17.03.2004
   //-- open analyst search form in select mode

    // Create an instance of a PopupForm class
    var form = new PopupForm();

    // Open the form TbSites
    if(form.Open("tbAnalysts","",""))
    {
        //-- Access the forms _swdoc and call the _swdoc.GetVariable() method with the name of the variable
        var stranalystid = form.document.analystid;
        if (stranalystid == "") return;

        //-- assign values
        var oe = elements;
        app._ete(oe["db.rfc_sb_id"] , stranalystid);
        app._ete(oe["db.rfc_sb_name"] , form.document.name);
        app._ete(oe["db.rfc_sb_sg"] , form.document.group);
        app._ete(oe["db.rfc_sb_tel"] ,form.document.phone);
        app._ete(oe["db.rfc_sb_email"] , form.document.email);
    }
}

