						sl_cust_cis1_OnItemChecked(nRow, bChecked)
{
	
	var pk_ci_id = app.g.sl_getrawcolvalue(sl_cust_cis1, nRow, "pk_auto_id");	
	//MessageBox(pk_ci_id + " A:A " + nRow)
	if(bChecked)
	{
		if(app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems, pk_ci_id, 'pk_auto_id') > -1) return;
	
		//MessageBox(pk_ci_id)	
		app.g.sl_appendfilter(_swdoc.EF().sl_configitems, pk_ci_id,'pk_auto_id');

		
		//-- NWJ - determine who the preferred support is
 		_swdoc.determine_prefered_support(true)	
 		
 		//-- nwj - 15.07.2008 - check ci contract
		app.cfg.process_call_contract_check(pk_ci_id, true,_swdoc);
	}
	else
	{
			var intRowPos = app.g.sl_findrow_byvalue(_swdoc.EF().sl_configitems, pk_ci_id, 'pk_auto_id');
			if(intRowPos>-1)
			{
				_swdoc.EF().sl_configitems.RemoveRow(intRowPos);		
	
				//-- 29.04.2008 - 2.2.0 if this ci that has been removed was used to set the pref supp group then change it
				if(_swdoc.intAssessmentCI == pk_ci_id)
				{
					_swdoc.OC().itsm_prefered_suppgroup = "";
					_swdoc.OC().itsm_prefered_text = "";
				}
			
				_swdoc.determine_prefered_support(true);
				//_swdoc.check_ci_assessment();
			}


	}

}

