						sl_component1_OnHeaderItemClicked(strOrder, nCol)
{
	// TODO: Add your event handler code here

	return false;
}

						sl_component1_OnItemDoubleClicked(nRow, nCol)
{
	if(nRow!=sl_component1.curSel)
		return;

	// TODO: Add your event handler code here
	var strCanCust = app.g.sl_getcolvalue(sl_component1, sl_component1.curSel, "flg_cancustomise");
	if(strCanCust=="No"){
		var strKeyValue = app.g.sl_getprikeyvalue(sl_component1, nRow);
		app.OpenFormForEdit("service_component", strKeyValue, "customise=1&selection=1", true, function(){
			return false;
		});
	}
	
	var strKeyValue = app.g.sl_getprikeyvalue(sl_component1,  sl_component1.curSel);
	app.OpenFormForEdit("service_component_selection", strKeyValue, "", true, function(x){
		var newKey = x.document.strSelected;
		var pDiff = x.document.strPriceDiff;
		if(newKey=="")
			return false;
			
		var currKey = app.g.sqllist_keys(sl_component1, 0, true, ","); 
		var currentKeys = currKey.split(",");
		var strFilter = "";
		for(var i = 0; i< currentKeys.length ; i++)
		{
			if(currentKeys[i]==strKeyValue)
			{
				if(strFilter!="")strFilter+=",";
				strFilter+=newKey;
			}
			else
			{
				if(strFilter!="")strFilter+=",";
				strFilter+=currentKeys[i];
			}
		}
		//MessageBox(strFilter);
		if(strFilter=="")
			strFilter = -1;
			
		var strPrice = _swdoc.strCurrentPrice;
		strPrice = parseFloat(strPrice)+parseFloat(pDiff);
		_swdoc.strCurrentPrice = strPrice;
			
		app._slf(_swdoc.MF().sl_component1, "rids="+strFilter);
		_swdoc.MF().sl_component1.Refresh();
		return false;
		
	});	
}

