						lb_oc_slad_OnValueChanged(strValue)
{
	_swdoc.boolServiceSLASetManually = true;
	_swdoc.handle_set_sladef(strValue);
	
	//-- Filter Contracts for Company
	app.g.filter_company_contracts(_swdoc.oCompany().pk_company_id, _swdoc.MF().lb_contract);
	var slarec = app.g.get_sqrecordset("general/get_itsm_sla","kv="+strValue);
	if(slarec.Fetch())
	{
		var intSLAId = app.g.get_field(slarec,"pk_slad_id");
		if(intSLAId!="")
			_swdoc.handle_set_contract(intSLAId);
	}

}

