						OnFieldValueChanged(strName, strValue)
{
	// TODO: Add your event handler code here
	if(strName == "fld_del_date")
	{		
		if(_swdoc.MF().fld_del_date.value < app.global.GetCurrentEpocTime()){
			MessageBox("The Delivery Date that you have selected is in the past!");
		}
	}

}

