						mbtn_cci_views1_OnMenuItem(strName, nItemID)
{
  
	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;
	
	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_configitems1,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_configitems1,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}	
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

						mbtn_cci_actions1_OnMenuItem(strName, nItemID)
{
	var iRemoveCI = 1;

	//-- Remove Selected config items
	if(nItemID == iRemoveCI)
	{
   	
		//-- do this by finding unselected rows and making sure they are part of fitler
		var strFilter = "_kv=-1";
		for (var x=sl_configitems1.rowCount()-1;x > -1;x--)
		{
			if (!sl_configitems1.IsRowSelected(x))
			{
				//-- add to filter
				var value = sl_configitems1.GetItemTextRaw(x,"pk_auto_id");
				if (value != "")
				{
    				if (strFilter != "") strFilter +=",";
    				strFilter += value;
				}
			}
		}

       app._slf(sl_configitems1, strFilter);  
       sl_configitems1.Refresh();
       sl_configitems1.SetRowSelected(0);
       return;
	}
       
}

						btn_search_cci1_OnPressed()
{

   	var strURL = "canreset=1&fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id);
	app.cmdb.search_ci(true,strURL,function(sResults)
	{	
		if (sResults.selectedkeys=="") return;
   
		_swdoc.link_cause_cis_to_change(sResults.selectedkeys);
		
	
 		//-- nwj - 15.07.2008 - check ci contract
		app.cfg.process_call_contract_check(sResults.selectedkeys, true,_swdoc);	
		app.g.setSLAFromRules(_swdoc.strSLACallClass, _swdoc);
	});
	

}

