						sl_configitems1_OnItemSelected(nRow)
{
	//-- NWJ
	//-- 27.11.2006
	//-- enable list menus if an item is selected	
	var boolActive = (sl_configitems1.rowCount()>0);
	var selCount = app.g.sl_getselected_count(sl_configitems1);	
	app._een(mbtn_cci_views1, (selCount==1));
	app._een(mbtn_cci_actions1, boolActive);

}

