	{
		case iDetails:
			sl_rels1.EditRecord(sl_rels1.curSel);
			break;
	}
}

						mbtn_rel_actions1_OnMenuItem(strName, nItemID)
{

	var iClear = 1;
	
	switch (nItemID)
	{
		case iClear:
			var strSelectedKes = app.g.sl_getselected_valuesdel(sl_rels1,0,",","");
			_swdoc.remove_service_relatedcalls(strSelectedKes, sl_rels1);
			break;
	}


}

						btn_srch_rel1_OnPressed()
{
	var strURL = "canreset=1";  
	app.itsm.search_calls("REL",strURL,true,function(oResults)
	{
		if(oResults.selectedkeys=="") return;
		app.g.sl_kv_appendfilter(sl_rels1, oResults.selectedkeys,0);
		sl_rels1.Refresh();
	});
}

