						mbtn_inc_views1_OnMenuItem(strName, nItemID)
{
	var iDetails = 1;
	var iCust = 2
	if(nItemID == iDetails)
	{
		if(sl_incs1.curSel > -1)sl_incs1.EditRecord(sl_incs1.curSel);
	}
	else if (nItemID==iCust)
	{
		if(sl_incs1.curSel > -1)
		{
			var strKeySearch = app.g.sl_getcolvalue(sl_incs1,-1,"cust_id");
			if(strKeySearch!="")app.OpenFormForEdit("tfCustomer", strKeySearch, "", true);
		}
	}
}

						mbtn_inc_actions1_OnMenuItem(strName, nItemID)
{

	var iClear = 1;
	var iLinkCauseCI = 2;
	var iLinkImpactCI = 3;
	
	switch (nItemID)
	{
		case iClear:
			//-- get select incidents
			var strSelectedIncs = app.g.sl_getselected_valuesdel(sl_incs1,"callref",",","");
			app.g.sl_clearselected(sl_incs1,null,0);			
			_swdoc.remove_selected_incident_cis(strSelectedIncs);
			break;
		case iLinkCauseCI:
			var strSelectedIncs = app.g.sl_getselected_valuesdel(sl_incs1,"callref",",","");
			var strLinkedCIs = app.cmdb.get_call_cis(strSelectedIncs , "INCIDENT", "");
			_swdoc.link_cause_cis_to_change(strLinkedCIs);		
			break;
		case iLinkImpactCI:
			var strSelectedIncs = app.g.sl_getselected_valuesdel(sl_incs1,"callref",",","");	
			var strLinkedCIs = app.cmdb.get_call_cis(strSelectedIncs , "INCIDENT", "");
			_swdoc.link_affect_cis_to_change(strLinkedCIs);		
			break;
	}

}

						btn_search_inc1_OnPressed()
{
	var strURL = "canreset=1";  
	app.itsm.search_calls("INC",strURL,true,function(oResults)
	{
		if(oResults.selectedkeys=="") return;
	
		_swdoc.filter_inc_impact(oResults.selectedkeys);
	});
}

						TabRel_OnTabSelected(nTab)
{
	if(nTab==5)
	{
		_swdoc.filter_impact_customers(_swdoc.EF()); 
	}
	else if (nTab==6)
	{
		_swdoc.filter_impact_orgs(_swdoc.EF()); 
	}


}

