method.code:
						var FLG_0 = 0;
var FLG_1 = 1;
var FLG_2 = 2;
var FLG_3 = 8;
var FLG_4 = 16;
var FLG_5 = 32;
var FLG_6 = 64;
var strCallref = 0;
var oRec = null;

function setup_form_fields()
{
	// TODO: Add your event handler code here
	var inCallref = _swdoc.GetArg("in_callref");
	var inTaskID = _swdoc.GetArg("in_taskid");

	if(inTaskID>0 && inCallref>0)
	{
		_swdoc.oRec = app.g.get_recordwhere("calltasks", "callref = "+inCallref+" and taskid="+inTaskID);
		_swdoc.SetTitle("Work Item Properties "+app.g.callref_pad(inCallref)+"-"+app.lpad(inTaskID,"0",3));
		if(_swdoc.oRec.status<15)
		{
			mainform.tWI.ShowTabItem(1,false);
			app.g.load_form_fields(_swdoc.oRec, mainform, "calltasks");
			_swdoc.oRec.notifytime = app.g.number_to_time(_swdoc.oRec.notifytime/60);
			var strAssignee = "#"+_swdoc.oRec.groupid;
			if(_swdoc.oRec.analystid!="")
			{
				strAssignee+=":"+_swdoc.oRec.analystid;
			}

			app._eva(mainform.fld_notify_time,_swdoc.oRec.notifytime);
			//setup fields from form flags.
			if(_swdoc.oRec.flags & _swdoc.FLG_0)
			{
				app._eva(mainform.fld_compl, 0);
			}
			if(_swdoc.oRec.flags & _swdoc.FLG_1)
			{
				app._ete(mainform.fld_compl, 1);
			}
			if(_swdoc.oRec.flags & _swdoc.FLG_2)
			{
				app._eva(mainform.fld_compl, 2);
			}
				
			if(_swdoc.oRec.flags & _swdoc.FLG_3)
			{
				//sequential
			}

			if(_swdoc.oRec.flags & _swdoc.FLG_4)
			{
				mainform.fld_reminder_OnValueChanged(1);
				app._eva(mainform.fld_email_assignee, 1);
				app._eva(mainform.fld_reminder, 1);
			}
			if(_swdoc.oRec.flags & _swdoc.FLG_5)
			{
				mainform.fld_reminder_OnValueChanged(1);
				app._eva(mainform.fld_email_owner, 1);
				app._eva(mainform.fld_reminder, 1);
			}
			if(_swdoc.oRec.flags & _swdoc.FLG_6)
				app._eva(mainform.fld_notify,1);
	
			app._ete(mainform.fld_assignee, strAssignee);
			
			app._evi(mainform.fld_allow_time, false);
			app._evi(mainform.lbl_allow_time, false);
			
			app._evi(mainform.s_calltasks__compltbyx, true);
			app._evi(mainform.lbl_compltbyx, true);
	
			app._een(mainform.s_calltasks__status, true);
			app._een(mainform.lbl_status, true);
			app._een(mainform.s_calltasks__percentcomplete, true);
			app._een(mainform.lbl_perc_comp, true);
			app._ero(mainform.s_calltasks__status, false);
			
			app._ete(mainform.btn_action, "Update");
		}
		else
		{
			_swdoc.oRec.status_complete = _swdoc.oRec.status;
			mainform.tWI.ShowTabItem(0,false);
			app.g.load_form_fields(_swdoc.oRec, mainform, "calltasks");
			_swdoc.oRec.notifytime = app.g.number_to_time(_swdoc.oRec.notifytime/60);

			var strAssignee = "#"+_swdoc.oRec.groupid;
			if(_swdoc.oRec.analystid!="")
			{
				strAssignee+=":"+_swdoc.oRec.analystid;
			}

			app._eva(mainform.fld_notify_time,_swdoc.oRec.notifytime);
			//setup fields from form flags.
			if(_swdoc.oRec.flags & _swdoc.FLG_0)
				app._ete(mainform.fld_compl, 0);
			if(_swdoc.oRec.flags & _swdoc.FLG_1)
				app._ete(mainform.fld_compl, 1);
			if(_swdoc.oRec.flags & _swdoc.FLG_2)
				app._ete(mainform.fld_compl, 2);

			app._ete(mainform.fld_assignee, strAssignee);
						
			app._een(mainform.fld_assignee, false);
			app._een(mainform.fld_assignee1, false);
			app._een(mainform.fld_compl, false);
			app._evi(mainform.s_calltasks__compltbyx, true);
			app._een(mainform.s_calltasks__compltbyx, false);
			app._een(mainform.s_calltasks__status, false);
			app._evi(mainform.s_calltasks__percentcomplete, true);
			app._een(mainform.s_calltasks__percentcomplete, false);
			app._een(mainform.s_calltasks__completedonx, false);
			app._een(mainform.s_calltasks__completedby, false);
			app._een(mainform.s_calltasks__status_complete, false);
			app._een(mainform.s_calltasks__priority, false);
			app._een(mainform.s_calltasks__type, false);
			app._een(mainform.s_calltasks__details, false);
			app._ero(mainform.s_calltasks__type, true);
			app._ero(mainform.s_calltasks__completedby, true);
			app._ero(mainform.s_calltasks__priority, true);
			app._ero(mainform.fld_compl, true);
		}
	}
	else
	{
		mainform.tWI.ShowTabItem(1,false);
		app._evi(mainform.s_calltasks__compltbyx, false);
		_swdoc.oRec = new Object();
		_swdoc.oRec.callref = inCallref;
		_swdoc.oRec.parentgroup = _swdoc.GetArg("in_parentgroup");
		_swdoc.oRec.parentgroupsequence = _swdoc.GetArg("in_parentgroupsequence");
		_swdoc.oRec.groupid = app.session.groupId;
		_swdoc.oRec.analystid = "";
		_swdoc.oRec.flags = 0;

		var strAssignee = "#"+_swdoc.oRec.groupid;
		app._ete(mainform.fld_assignee, strAssignee);
	}

}

function create_wi()
{
	var boolVal = _swdoc.validate();
	if(!boolVal)
		return;
	//-- call vpme to create call and setup inputs
	var xmlmc = new XmlMethodCall;
	
	_swdoc.oRec.compltbyx = mainform.fld_allow_time.value*60;
	_swdoc.oRec.notifytime = mainform.fld_notify_time.value*60;

	xmlmc.SetValue("in_callref", _swdoc.oRec.callref);
	xmlmc.SetValue("in_flags", _swdoc.oRec.flags);
	xmlmc.SetValue("compltbyx", _swdoc.oRec.compltbyx);
	xmlmc.SetValue("analystid", _swdoc.oRec.analystid);
	xmlmc.SetValue("groupid", _swdoc.oRec.groupid);
	xmlmc.SetValue("details", mainform.s_calltasks__details.value);
	xmlmc.SetValue("priority", mainform.s_calltasks__priority.value);	
	xmlmc.SetValue("type", mainform.s_calltasks__type.value);
	xmlmc.SetValue("notifytime", _swdoc.oRec.notifytime);
	xmlmc.SetValue("parentgroup", _swdoc.oRec.parentgroup);	
	xmlmc.SetValue("parentgroupsequence", _swdoc.oRec.parentgroupsequence);	
	
	
	//-- Invoke the method
	if(xmlmc.Invoke("VPME", "wiCreateCalltask"))
	{
		_swdoc.CloseForm();
	}
	else
	{
		MessageBox(xmlmc.GetLastError());
	}	

}

function update_wi()
{
	var boolVal = _swdoc.validate();
	if(!boolVal)
		return;
	//-- call vpme to create call and setup inputs
	var xmlmc = new XmlMethodCall;
	
	_swdoc.oRec.notifytime = mainform.fld_notify_time.value*60;

	xmlmc.SetValue("in_callref", _swdoc.oRec.callref);
	xmlmc.SetValue("in_taskid", _swdoc.oRec.taskid);
	xmlmc.SetValue("in_status", mainform.s_calltasks__status.value);
	xmlmc.SetValue("in_flags", _swdoc.oRec.flags);
	xmlmc.SetValue("in_analystid", _swdoc.oRec.analystid);
	xmlmc.SetValue("in_groupid", _swdoc.oRec.groupid);
	xmlmc.SetValue("in_details", mainform.s_calltasks__details.value);
	xmlmc.SetValue("in_priority", mainform.s_calltasks__priority.value);	
	xmlmc.SetValue("in_type", mainform.s_calltasks__type.value);
	xmlmc.SetValue("in_notifytime", _swdoc.oRec.notifytime);
	xmlmc.SetValue("in_parentgroup", _swdoc.oRec.parentgroup);	
	xmlmc.SetValue("in_parentgroupsequence", _swdoc.oRec.parentgroupsequence);	
	
	
	//-- Invoke the method
	if(xmlmc.Invoke("VPME", "wiUpdateCalltask"))
	{
		MessageBox("You have sucessfully updated the Work Item.",32);
		_swdoc.CloseForm();
	}
	else
	{
		MessageBox(xmlmc.GetLastError());
	}	

}
function calc_flags()
{
	var intFlags = 0;
	
	intFlags = intFlags + parseInt(mainform.fld_compl.value);

	if(mainform.fld_email_owner.value==1)
		intFlags = intFlags +_swdoc.FLG_4;
	if(mainform.fld_email_assignee.value==1)
		intFlags = intFlags +_swdoc.FLG_5;
	if(mainform.fld_notify.value==1)
		intFlags = intFlags +_swdoc.FLG_6;
		
	_swdoc.oRec.flags = intFlags;
}

function validate()
{
	//check if analyst is correct
	if(_swdoc.oRec.analystid=="")
	{
		if(mainform.fld_compl.value<1)
		{
			MessageBox("You have chosen to only allow the assignee to complete the work item\n but you have not selected an individual assignee",16);
			return false;
		}
	}
	
	//check if there is a correct time
	if(mainform.fld_allow_time.value<1)
	{
		MessageBox("Please enter a time period in the format hh:mm",16);
		return false;
	}
	
	//check if there are reminder values
	if(mainform.fld_reminder.value==1)
	{
		if(mainform.fld_notify_time.value<1)
		{
			MessageBox("Please enter a valid Reminder time in the format (hh:mm)",16);
			return false;
		}
		
		if(mainform.fld_email_assignee.value==0 && mainform.fld_email_owner.value==0)
		{
			MessageBox("Please select one of the reminder options.",48);
			return false;
		}
	}

	return true;
}

function action_wi()
{
	if(mainform.btn_action.text == "Update")
		_swdoc.update_wi();
	else
		_swdoc.create_wi();
	return;
}

							fld_assignee1_OnLeftClick()
{
	// TODO: Add your event handler code here
		app.g.pick_analystorgroup("Assignee", function(oAnalyst)
		{
			if(!oAnalyst)
				return false;
	
			if(oAnalyst.oAnalyst.id!="")
			{
				_swdoc.oRec.analystid = oAnalyst.oAnalyst.id;
				_swdoc.oRec.groupid =  oAnalyst.oAnalyst.supportgroup;
				app._ete(mainform.fld_assignee, "#"+_swdoc.oRec.groupid+":"+_swdoc.oRec.analystid);
			}
			else
			{
				_swdoc.oRec.analystid = "";
				_swdoc.oRec.groupid =  oAnalyst.oGroup.id;
				app._ete(mainform.fld_assignee, "#"+_swdoc.oRec.groupid);
			}
		
	});
	return false;

}

							fld_assignee1_OnRightClick()
{
	// TODO: Add your event handler code here
	if(!app.service.can_update(true))
		return false;
	app._ete(fld_it_owner1,"");
	app._eva(fld_it_owner1,"");
	oCI().owner_it = "";

	return true;
}

