							fld_reminder_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	if(strValue==1)
	{
		app._een(fld_email_assignee, true);
		app._een(fld_email_owner, true);
		app._een(fld_notify_time, true);
	}else
	{
		app._een(fld_email_assignee, false);
		app._een(fld_email_owner, false);
		app._een(fld_notify_time, false);
	}

}

