method.code:
						function oTask()
{
	return _swdoc.bpm_stage_task;
}

function MF()
{
	return _swdoc.mainform;
}

							OnFormLoaded()
{
	
	app.g.popuplate_form_fromargs("bpm_stage_task", _swdoc, true,true);
		
	var boolNoCond = (bpm_stage_task.h_condition=="");
	app._een(btn_apply_test1, boolNoCond);
	app._een(btn_and1,(!boolNoCond));
	app._een(btn_or1,(!boolNoCond));

	
}

							lb_analyst_OnLeftClick()
{
	//-- pick analyst or group
	app.g.pick_analystorgroup("Assign To", function(oAnalystGroup)
	{
		if (!oAnalystGroup)
		{
			return false;
		}
		if (oAnalystGroup.oGroup!=false)
		{
			_swdoc.oTask().fk_suppgroup = oAnalystGroup.oGroup.id;	
		}
		if(!oAnalystGroup.oAnalyst.id)
		{
			_swdoc.oTask().fk_analyst = "";					
			app._ete(lb_analyst,"");	
		}
		else
		{
			_swdoc.oTask().fk_analyst = oAnalystGroup.oAnalyst.id;
		}
		_swdoc.UpdateFormFromData();
	});
	
	return false;
}

							lb_predecessor_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	_swdoc.oTask().predecessor_name = lb_predecessor.display;

}

							lb_test1_OnValueChanged(strValue)
{
	//- -show inputs for user based on the test they are doing
	if(strValue.toLowerCase().indexOf("opencall status")!=-1)
	{
		app._evi(lb_teststatus1,true);
		app._evi(tb_value1,false);
		app._evi(lb_testwfstatus1,false);
	}
	else if(strValue.toLowerCase().indexOf("bpm status")!=-1)
	{
		app._evi(lb_teststatus1,false);
		app._evi(lb_testwfstatus1,true);
		app._evi(tb_value1,false);
	}
	else
	{
		app._evi(lb_teststatus1,false);
		app._evi(lb_testwfstatus1,false);
		app._evi(tb_value1,true);
		
	}

}

							btn_apply_test1_OnPressed()
{
	
	if((lb_op1.text=="")||(lb_test1.text ==""))
	{
		MessageBox("Please specify some test criteria");
		return;
	}
	
	var boolString=false;
	var strOp = lb_op1.text;
	var strTest = "";
	switch(lb_test1.text.toLowerCase())
	{
		case "bpm status":
			strTest = "&[new_opencall.bpm_status_id]";		
			boolString = true;
			break;
		case "opencall status":
			strTest = "&[new_opencall.status]";				
			break;
		case "customer id":
			strTest = "&[new_opencall.cust_id]";				
			boolString = true;		
			break;						
		case "customer name":
			strTest = "&[new_opencall.cust_name]";				
			boolString = true;		
			break;			
		case "support group":
			strTest = "&[new_opencall.suppgroup]";				
			boolString = true;		
			break;			
		case "support analyst":
			strTest = "&[new_opencall.owner]";				
			boolString = true;		
			break;			
			//-- old values to test
		case "previous bpm status":
			strTest = "&[old_opencall.bpm_status_id]";		
			boolString = true;
			break;
		case "previous opencall status":
			strTest = "&[old_opencall.status]";		
			break;
		case "previous customer id":
			strTest = "&[old_opencall.cust_id]";				
			boolString = true;		
			break;						
		case "previous customer name":
			strTest = "&[old_opencall.cust_name]";				
			boolString = true;		
			break;			
		case "previous support group":
			strTest = "&[old_opencall.suppgroup]";				
			boolString = true;		
			break;			
		case "previous support analyst":
			strTest = "&[old_opencall.owner]";				
			boolString = true;		
			break;			

	}
	
	//--
	//-- get appropriate value
	var strValue = tb_value1.text;	
	if(lb_teststatus1.visible)strValue=lb_teststatus1.value;
	if(lb_testwfstatus1.visible)strValue=lb_testwfstatus1.value;	
	
	if(boolString)
	{
		strTest = "'" + strTest + "'";
		strValue = "'" + strValue + "'";
	}

	var strFullTest = strTest + strOp + strValue;
	bpm_stage_task.h_condition = bpm_stage_task.h_condition + " " + strFullTest;
	_swdoc.UpdateFormFromData();
	
	app._een(btn_apply_test1,false);
	app._een(btn_and1,true);
	app._een(btn_or1,true);

}

							btn_and1_OnPressed()
{
	bpm_stage_task.h_condition = bpm_stage_task.h_condition + " " + " AND " + " ";
	_swdoc.UpdateFormFromData();

	app._een(btn_apply_test1,true);
	app._een(btn_and1,false);
	app._een(btn_or1,false);	
	
}

							btn_or1_OnPressed()
{

	bpm_stage_task.h_condition = bpm_stage_task.h_condition + " " + " OR " + " ";
	_swdoc.UpdateFormFromData();

	app._een(btn_apply_test1,true);
	app._een(btn_and1,false);
	app._een(btn_or1,false);	

}

							fld_profile_OnDropDown()
{
	app.g.pick_problemprofile(_swdoc.oTask().probcode,"",true, function(strInfo)
	{	
		if(strInfo!="")
		{
			var arrInfo = strInfo.split("|");
			_swdoc.oTask().probcode=arrInfo[0];
			_swdoc.UpdateFormFromData();
		}
	});

	return false;
}
