method.code:
						OnSaveData()
{
	//-- if title (description) not set then set it
	if(equipmnt.asset_title=="")
	{
		var strSpace = (equipmnt.manufactur!="")?" ":"";
		equipmnt.asset_title = equipmnt.manufactur + strSpace + equipmnt.model;
	}

	//-- store company name
	_swdoc.equipmnt.companyname = mainform.lb_company.display;
	

	return true;
}

						var boolInserting = false;
var array_sites = new Array();

function check_sites()
{	
	if(mainform.IsObjectDefined("sl_sites"))
	{
	
		var strSite = "";
		
		for(var x=0;x<mainform.sl_sites.rowCount();x++)
		{
			strSite = mainform.sl_sites.GetItemTextRaw(x, "site_name");
			if(_swdoc.array_sites[strSite])
			{
				mainform.sl_sites.SetRowChecked(x,true);
			}
			else
			{
				mainform.sl_sites.SetRowChecked(x,false);
			}
		}	
	}
}

function store_sites()
{
	_swdoc.array_sites = new Array();
	var strParams = "eid="+app.pfu(equipmnt.equipid);
	var oRS = app.g.get_sqrecordset("form/tfasset/get_sites",strParams);
	while(oRS.Fetch())
	{
		var strSite = app.g.get_field(oRS,"FK_SITE_NAME");
		_swdoc.array_sites[strSite] = true;
	}	
}

						OnDataSaved()
{
	//-- save user sites
	if(_swdoc.boolInserting)
	{
		for(strSite in _swdoc.array_sites)
		{
			if(_swdoc.array_sites[strSite])
			{
				var strParams = "eid="+app.pfu(equipmnt.equipid)+"&company="+app.pfu(_swdoc.equipmnt.fk_company_id)+"&site="+app.pfu(strSite);
				app.g.submitsqs("form/tfasset/add_site_rel",strParams);
			}
		}
	}


}

							OnFormLoaded(bDataReloaded)
{
	
	var boolSupportExtOrgs = (dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1);
	
	if((equipmnt.equipid=="")||(_swdoc.equipmnt.fk_company_id==""))
	{
		_swdoc.boolInserting=true;
		_swdoc.equipmnt.fk_company_id = _swdoc.GetArg('fk_company_id');
		_swdoc.equipmnt.site = _swdoc.GetArg('site');		
		_swdoc.equipmnt.owner=_swdoc.GetArg('owner');		
	
			//-- are we creating from a log call auto resolve?
		if((_swdoc.equipmnt.fk_company_id=="") && (app.global.logcall_addnew_orgid!=null))
		{
			_swdoc.equipmnt.fk_company_id = app.global.logcall_addnew_orgid;
			_swdoc.equipmnt.companyname = app.global.logcall_addnew_orgname;
			app.global.logcall_addnew_orgid=null;
			app.global.logcall_addnew_orgname=null;

		}		
		
		if((_swdoc.equipmnt.fk_company_id=="")&&(!boolSupportExtOrgs))
		{
			_swdoc.equipmnt.fk_company_id = dd.GetGlobalParamAsString("Application Settings/DefaultCompanyID");
		}		
			
		if(_swdoc.equipmnt.site!="")_swdoc.LoadRecordData("swdata", "site", _swdoc.equipmnt.site)		
		_swdoc.UpdateFormFromData();
		
		_swdoc.ResetModiedFlag("equipmnt");
	}
	else
	{
		_swdoc.boolInserting=false;
		//_swdoc.store_sites();
	}

	app._een(lb_company , boolSupportExtOrgs);

}

							lb_company_OnValueChanged(strValue)
{
	
	//-- set company name
	_swdoc.equipmnt.companyname = lb_company.display;
	//-- reset sites list	
	_swdoc.equipmnt.site="";	
	app._ete(lb_site,"");	
	
	_swdoc.UpdateFormFromData()
	_swdoc.check_sites();
	
}
