						var boolInserting = false;
var arrDiary = Array();
function oContract()
{
	return _swdoc.contract;
}

function MF()
{
	return _swdoc.mainform;
}

function init_form()
{
	//-- 27.10.2004
	//-- fix date fields as getrecord does not process them correctly
	if (_swdoc.oContract().pk_contract_id !="")
	{	
		_swdoc.boolInserting=false;
		_swdoc.MF().TabContract.ShowTabItem(1,true);		
		_swdoc.MF().TabContract.ShowTabItem(2,true);		
		_swdoc.MF().TabContract.ShowTabItem(3,true);
		_swdoc.MF().TabContract.ShowTabItem(4,true);
		_swdoc.MF().TabContract.ShowTabItem(5,true);
		_swdoc.check_contract_state(true);
		_swdoc.refresh_contract_cis();		
	}
	else
	{	
		//-- inserting
		_swdoc.boolInserting = true;
		_swdoc.oContract().fk_company_id= unescape(_swdoc.GetArg("fk_company_id"));
		_swdoc.oContract().type = unescape(_swdoc.GetArg("type"));
		_swdoc.MF().TabContract.ShowTabItem(1,false);		
		_swdoc.MF().TabContract.ShowTabItem(2,false);
		_swdoc.MF().TabContract.ShowTabItem(3,false);
		_swdoc.MF().TabContract.ShowTabItem(4,false);
		_swdoc.MF().TabContract.ShowTabItem(5,false);		
	}	
	
	_swdoc.ResetModiedFlag('contract');
}


//-- check contract state
function check_contract_state(boolSave)
{	
	if(boolSave==undefined)boolSave = true;
	//-- if the contract end date is less than todays date then set flag to 0
	//-- or if the start date is greater than todays date (not started yet)
	if ( (app.g.fix_epoch(_swdoc.oContract().end_datex) < app.global.GetCurrentEpocTime()) || (app.g.fix_epoch(_swdoc.oContract().start_datex) > app.global.GetCurrentEpocTime())	)			
	{
		//-- if system has not flagged it yet set flag and save
		if (_swdoc.oContract().flg_valid=="1")
		{	
			_swdoc.oContract().flg_valid="0";
			_swdoc.UpdateFormFromData();
			if(boolSave)_swdoc.Save(0);
		}
	}
	else
	{
		if (_swdoc.oContract().flg_valid==0)
		{	
			_swdoc.oContract().flg_valid="1";
			_swdoc.oContract().flg_ewemail_sent="0";			
			_swdoc.oContract().flg_expemail_sent="0";						
			_swdoc.UpdateFormFromData();
			if(boolSave)_swdoc.Save(0);
		}
	}
}

//-- 26.02.2008 - nwj
//-- refresh contract ci list
function refresh_contract_cis()
{
	var strCMDBids = app.cfg.get_contract_cikeys(_swdoc.oContract().pk_contract_id,true);
	app._slf(_swdoc.MF().sl_contractci , "pids=" + strCMDBids + "&sf=cilist_activebln");
	_swdoc.MF().sl_contractci.SetRowSelected(0);
}

						OnDataSaved()
{

    var pDoc = app.g.get_me_document("COMPANY",_swdoc.oContract().fk_company_id);
    if(pDoc)
    {
    	var intCurSel = pDoc.MF().sl_contracts.curSel;
    	pDoc.MF().sl_contracts.Refresh();
    	pDoc.MF().sl_contracts.SetRowSelected(intCurSel,true);
    }

    //-- 
    //-- NWJ - show tab - and also update parent _swdoc
    if(_swdoc.boolInserting)
    {		
    	//-- create app.cmdb relationship
    	if(pDoc)
    	{
    		//-- need to reload site record to get fk_cmdb_id
    		_swdoc.LoadRecordData("swdata", "contract", _swdoc.oContract().pk_contract_id);
    		
    		var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgContractRelationship");
    		app.cmdb.generate_meconfigrelation(pDoc.oCompany().fk_cmdb_id,_swdoc.oContract().fk_cmdb_id,strRel);  
    	}
		_swdoc.boolInserting=false;
	}
    
	//RJC 05.01.09
	//ITSM 2.4 FS 13.7	
	var strText = "";
	for(var intNumberOfInsert=0;intNumberOfInsert<_swdoc.arrDiary.length;intNumberOfInsert++)
	{
		arrDetails = _swdoc.arrDiary[intNumberOfInsert]
		if(strText!="")strText += ","
		strText += arrDetails[0]+" has been modified from '"+arrDetails[1]+"' to '"+arrDetails[2]+"'";
	}
	if(strText!=""){
		var strParams = "contract="+app.pfu(_swdoc.oContract().pk_contract_id)+"&txt="+app.pfu(strText);
		app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
	}
	
	
	//-- ensure tab is active after saving
	_swdoc.init_form();

}

						function setup_history()
{
	//RJC 05.01.09
	//ITSM 2.4 FS 13.7
	//Create array of insert details
	var arrInserts = new Array();
	
	//-- get matching contract record
	var oLiveContractRec = app.g.get_record("CONTRACT",contract.pk_contract_id);
	var liveVal="";
	//-- for each field check if diff from contract
	for (x=0; x < dd.tables['contract'].columns.length;x++)
	{
	
		var colName = dd.tables['contract'].columns[x].Name;
					
		if(colName=="contract_notes")continue;
		
		//make sure the field exists
		if(oLiveContractRec[colName]!=undefined)
		{
			//-- if values differ and value is not empty put into a field table
			//-- if numeric value and = 0 then ignore
			var FORM_VALUE = contract[colName];
			if((app.g.dd_isnumeric('contract',colName))&&(FORM_VALUE==0))continue;
			
			var	dispFormVal = FORM_VALUE;
			var liveVal = oLiveContractRec[colName];
			var dispLiveVal = liveVal;
			if(app.g.dd_isnumeric('contract',colName))
			{
				if(FORM_VALUE<0)
				{
					FORM_VALUE= app.g.fix_epoch(FORM_VALUE);
					dispFormVal = app.g.convert_epochddmmyyyy(FORM_VALUE);
					dispLiveVal = app.g.convert_epochddmmyyyy(liveVal);
				}
			}
			
			if((liveVal!=FORM_VALUE))
			{
				arrInserts.push(new Array( app.g.dd_fieldlabel('contract',colName),app.g.pfs(dispLiveVal) , app.g.pfs(dispFormVal)))
			}
		}	
	}

	_swdoc.arrDiary = arrInserts;
}

						OnSaveData()
{
	// TODO: Add your event handler code here

	_swdoc.setup_history();
	return true;
}

