							sl_contractci_OnItemSelected(nRow)
{
  //-- Load CI info based on CI loaded
    
    var ci_id  = sl_contractci.GetItemTextRaw(nRow,'pk_auto_id');
    if (ci_id == "")
    {
        app._een(mbtn_view_ci , false);
    }
    else
    {
		app._een(mbtn_view_ci , true);
    }

}

							mbtn_contract_actions_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	

	//-- get ci
 	var intCiID = sl_contractci.GetItemTextRaw(sl_contractci.curSel,"pk_auto_id");	
	
	if(nItemID == iAssignCI)
	{
		//-- associate org owned or used cis to the contract
		var strCIS = app.cfg.get_company_cikeys(_swdoc.oContract().fk_company_id,true,true);
		var arrUsedCIS = strCIS.split(",");
		app.cmdb.search_ci(false, "", function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			var strCisToLink = "";
			var strCiTextToLink = "";
			var boolAlerted = false; 
			var arrLinkCIS = sResults.selectedkeys.split(",");
			var arrLinkCITextS = sResults.selectedtext.split(",");
			var start=" ";
			var end=" was ";
			for(var x=0; x<arrLinkCIS.length;x++)
			{
				var boolInList = false;
				for (var y=0; y < arrUsedCIS.length;y++)
				{
					if(arrLinkCIS[x]==arrUsedCIS[y])
					{
						//-- store as a ci we can link
						if(strCisToLink!="")strCisToLink+=",";
						strCisToLink += arrUsedCIS[y];
						if(strCiTextToLink!="")
						{
							strCiTextToLink+=",";
							start = "'s ";
							end = " were ";
						}
						strCiTextToLink += arrLinkCITextS[x];
						boolInList = true;
						break;
					}
				}
	
				//-- if not a ci used by org and we havent told user tell them and then dont bother
				if((!boolInList)&&(!boolAlerted))
				{
					boolAlerted = true; 
					MessageBox("One or more of the selected configuration items are not owned by or used by the organisation that this contract is for.\nSuch configuration items will not be associated to the contract.");
				}
			}
			
			if(strCisToLink=="")return;
	
			//-- relate cis to contract ci   		
   			if(_swdoc.oContract().fk_cmdb_id)
   			{
				var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultContractCIRelationship");   		
				app.cmdb.generate_meconfigrelation(_swdoc.oContract().fk_cmdb_id, strCisToLink, strRel);
			}
			
			//RJC 05.01.09
			//ITSM 2.4  FS 13.7
			var strText = "The CI"+start+strCiTextToLink+end+"associated to the contract";
			var strParams = "contract="+app.pfu(_swdoc.oContract().pk_contract_id)+"&txt="+app.pfu(strText)+"&code="+app.pfu("CI Association");
			app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
			//-- prompt user if they want to associate the ci's to the company that contract is for?
			
			//-- now need to refresh sqllist
			_swdoc.refresh_contract_cis();
			return;
		});
    }
	else if(nItemID == iRemoveCI)
	{
		//-- remove link between ci and contract
		if (intCiID > 0)
    	{
    		if (!confirm("Are you sure you want to remove the selected configuration item from this contract ?")) return;

    		//--
			//-- delete ci relationship (always assume that ME is parent)
			if(_swdoc.oContract().fk_cmdb_id>0)
			{
				app.cmdb.delete_meconfigrelation(_swdoc.oContract().fk_cmdb_id, intCiID);
			}   		
			
			//RJC 05.01.09
			//ITSM 2.4  FS 13.7
 			var intCiItem = sl_contractci.GetItemTextRaw(sl_contractci.curSel,"ck_config_item");	
			var strText = "The CI "+intCiItem+" was disassociated to the contract";
			var strParams = "contract="+app.pfu(_swdoc.oContract().pk_contract_id)+"&txt="+app.pfu(strText)+"&code="+app.pfu("CI Disassociation");
			app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
			_swdoc.refresh_contract_cis();
    	} 		
	}
		
}

